	["cu_g3sg1_executioner"] = { name = "The Executioner", desc = "Esta arma em particular foi pintada à mão com desenhos grotescos de ossos.\n\nEu levo sempre um troféu - Javier Alviso, o \"Braço Forte\" da Phoenix" },
	["gs_m4a4_royal_squire"] = { name = "Royal Paladin", desc = "Esta arma em particular foi cuidadosamente gravada e pintada para dar um ar de grandiosidade.\n\nEste telefonema nunca aconteceu, está entendido? - O Paladino e o Pai, 1.ª Parte" },
	["cu_negev_impact"] = { name = "Power Loader", desc = "Esta arma em particular foi pintada com um padrão hidrográfico que faz lembrar maquinaria pesada.\n\nTemida por Rainhas. Venerada por Bispos." },
	["am_p2000_imperial_red"] = { name = "Imperial", desc = "Esta arma em particular foi decorada com gravuras a laser e pintada a vermelho e amarelo metalizado.\n\nOs meus sentimentos são irrelevantes, Keo. Eu tenho um império para proteger - Booth, traficante de armas" },
