	["hy_scar20_jungler"] = { name = "Outbreak", desc = "Esta arma em particular foi pintada com tons verdes vibrantes.\n\nA Valeria está a ficar cada vez mais audaz... Chegou a hora de finalmente enfrentá-la - Felix Riley, Comandante" },
	["gs_sg553_tiger_moth"] = { name = "Tiger Moth", desc = "Esta arma em particular foi pintada com um padrão de asas de traça.\n\nA Valeria está a ficar cada vez mais audaz... Chegou a hora de finalmente enfrentá-la - Felix Riley, Comandante" },
	["cu_tec9_avalanche"] = { name = "Avalanche", desc = "Esta arma em particular foi pintada com um padrão hidrográfico futurista\n\nTemos de pensar bem como vamos enfrentá-la, basta dar um passo em falso e ela acaba com nós os dois - A Serpente e o Perseguidor da Verdade" },
	["aq_xm1014_hot_rod"] = { name = "Teclu Burner", desc = "Esta arma em particular foi pintada com tintas metalizadas e decorada com um padrão de chamas.\n\nPor vezes, um bico de Bunsen não é o suficiente..." },
	["gs_ak47_supercharged"] = { name = "Fuel Injector", desc = "Grande parte desta arma em particular foi pintada de amarelo e o carregador foi decorado com um padrão semelhante a engrenagens.\n\nVive a tua vida, uma bala de cada vez" },
