	["aq_deserteagle_kumichodragon"] = { name = "Kumicho Dragon", desc = "O ferrolho e a empunhadura desta arma em particular foram decorados com gravuras de dragões.\n\nSou um grande fã do teu trabalho, Yukako. Devíamos ter uma conversa... - Huxley, a concorrência" },
	["cu_fiveseven_augmented"] = { name = "Triumvirate", desc = "Esta arma em particular foi decorada com um padrão hidrográfico azul-claro DDPAT.\n\nEu não lucro da guerra, eu apenas lucro da condição humana... - Booth, traficante de armas" },
