	["gs_tec9_jambiya"] = { name = "Jambiya", desc = "Esta arma em particular foi decorada com padrões do Médio Oriente.\n\nXerazade contou 1000 histórias... e esta é a última que vais ouvir" },
	["gs_usp_voltage"] = { name = "Lead Conduit", desc = "O ferrolho e o silenciador desta arma em particular foram decorados com gravuras.\n\nConseguiste o que querias. E agora, sabes o que isso significa? – Booth, traficante de armas" },
	["cu_aug_swallows"] = { name = "Fleet Flock", desc = "Esta arma em particular foi decorada com um padrão hidrográfico de pássaros a voar pelas nuvens.\n\nAlguns pássaros representam harmonia e paz... Estes não são desse tipo de pássaros" },
	["cu_bizon_Curse"] = { name = "Judgement of Anubis", desc = "Esta arma em particular foi pintada com motivos egípcios.\n\nO gatilho desta arma é mais leve do que uma pena" },
	["gs_cz75a_redastor"] = { name = "Red Astor", desc = "Esta arma em particular foi pintada à mão com motivos vermelhos e brancos.\n\nO Booth tornou-se sinónimo de dramas familiares... e quem é que tem tempo para dramas quando se está a falar de negócios? - Huxley, a concorrência" },
