	["gs_galilar_incenerator"] = { name = "Firefight", desc = "Esta arma em particular foi decorada com um ícone pintado à mão e certas partes da mesma foram pintadas de carmesim.\n\nBooth comissionou esta arma para a Valeria numa tentativa de melhorar a relação com a Phoenix" },
