	["am_mp9_bioleak"] = { name = "Bioleak", desc = "Tons variados de verde criam a ilusão de um derramamento de produtos químicos.\n\nPERIGO - RISCO DE MORTE" },
	["hy_p2000_oceani"] = { name = "Oceanic", desc = "Esta arma em particular foi tratada com uma camada base de tinta preta e de seguida decorada com padrões azuis.\n\nE o Kotaro e Carmen, já disseram alguma coisa? - Felix Riley, Comandante" },
	["cu_p250_asiimov"] = { name = "Asiimov", desc = "Esta arma em particular foi pintada com um tema de ficção científica.\n\nSe achas que eu preciso de uma espingarda para matar um homem, não fazes ideia de com quem estás a falar... - Mikha Biton, Sniper da Phoenix" },
	["gs_sawedoff_fubar"] = { name = "Fubar", desc = "O cano desta caçadeira bem usada foi decorado com traços que representam as vítimas que ela causou.\n\nComeça com um estouro... e acaba em silêncio" },
	["cu_sg553_atlas"] = { name = "Atlas", desc = "Esta arma em particular foi decorada com um padrão hidrográfico moderno de camuflagem.\n\nFoi outrora um visual concebido especialmente para as Forças da Coligação, mas o Huxley e a espionagem industrial acabaram por torná-lo num dos visuais exclusivos da Phoenix" },
	["gs_ssg08_armacore"] = { name = "Ghost Crusader", desc = "Esta arma em particular foi pintada de branco e decorada com tons de cor azul-ártico.\n\nEu nunca poderei substituir as coisas que eles nos tiraram... mas eu posso tirar ainda mais deles - A Fénix e o Aprendiz, 2.ª Parte" },
	["am_tec9_redblast"] = { name = "Re-Entry", desc = "O cano desta arma em particular foi decorado com um decalque semelhante a chamas.\n\nDetesto ter sempre razão - Carmen Cocinero, especialista em resgates" },
