	["bloodhound_snakeskin_brass"] = { name = "Snakebite", desc = "Este par de luvas em particular foi fabricado com uma combinação de couro e pele de cobra.\n\nSejamos honestos, ninguém tem saudades do Sergei - Hans Kriegeld, estrategista da Phoenix" },
	["bloodhound_metallic"] = { name = "Bronzed", desc = "O couro deste par de luvas em particular foi tingido de cores metálicas semelhantes a bronze.\n\nA Operação Bloodhound marcou o fim do franco-atirador desertor Chase Turner" },
	["bloodhound_guerrilla"] = { name = "Guerrilla", desc = "Este par de luvas em particular foi fabricado com uma combinação de couro castanho e couro sintético com um padrão de camuflagem.\n\nA Valeria está a planear a sua vingança, disso não há qualquer dúvida... - Sebastien Hennequet, franco-atirador" },
