	["cu_ak47_anubis"] = { name = "Legion of Anubis", desc = "Esta arma em particular foi decorada com imagens dos deuses egípcios Anúbis e Rá, que representam o equilíbrio entre a vida e a morte.\n\nQue a balança dele esteja a teu favor" },
