	["hy_p90_dino_rampage"] = { name = "Cocoa Rampage", desc = "Esta arma em particular foi decorada com um padrão hidrográfico verde-azeitona e dourado com desenhos de dinossauros estilizados.\n\nDinossauros e extraterrestres e cometas, ena pá!" },
