	["gs_mp5_conditionzero"] = { name = "Condition Zero", desc = "O acabamento azul e amarelo desta arma em particular é uma homenagem ao Counter-Strike: Condition Zero.\n\n\"Rocket a vir da direita! Desviar!\"" },
