	["gs_sg553_deathmetal"] = { name = "Heavy Metal", desc = "Esta arma em particular foi decorada com motivos cor de laranja e amarelos sobre uma camada negra. Três estrelas foram pintadas na parte traseira.\n\n\"Um destes rebites é o botão Turbo, só não me lembro qual…\"" },
