text = {
	["Default"] = { name = "-", desc = "Розфарбування без назви" },
	["so_yellow"] = { name = "Бульдозер", desc = "Bulldozer. Частини зброї пофарбовані аерозольною фарбою у жовті кольори, що чергуються з оригінальним кольором зброї.\n\nПеретворить ворожі кістки у порох" },
	["so_olive"] = { name = "Ґрунтові води", desc = "Groundwater. Частини зброї пофарбовані аерозольною фарбою у суцільний оливковий колір." },
	["so_red"] = { name = "Карамельне яблуко", desc = "Candy Apple. Частини зброї пофарбовані аерозольною фарбою у червоно-чорній кольоровій гамі.\n\nНа авто добре, але на зброї краще" },
	["so_purple"] = { name = "Ультрафіолет", desc = "Ultraviolet. Частини зброї пофарбовані аерозольною фарбою у пурпурово-чорній кольоровій гамі.\n\nЕлегантний дизайн поєднано зі злочинними намірами" },
	["so_night"] = { name = "Ніч", desc = "Night. Частини зброї пофарбовані аерозольною фарбою у кольоровій гамі нічного пейзажу.\n\nЦе шепіт вітру… це укус на вашій шиї" },
	["so_caramel"] = { name = "Карамель", desc = "Caramel. Частини зброї пофарбовані аерозольною фарбою у карамельно-чорній кольоровій гамі." },
	["so_grassland"] = { name = "Пасовиська", desc = "Grassland. Частини зброї пофарбовані аерозольною фарбою у кольоровій гамі трави." },
	["so_moss"] = { name = "Синя хвоя", desc = "Blue Spruce. Частини зброї пофарбовані аерозольною фарбою у суцільній гамі кольору моху." },
	["so_sand"] = { name = "Піщана дюна", desc = "Sand Dune. Частини зброї пофарбовані аерозольною фарбою у піщаній кольоровій гамі.\n\n«Після смерті Тернера у нас тепер інший пріоритет: врятувати Алекса Кінкейда» — командир Фелікс Райлі" },
	["so_stormfront"] = { name = "Шторм", desc = "Storm. Частини зброї пофарбовані аерозольною фарбою у суцільній штормовій кольоровій гамі.\n\nЧекатиме тебе на монорельсі" },
	["so_tornado"] = { name = "Смерч", desc = "Tornado. Частини зброї пофарбовані аерозольною фарбою у кольоровій гамі смерчу.\n\nНе дайте себе одурити його простотою" },
	["so_whiteout"] = { name = "Сніжна мла", desc = "Whiteout. Зброя пофарбована повністю у білий колір.\n\nВиглядає чисто, але зовнішність оманлива" },
	["so_jungle"] = { name = "Джунглі", desc = "Jungle. Частини зброї пофарбовані аерозольною фарбою у кольоровій гамі джунглів." },
	["so_space_marine"] = { name = "Колонія", desc = "Colony. Частини зброї пофарбовані аерозольною фарбою у хакі та сірий колір." },
	["so_pmc"] = { name = "Найманець", desc = "Contractor. Частини зброї пофарбовані аерозольною фарбою у брунатний, темно-синій та темно-зелений кольори.\n\nРона Сабрі досі не пробачила Себастьяну, те що він не обрав її для знищення Тернера" },
	["so_tangerine"] = { name = "Зруйнування", desc = "Demolition. Частини зброї пофарбовані аерозольною фарбою у червоно-помаранчевій кольоровій гамі.\n\n«Рона настільки ж управна, як вона про себе думає… вона просто не достатньо досвідчена, щоб зрозуміти що не потрібно це говорити кожному» — командир Фелікс Райлі" },
	["so_orange_accents"] = { name = "Нітро", desc = "so_orange_accents" },
	["cu_broken_path_famas"] = { name = "Післясвітло", desc = "Afterimage. Нанесено зображення з порваних стрічок." },
	["cu_bullet_rain_m4a1"] = { name = "Дощ із куль", desc = "Bullet Rain. Нанесено зображення хмар та дощу з куль." },
	["cu_catskulls_p90"] = { name = "Смерть від кицьок", desc = "Death by Kitty. Поверхня зброї вкрита малюнками з котячими черепами." },
	["CSGO_Doomkitty"] = { name = "Смертеня", desc = "CSGO_Doomkitty" },
	["hy_ddpat"] = { name = "Пікс. камуфляж «Ліс»", desc = "Forest / Orange / Urban / Pink DDPAT. Аквадруком нанесено текстуру піксельного камуфляжу.\n\nУ момент, коли ви будете достатньо близько, аби розгледіти пікселі, стане запізно" },
	["hy_ddpat_urb"] = { name = "Пікс. камуфляж «Місто»", desc = "hy_ddpat_urb" },
	["hy_ddpat_jungle"] = { name = "Пікс. камуфляж «Джунглі»", desc = "hy_ddpat_jungle" },
	["hy_ddpat_orange"] = { name = "Пікс. камуфляж «Помаранч»", desc = "hy_ddpat_orange" },
	["hy_ddpat_pink"] = { name = "Пікс. камуфляж «Фуксія»", desc = "hy_ddpat_pink" },
	["hy_ddpat_desert"] = { name = "Пікс. камуфляж «Пустеля»", desc = "hy_ddpat_desert" },
	["hy_arctic"] = { name = "Зимній камуфляж", desc = "Arctic / Polar Camo. Аквадруком нанесено текстуру арктичного камуфляжу.\n\nСніг холодний, але смерть холодніша" },
	["hy_dive"] = { name = "Підводний камуфляж", desc = "Dive Camo. Аквадруком нанесено текстуру синього камуфляжу.\n\nНаш світ сповнений підступністю… та часом камуфляж зроблений саме для того, аби його побачили — Бут, продавець зброї" },
	["hy_ocean"] = { name = "Чиста вода", desc = "hy_ocean" },
	["hy_forest"] = { name = "Лісовий камуфляж", desc = "Wood Camo, Night / Winter / Boreal Forest. Аквадруком нанесено текстуру лісового камуфляжу.\n\nЛіс може бути небезпечним місцем… ніколи не подорожуйте наодинці" },
	["hy_forest_winter"] = { name = "Зимній ліс", desc = "hy_forest_winter" },
	["hy_forest_boreal"] = { name = "Тундра", desc = "hy_forest_boreal" },
	["hy_forest_night"] = { name = "Лісова ніч", desc = "hy_forest_night" },
	["hy_desert"] = { name = "Піщана буря", desc = "Desert Storm. Аквадруком нанесено текстуру пустельного камуфляжу." },
	["hy_tiger"] = { name = "Бенгальський тигр", desc = "Bengal Tiger. Аквадруком нанесено зображення у вигляді тигрових смуг." },
	["hy_copperhead"] = { name = "Гадюка", desc = "Copperhead. Аквадруком нанесено текстуру камуфляжу шкіри гадюки." },
	["hy_skulls"] = { name = "Черепи", desc = "Skulls. Аквадруком нанесено текстуру черепів у червоно-чорній кольоровій гамі.\n\nМасове поховання, що цілком вміщується у ваших долонях" },
	["hy_webs"] = { name = "Криваве павутиння", desc = "Crimson Web. На окремі частини аквадруком нанесено візерунки павутини, під ними червоний колір, і все це вкрито шаром напівглянцю.\n\nХодіть обережно, бо ж ви ніколи не знатимете, куди може простягатися павутина" },
	["hy_splatter"] = { name = "Сині струмені", desc = "Blue Streak. Аквадруком нанесено напівпрозору текстуру розбризкування на основу кольору водяної блакиті." },
	["hy_ak47lam"] = { name = "Червоний композит", desc = "Black / Red / Blue Laminate. Вироблено з композитного матеріалу." },
	["hy_ak47lam_bw"] = { name = "Чорний композит", desc = "hy_ak47lam_bw" },
	["hy_ak47lam_blue"] = { name = "Синій композит", desc = "hy_ak47lam_blue" },
	["hy_gelpen"] = { name = "Пороховий дим", desc = "Gunsmoke. Аквадруком нанесено візерунок у стилі малюнків гелевою ручкою." },
	["hy_v_tiger"] = { name = "Тигр у джунглях", desc = "Jungle Tiger. Аквадруком нанесено текстуру тигра і джунглів." },
	["hy_redtiger"] = { name = "Кривавий тигр", desc = "hy_redtiger" },
	["hy_zombie"] = { name = "Вірус", desc = "Virus. Аквадруком на зелену основу нанесено зображення у вигляді зомбі." },
	["hy_granite"] = { name = "Мармуровий граніт", desc = "Granite Marbleized. Нанесено зображення, що імітує мармур." },
	["hy_blizzard"] = { name = "Мармурова хуртовина", desc = "hy_blizzard" },
	["hy_sediment"] = { name = "Бруд", desc = "hy_sediment" },
	["hy_reef_rock"] = { name = "Блакитні бризки", desc = "hy_reef_rock" },
	["sp_spray"] = { name = "Контрастна аерографія", desc = "Army Recon, Contrast / Jungle / Sage / Sand / Wave Spray. Аерозольним балончиком нанесено малюнок у вигляді коротких товстих ліній контрастних кольорів.\n\nІдеальна для бойовика-кочовика" },
	["sp_spray_sage"] = { name = "Димчаста аерографія", desc = "sp_spray_sage" },
	["sp_spray_jungle"] = { name = "Аерографія джунглів", desc = "sp_spray_jungle" },
	["sp_spray_sand"] = { name = "Піщана аерографія", desc = "sp_spray_sand" },
	["sp_spray_waves"] = { name = "Аерографія «Хвилі»", desc = "sp_spray_waves" },
	["sp_tape_dots"] = { name = "Сухий сезон", desc = "Dry Season, Urban / Waves Perforated. Аерозольним балончиком нанесено малюнок за допомогою трафарету з уривків скотчу та перфорованого металу." },
	["sp_tape_dots_urban"] = { name = "Міське перфорування", desc = "sp_tape_dots_urban" },
	["sp_tape_dots_waves"] = { name = "Перфоровані хвилі", desc = "sp_tape_dots_waves" },
	["sp_leaves"] = { name = "Лісове листя", desc = "Forest / Grassland Leaves. Аерозольним балончиком нанесено малюнок за допомогою трафарету з листя." },
	["sp_leaves_grassland"] = { name = "Лугове листя", desc = "sp_leaves_grassland" },
	["sp_short_tape"] = { name = "Лишайникове штрихування", desc = "Jungle / Lichen / Sand / Urban Dashed. Аерозольним балончиком нанесено малюнок за допомогою трафарету з маленьких шматочків скотчу." },
	["sp_short_tape_jungle"] = { name = "Штриховані джунглі", desc = "sp_short_tape_jungle" },
	["sp_short_tape_sand"] = { name = "Штриховані піски", desc = "sp_short_tape_sand" },
	["sp_short_tape_urban"] = { name = "Міське штрихування", desc = "sp_short_tape_urban" },
	["sp_tape"] = { name = "Кістяна маска", desc = "Bone Mask. Аерозольним балончиком нанесено малюнок за допомогою трафарету зі сплутаного скотчу.\n\nСправжня сила не виставляється напоказ" },
	["sp_tape_orange"] = { name = "Помаранчева шкірка", desc = "sp_tape_orange" },
	["sp_tape_urban"] = { name = "Міське маскування", desc = "sp_tape_urban" },
	["sp_mesh"] = { name = "Плавникова сітка", desc = "Condemned, Teardown, Army / Glacier / Gator / Safari / Sand / Polar Mesh. Аерозольним балончиком нанесено малюнок за допомогою трафарету з сітки огорожі та шматків картону.\n\nХижак є хижаком незалежно від оточення" },
	["sp_mesh_tan"] = { name = "Сітка сафарі", desc = "sp_mesh_tan" },
	["sp_mesh_arctic_contrast"] = { name = "Полярна сітка", desc = "sp_mesh_arctic_contrast" },
	["sp_mesh_fire"] = { name = "Обгорілий", desc = "sp_mesh_fire" },
	["sp_mesh_glacier"] = { name = "Крижана сітка", desc = "sp_mesh_glacier" },
	["sp_mesh_sand"] = { name = "Піщана сітка", desc = "sp_mesh_sand" },
	["sp_snake"] = { name = "Зміїний камуфляж", desc = "Snake Camo. Аерозольним балончиком нанесено малюнок за допомогою вирізаного вручну трафарету зі зміїної шкіри." },
	["an_silver"] = { name = "Срібло", desc = "Silver. Нанесено зображення із хромовою основою, що вкрита прозорим шаром срібла задля ефекту анодованості.\n\n«В простоті існує краса» — Франц Кригельд, стратег угрупування «Фенікс»" },
	["an_red"] = { name = "Хот-род", desc = "Hot Rod. Нанесено зображення із хромовою основою, що вкрита прозорим шаром червоного задля ефекту анодованості.\n\nАвтоматичне. Систематичне. Гідроматичне." },
	["an_gunmetal"] = { name = "Анодований цинковий сплав", desc = "an_gunmetal" },
	["an_navy"] = { name = "Анодована синява", desc = "Anodized Navy. Нанесено зображення із хромовою основою, що вкрита прозорим шаром темно-синього задля ефекту анодованості." },
	["am_urban"] = { name = "Пікс. камуфляж «Металічний»", desc = "Metallic DDPAT. Аквадруком нанесено текстуру міського піксельного камуфляжу на металеву основу." },
	["am_tiger"] = { name = "Золотий тигр", desc = "Gold Tiger. Нанесено золотий шар, на якому вручну вигравірувані смужки у вигляді шкіри тигра." },
	["am_dragon_glock"] = { name = "Тату дракона", desc = "Dragon Tattoo. На металеву основу наклеєно малюнок у вигляді дракона.\n\nУ казках лицар завжди вбиває дракона… однак це реальний світ — Валерія Дженнер, революціонерка" },
	["am_caustics"] = { name = "Каустика", desc = "Caustics. Аквадруком нанесено червоний і білий колір на вугільно-чорну металеву основу." },
	["am_lightning_awp"] = { name = "Удар блискавки", desc = "Lightning Strike. На металеву основу нанесено малюнок у вигляді блискавок з використанням ефекту анодованості.\n\nІноді вам не потрібно бити в одне місце двічі" },
	["am_ossify"] = { name = "Закостенілий", desc = "Ossified. Аквадруком нанесено прозорий світло-зелений абстрактний малюнок на металеву основу." },
	["aa_flames"] = { name = "Полум’я", desc = "Blaze. Аерографічний малюнок нанесено прозорою фарбою на хромову основу." },
	["aa_splash_p250"] = { name = "Бризк", desc = "Splash. Аерографічний малюнок нанесено прозорою фарбою за допомогою трафарету на хромову основу." },
	["aa_fade"] = { name = "Градієнт", desc = "Fade, Acid / Amber Fade. Аерографічний малюнок нанесено напівпрозорими фарбами, що переходять одна в одну, на хромову основу.\n\n«Це не просто зброя, це жанрова картина» — Імоґен, продавець зброї" },
	["aa_fade_metallic"] = { name = "Бурштиновий градієнт", desc = "aa_fade_metallic" },
	["aa_fade_grassland"] = { name = "Кислотний градієнт", desc = "aa_fade_grassland" },
	["aq_copper"] = { name = "Мідь", desc = "Copper. Покрито міддю." },
	["aq_blued"] = { name = "Блакитна сталь", desc = "Blue Steel. Розфарбований синім кольором з холодним відтінком.\n\nЦе справжній виноград зброєробства — Бут, продавець зброї" },
	["aq_forced"] = { name = "Наліт іржі", desc = "Stained. Нанесено патину, зроблену з лимону та гірчиці.\n\nЯкщо гадаєте, що це безлад, то погляньте на це в ультрафіолеті" },
	["aq_oiled"] = { name = "Поверхневий гарт", desc = "Case Hardened. Загартовано за допомогою дерев’яного вугілля на високих температурах.\n\nТрохи кольору нікому не зашкодить" },
	["sp_spitfire_famas"] = { name = "Спітфайр", desc = "sp_spitfire_famas" },
	["am_zebra"] = { name = "Бійня", desc = "Slaughter. Нанесено зображення у вигляді смужок зебри з використанням алюмінієвого та хромового кольору з різними рефлексами, а потім вкрито томатно-червоним шаром.\n\nВалерія не платить Хав’єру, аби він задавав питання… вона йому платить, щоби він діставав відповіді" },
	["am_zebra_dark"] = { name = "Темна вода", desc = "Dark Water. Нанесено зображення у вигляді водяних смуг з використанням вугільно-металевого кольору різних відтінків на хромову основу.\n\nВода дає життя, але вона ж може його забрати" },
	["aa_vertigo"] = { name = "Гіпноз", desc = "Hypnotic. На пурпурову основу аерозольним балончиком нанесено чорний візерунок за допомогою трафарету.\n\nВам не просто спати захотілося, це ви помираєте від втрати крові" },
	["cu_spring_nova"] = { name = "Квітуча гілка", desc = "Bloomstick. Вручну нанесено аерографічне зображення квіток вишні, а потім вкрито глянцевим лаком." },
	["sp_hazard"] = { name = "Загроза", desc = "sp_hazard" },
	["aq_rust"] = { name = "Іржа", desc = "Rusty. Хоч зовнішня поверхня вкрилася іржею, однак ця зброя все ще цілковито боєздатна." },
	["am_slither_p90"] = { name = "Холоднокровний", desc = "Cold Blooded. На хромову основу нанесено напівпрозору червону фарбу задля ефекту адонованості. Потім лазером було вигравіювано візерунок у вигляді шкіри змії." },
	["am_carbon_fiber"] = { name = "Вуглепластик", desc = "Carbon Fiber. Аквадруком нанесено вуглецевий малюнок на графітну основу.\n\n«Те, що я найкраща в команді, не означає, що я могла б обійтися без неї» — Рона Сабрі, перспективний боєць" },
	["am_scorpion_p2000"] = { name = "Скорпіон", desc = "Scorpion. Нанесено зображення з ефектом анодування і хвилястими переходами, а на рукоятці наклеєне зображення червоного скорпіона." },
	["hy_feathers_aug"] = { name = "Крила", desc = "Wings. Декоровано наклейками із зображенням пір’їн орла.\n\nОрел виглядає дуже величним, коли викльовує ваші очі" },
	["sp_palm_shadow"] = { name = "Тропічний шторм", desc = "sp_palm_shadow" },
	["cu_walnut_nova"] = { name = "Горіх", desc = "Walnut. Нанесено зображення, що імітує горіхову дошку." },
	["aq_brass"] = { name = "Латунь", desc = "Brass. Має латунні деталі.\n\nЗолото дурнів" },
	["hy_blam"] = { name = "БАБАХ!", desc = "BOOM. Аквадруком нанесено на помаранчеву основу візерунок у вигляді звуконаслідувань із коміксів." },
	["hy_blam_simple"] = { name = "БАХ", desc = "hy_blam_simple" },
	["sp_dapple"] = { name = "Випалення", desc = "Scorched. Аерозольним балончиком нанесено малюнок у вигляді випалених сонцем плям.\n\nФенікс — це не символ руйнування… це символ відродження — Валерія Дженнер, революціонерка" },
