	["CSGO_Icosahedron"] = { name = "Ікосаедр", desc = "Memento. Аквадруком нанесено візерунок у вигляді геометричних фігур." },
	["cu_fireserpent_ak47_bravo"] = { name = "Вогнистий змій", desc = "Fire Serpent. На цей сувенір з Ruins нанесено зображення вогняного змія.\n\nЯкщо ви хочете вижити на вулицях, навчіться плюватись полум’ям" },
	["cu_favela"] = { name = "Кортісейра", desc = "Corticera. Нанесено зображення дерева кортісейра." },
	["cu_dragon_p90_bravo"] = { name = "Cмарагдовий дракон", desc = "Emerald Dragon. На цей сувенір з Chinatown нанесено зображення дракона.\n\nТрадиція може бути небезпечною" },
	["hy_siege_bravo"] = { name = "Зарості", desc = "Overgrowth. На цей сувенір із Siege аквадруком нанесено зображення камуфляжу.\n\n«Подивись мені в очі, що це стандартне запитання» — командир Фелікс Райлі" },
	["am_scales_bravo"] = { name = "Золотий короп", desc = "Golden Koi. Цей сувенір з Chinatown вкритий металевою фольгою з візерунком.\n\nГучна зброя, в будь-якому розумінні цього слова" },
	["aq_etched_mac10_bravo"] = { name = "Гравірований", desc = "Graven. Цей сувенір з Gwalior має латунні частини з гравіруванням.\n\nПорушує одразу дві заповіді" },
	["cu_season_elites_bravo"] = { name = "Чорна Лімба", desc = "Black Limba. Цей сувенір з Agency має темну патину та рукоятки з темного дерева лімба.\n\nКоли класична конструкція відповідає сучасній естетиці" },
	["hy_seaside_bravo"] = { name = "Шторм", desc = "Tempest. На цей сувенір із Seaside аквадруком нанесено зображення хвиль і хмар.\n\nБуря наближається" },
	["hy_crumple_dark_bravo"] = { name = "Багатокутники", desc = "hy_crumple_dark_bravo" },
	["hy_crumple_bravo"] = { name = "Тріщини", desc = "Facets, Shattered. На цей сувенір з Agency аквадруком нанесено зображення багатокутників.\n\n«Мої люди професіонали, вони роблять свою роботу правильно…» — Елліот Кігнсма кримінальний авторитет" },
	["hy_bluepolygon_bravo"] = { name = "Деметра", desc = "hy_bluepolygon_bravo" },
	["hy_ali_tile_bravo"] = { name = "Мозаїка", desc = "hy_ali_tile_bravo" },
	["an_emerald_bravo"] = { name = "Emerald", desc = "Emerald. Цей сувенір з Chinatown вкритий хромом, на який нанесено напівпрозору смарагдову фарбу з ефектом анодування.\n\nІ це те до чого опустився великий Себастьян Енке? Буркотливий стариган? Що з тобою трапилось…» — Учитель та бунтівник, частина 2" },
	["hy_mayan_dreams_bravo"] = { name = "Сни майя", desc = "Mayan Dreams. На цей сувенір із Ruins аквадруком нанесено зображення хмар майя.\n\nІнколи самопожертва має місце" },
	["am_ossify_blue"] = { name = "Морська піна", desc = "Ocean Foam. Аквадруком нанесено абстрактне зображення на основу металевого кольору." },
	["am_crumple"] = { name = "Графіт", desc = "Graphite. На хромову основу металевими фарбами нанесено візерунок з трикутників." },
	["sp_skull_diagram_bravo"] = { name = "Купа кісток", desc = "Bone Pile. На цей сувенір з Cache нанесено малюнок з черепами на електричній схемі.\n\nЩе один для колекції" },
	["sp_star_bravo"] = { name = "Зірка", desc = "Zirka. На цей сувенір із Cache нанесено зображення зірок.\n\nІмоген розділяє погляди свого батько щодо якості, але і амбіції навіть шаленіші" },
	["aq_steel_bravo"] = { name = "Прошарок іржі", desc = "Rust Coat. Цей сувенір зі Siege усе ще чудово працює, незважаючи на іржу зовні.\n\nШрами гартують характер" },
