	["am_ossify_red"] = { name = "Гемоглобін", desc = "Hemoglobin. Аквадруком нанесено напівпрозорий червоний абстрактний малюнок на металеву основу." },
	["am_electric_red"] = { name = "Сироватка", desc = "Serum. Металевими фарбами різних відтінків на хромову основу нанесено піксельний візерунок." },
