	["hy_snakeskin_red"] = { name = "Червоний пітон", desc = "hy_snakeskin_red" },
	["so_green"] = { name = "Зелене яблуко", desc = "Green Apple. Частини зброї пофарбовані аерозольною фарбою у зелені та чорні барви.\n\nЯблуко в день, продовжує життя" },
	["cu_money"] = { name = "Франклін", desc = "Franklin. Аквадруком нанесено малюнок у вигляді стодоларових купюр США.\n\nГроші не балакають… вони вбивають" },
	["so_orca"] = { name = "Смокінг", desc = "Tuxedo. Частини зброї пофарбовані аерозольною фарбою у чорні та білі барви.\n\nЗатягни краватку перед боєм" },
	["am_army_shine"] = { name = "Армійська сталь", desc = "Army Sheen. Нанесено металеву фольгу з тисненням у вигляді камуфляжного візерунку.\n\nПам’ятайте ваші тренування" },
	["am_oval_hex"] = { name = "Сталь у клітинку", desc = "Caged Steel. Зображення нанесено через трафарет у вигляді шестикутного візерунку з використанням фарби з металевими часточками, згодом також аквадруком нанесено текстуру овального вирізу.\n\nМатовво!" },
	["cu_pinstripe_ak47"] = { name = "Смарагдові витки", desc = "Emerald Pinstripe. Нанесено зображення у вигляді поверхні мореного дуба, покритої білими та зеленими витками.\n\n«Питаючи Імоґен ти питаєш мене. А тепер розслабся, випий скляночку скотчу, і давай поговоримо про бізнес» — Батько та «Фенікс», частина 2" },
	["sp_nightstripe"] = { name = "Нічна смужка", desc = "Night Stripe. Аерозольним балончиком нанесено малюнок у вигляді текстури стрічкової маски у кольоровій гамі нічного пейзажу.\n\nРозбризкуйте… Моліться… Фарбуйте… Ріжте." },
	["cu_m4_asimov"] = { name = "Азімов", desc = "Asiimov. Нанесено зображення у науково-фантастичному стилі.\n\nБудь-хто може передбачити майбутнє… але лише мрійник здійснює його" },
	["cu_sawedoff_octopump"] = { name = "Кракен", desc = "The Kraken. Нанесено ручний малюнок восьминога.\n\nЙого звільнили" },
	["cu_m4a1-s_elegant"] = { name = "Вартовий", desc = "Guardian. Пофарбовано синім кобальтом, а за допомогою трафарету на магазин нанесено логотип ССО.\n\n«Ти не маєш мені подобатись, та має бути в безпеці», — Кармен Кочінеро, спеціаліст із порятунку заручників" },
	["cu_p250_refined"] = { name = "Розпис хною", desc = "Mehndi. Аквадруком нанесено візерунок у вигляді крапель.\n\nПоклін вам" },
	["cu_awp_cobra"] = { name = "Червона лінія", desc = "Redline. Аквадруком нанесено малюнок із використанням вуглецевого волокна і прикрашено наклейками у вигляді червоних ліній.\n\nНіколи не бійтесь іти до кінця" },
	["cu_famas_pulse"] = { name = "Пульс", desc = "Pulse. Аквадруком нанесено малюнок у вигляді крупних фрагментів квіток фуксії.\n\nВражаючи кольори для вражаючої зброї" },
	["hy_marina_sunrise"] = { name = "Гавань", desc = "Marina. Аквадруком нанесено контрастний жовто-синій малюнок.\n\nДеякі назвуть вас героєм… інші злодієм" },
	["am_thorny_rose_mp9"] = { name = "Залізна троянда", desc = "Rose Iron. Ручний малюнок троянди з шипами нанесено металевими фарбами різних відтінків.\n\nБережіться його шипів" },
	["cu_skull_nova"] = { name = "Схід черепа", desc = "Rising Skull. Зображення черепа нанесено матовою фарбою на малюнок, що виконаний маркером на олійній основі.\n\n«Містер Ізакі, я не скаржусь… але скільки рушниць вам потрібно?» — Імоґен та Оні, частина 1" },
	["cu_sandstorm"] = { name = "Піщана буря", desc = "Sandstorm. Аквадруком нанесено пурпурово-бежевий графічний дизайн.\n\nПотрібно щось більше за брезент, щоби вас захистити" },
	["hy_kami"] = { name = "Камі", desc = "Kami. Аквадруком нанесено малюнок у стилі японської манґи.\n\nДеякі привиди не хочуть залишатись похованими" },
	["aq_obsidian"] = { name = "Магма", desc = "Magma. Має частини з мідним покриттям, які сховані за візерунком магми і червоною патиною, що отриманий за допомогою хлориду натрію та високих температур. Непокриті частині згодом були затемнені окисленням сірчаним калієм.\n\nПалаючий темп вогню" },
	["am_turqoise_halftone"] = { name = "Сині півтони", desc = "Cobalt Halftone. Аквадруком нанесено крупний півтоновий візерунок на металево-бірюзову основу.\n\n«Фелікс, ми обидва знаємо, Валерія хоче розпочати війну. Питання в тому, чи ти готовий до цього?» — Бут, продавець зброї" },
	["cu_aug_chameleonaire"] = { name = "Хамелеон", desc = "Chameleon. Було нанесено зображення хамелеона.\n\nСкритність може повністю змінити ситуацію" },
	["cu_ump_corporal"] = { name = "Капрал", desc = "Corporal. На зброю було нанесено малюнок у військовому стилі.\n\nПовагу не можна завоювати — її потрібно заслужити" },
	["cu_p90_trigon"] = { name = "Триґон", desc = "Trigon. Зброю було розмальовано в контрастні помаранчевий, сірий та чорний кольори.\n\nСтаньте людиною‐тайфуном" },
	["cu_mac10_redhot"] = { name = "Спека", desc = "Heat. Малюнок нанесено так, аби виглядало, ніби зброя щойно з кузні.\n\nДодай свої штрихи до зброї" },
	["sp_negev_turq_terrain"] = { name = "Ґрунт", desc = "Terrain. Аквадруком нанесено текстуру в ґрунтовому стилі.\n\n«Для люди яка мала б бути неупередженою, ваша донька обрала цікаву компанію» — Батько та «Фенікс», частина 1" },
	["cu_nova_antique"] = { name = "Антикваріат", desc = "Antique. Нанесено зображення із використанням аквадруку та наліпок задля досягнення вигляду дерев’яного та золотистого декору.\n\nЗ віком приходить мудрість та міць" },
	["an_famas_sgt"] = { name = "Сержант", desc = "Sergeant. Нанесено зображення із використанням патини різної глибини, створеної за допомогою маскувальної рідини, що надає зброї військового стилю, та довершено матовим покриттям.\n\nЗразковий" },
	["cu_mag7_heaven"] = { name = "Небесний вартовий", desc = "Heaven Guard. Аквадруком нанесено текстуру у вигляді комбінації тонких візерунків та наліпок із крилами.\n\nКрила янгола не завжди значать порятунок" },
	["cu_tec_isaac"] = { name = "Айзек", desc = "Isaac. Нанесено зображення у науково-фантастичному стилі." },
	["am_alloy_orange"] = { name = "Термоядерний сплав", desc = "Atomic Alloy. Анодовано в чорні та помаранчеві барви." },
	["cu_rubber_ak47"] = { name = "Вулкан", desc = "Vulcan. Нанесено зображення у спортивному стилі." },
	["cu_immortal_ssg08"] = { name = "Роздертий", desc = "Slashed. Аквадруком нанесено текстуру у вигляді сліду від кігтів." },
	["cu_progressiv_aug"] = { name = "Кручений", desc = "Torque. Нанесено зображення у роботичному стилі." },
	["cu_retribution_beretta"] = { name = "Відплата", desc = "Retribution. Нанесено зображення у військовому стилі." },
	["cu_howling"] = { name = "Виття", desc = "Howl. Нанесено зображення вовка, що гарчить." },
