	["am_fuschia"] = { name = "Пурпурпур", desc = "The Fuschia Is Now. Малюнок нанесено на хромову основу і покрито напівпрозорим зображенням фуксії з ефектом анодованості." },
	["aq_etched_cz75"] = { name = "Вікторія", desc = "Victoria. Декоровано із золотими акцентами та з ручним гравіруванням орнаментів." },
