	["hy_poly_camo"] = { name = "Фраг. камуфляж «Червоний»", desc = "Desert / Red FragCam. Текстуру багатокутного камуфляжу нанесено водостійкою фарбою." },
	["hy_poly_camo_desert"] = { name = "Фраг. камуфляж «Пустеля»", desc = "hy_poly_camo_desert" },
	["cu_usp_sandpapered"] = { name = "Бійка на дорозі", desc = "Road Rash. Нанесено зображення кулі в польоті, яке згодом обробили шліфувальним папером." },
	["hy_ssg08_marker"] = { name = "Об’їзд", desc = "Detour. Зброю оздоблено незмивним маркером поверх білого тла." },
	["am_royal"] = { name = "Келих", desc = "Chalice. Було нанесено малюнок прозорими королівською синьою та бурштиновою анодованими фарбами поверх хромового тла.\n\nМудрий вибір" },
	["am_metals"] = { name = "Лицар", desc = "Knight. Зброя має латунні інкрустації в стилі обладунків епохи Відродження.\n\n«Як це ти не можеш знайти Кочінеро та Ідзакі?» — командир Фелікс Райлі" },
	["am_chainmail"] = { name = "Кольчуга", desc = "Chainmail. Аквадруком нанесено напівпрозору текстуру кольчуги поверх срібного тла.\n\nСеред усіх членів «Фенікса», ніхто так не наближений до Валерії так, як її охоронець Наомі" },
	["aq_handcannon"] = { name = "Ручна гармата", desc = "Hand Cannon. Зброя має латунні вставки з використанням кислотної патини, що нагадує старовинні латунні гармати.\n\nБудьте готові зіштовхнутись з безлічу шпильок у випробуванні" },
	["am_metal_inlay"] = { name = "Темні віки", desc = "Dark Age. Нанесено зображення металізованими фарбами так, аби зброя здавалась інкрустованою у середньовічному стилі.\n\n«Я не можу вбити її коханця… можливо це зможе дати вам з Валерією трохи часу» — Батьківська любов, частина 2" },
	["hy_vines"] = { name = "Шипшина", desc = "Briar. Аквадруком нанесено текстуру лози та квітів, що звиваються.\n\nСправжній цілунок кохання не може врятувати від дотику щетини" },
	["so_indigo"] = { name = "Королівський синій", desc = "Royal Blue. Зброю пофарбовано в суцільні барви сірого, індіґо та малинового.\n\n«Вам треба посміхатися, Феліксе, так ви б мали більше успіху з жінками, якби ви не виглядали, як суворий виродок весь час» — Імоґен, продавець зброї" },
	["so_indigo_and_grey"] = { name = "Індіґо", desc = "Indigo\nЗброю пофарбовано в суцільні барви сірого, індіґо та синього.\n\n«Алекс Кінкайд, я впевнений, ти маєш багато запитань… будь ласка, дозволь на них відповісти» — «Фенікс» і оповідач, частина 1" },
	["cu_well_traveled_ak47"] = { name = "Турист", desc = "Jet Set. Приклад та руків’я цієї зброї обшито шкірою. Ця зброя побувала в багатьох містах.\n\n«Будь-хто хто зверне увагу, помітить точки і побачить, що вона зробила. Ми не маємо вдосталь часу, щоби знайти вихід…» — Батьківська любов, частина 1" },
	["cu_green_leather"] = { name = "Люкс", desc = "First Class. Приклад цієї зброї було обшито зеленою шкірою.\n\nМімоза продається окремо" },
	["cu_brown_leather_p90"] = { name = "Шкіра", desc = "Leather. Приклад цієї зброї було обшито коричневою шкірою.\n\nНе переживай, на митниці тебе пропустять!" },
	["cu_luggage_mac10"] = { name = "Пасажир", desc = "Commuter. Аквадруком нанесено клітковану текстуру, а також частково обшито штучною шкірою крокодила.\n\n«Вони купились на приманку, наступаємо…» - Елліот Кігнсма, найманець" },
	["cu_medieval_dragon_awp"] = { name = "Знання дракона", desc = "Dragon Lore. На зброю було нанесено зображення виплетеного дракона.\n\nНавіть двісті ключів не відкриють його таємницю" },
	["cu_luggage_p2000"] = { name = "Плацкарта", desc = "Coach Class. Аквадруком нанесено клітковану текстуру, а також коричневою шкірою обшито частини рукоятки.\n\nЦе не фантазія, але він доставить тебе туди, куди тобі потрібно" },
	["aq_pilot_deagle"] = { name = "Пілот", desc = "Pilot. Має латунні вставки і зображення авіаційних крил.\n\nВорог буде збитим" },
	["cu_leather_xm1014"] = { name = "Червона шкіра", desc = "Red Leather. Приклад цієї зброї обшито червоною шкірою.\n\n«Пакуй валізи. Підемо за головою сучого сина…» — Демон та Валькірія, частина 2" },
	["cu_luggage_sg553"] = { name = "Мандрівник", desc = "Traveler. Нанесено стрічковане зображення, схоже на вигляд класичних сумок для багажу. Також певні частини зброї обшито коричневою шкірою.\n\nВсі мають свою історію, і Хав’єр її вислухає перед тим як вбити тебе" },
	["cu_luggage_usp-s"] = { name = "Бізнес-клас", desc = "Business Class. Нанесено зображення з використанням барв та візерунків, характерних для класичних сумок для багажу. Також певні частини зброї обшито коричневою шкірою.\n\nДеяким недостатньо тільки простягнути ноги… вони хочуть миру та покою" },
	["hy_plaid1"] = { name = "Оливковий плед", desc = "Green Plaid. Аквадруком нанесено бежеву текстуру з оливковими клітковими візерунками.\n\n«Мені подобається. Це підкреслить мої очі на камер…» — Рона Сабрі, молода зірка" },
	["chy_plaid2"] = { name = "Зелений плед", desc = "chy_plaid2" },
	["cu_m4a1-s_silence"] = { name = "Витвір мистецтва", desc = "Master Piece. Нанесено зображення wildstyle-графіті." },
