	["am_nuclear_pattern1_glock"] = { name = "Реактор", desc = "Reactor. Візерунок із символів ядерної небезпеки нанесено металевою фарбою." },
	["hy_nuclear_pattern2_mp9"] = { name = "Призахідне сонце", desc = "Setting Sun. Аквадруком нанесено візерунок із символів ядерної небезпеки." },
	["sp_nuclear_pattern3_negev"] = { name = "Ядерні відходи", desc = "Nuclear Waste. Аквадруком нанесено візерунок із символів ядерної небезпеки." },
	["am_nuclear_skulls1_xm1014"] = { name = "Кістяник", desc = "Bone Machine. Аквадруком нанесено напівпрозору текстуру радіоактивних черепів та кісток на хромовану основу." },
	["am_nuclear_skulls2_famas"] = { name = "Стікс", desc = "Styx. Аквадруком нанесено напівпрозору текстуру радіоактивних черепів та кісток на хромовану основу." },
	["am_nuclear_skulls3_mac10"] = { name = "Ядерний город", desc = "Nuclear Garden. Аквадруком нанесено напівпрозору текстуру радіоактивних черепів та кісток на хромовану основу." },
	["hy_nuclear_skulls4_p250"] = { name = "Забруднення", desc = "hy_nuclear_skulls4_p250" },
	["hy_nuclear_skulls5_tec9"] = { name = "Токсичний", desc = "Toxic. Аквадруком нанесено яскраву текстуру радіоактивних черепів та кісток." },
	["so_grey_nuclear_green_bizon"] = { name = "Хімічний зелений", desc = "Chemical Green. Зброю пофарбовано в суцільні барви сірого та яскраво-зеленого." },
	["so_grey_nuclear_orange_five_seven"] = { name = "Шибеник", desc = "Hot Shot. Зброю пофарбовано в суцільні барви світло-сірого та гаряче-зеленого." },
	["cu_cerbrus_galil"] = { name = "Цербер", desc = "Cerberus. Нанесено зображення Цербера, міфічного триголового пса, який охороняє підземний світ." },
	["cu_tribute_ak47"] = { name = "Пустельний повстанець", desc = "Wasteland Rebel. Обшарпано, нанесено зображення за допомогою трафарету, а потім графіті.\n\n«Одного дня вони попросять мене воювати, і коли це трапиться, вони зрозуміють» — Наомі, охоронець Валірії Дженнер" },
	["aq_glock_coiled"] = { name = "Жорно", desc = "Grinder. Сталевий затвор був розфарбований масляними фарбами з використанням трафарету.\n\nЄдиний спосіб зробити діаманти це — застосувати тиск" },
	["am_g3sg1_murky"] = { name = "Морок", desc = "Murky. Анодовано, а потім нанесено аквадруком леопардовий візерунок.\n\nЗавжди є темніше місце ніж це" },
	["aq_m4a1s_basilisk"] = { name = "Василіск", desc = "Basilisk. Витравлено зображення василіска.\n\nВитріщатись негарно, але війна не місце для люб’язності" },
	["cu_m4a4_griffin"] = { name = "Грифон", desc = "Griffin. Вручну нанесено зображення грифона.\n\nФелікс вибирав собі команду серед всіх оперативників по всьому світу… І поки люди ставлять під питання їхні особистості, вони виконують все без питань!" },
	["sp_mag7_firebitten"] = { name = "Запальник", desc = "Firestarter. Аерозольним балончиком у кілька шарів нанесено зображення, а потім обшарпано, щоби створити ефект палаючих країв.\n\nЦе завжди починається з маленької іскри" },
	["cu_mp9_chevron"] = { name = "Жало", desc = "Dart. Водостійкою фарбою нанесені смуги у вигляді шевронів.\n\nЧим більше пострілів, тим більший шанс попадання" },
	["aq_p250_cartel"] = { name = "Картель", desc = "Cartel. Вигравіювано зображення черепів, квітів та зміїв.\n\nОдин маленький потиск перетворює кожен ранок на День мертвих" },
	["cu_p2000_fire_elemental"] = { name = "Дух вогню", desc = "Fire Elemental. Нанесено зображення вогняної істоти у графічному стилі.\n\nДеколи гра з вогнем може принести користь" },
	["aq_sawedoff_blackgold"] = { name = "Розбійник", desc = "Highwayman. Нанесено зображення заростей чортополоху чорною і золотою лаковою фарбою.\n\nНе потрібно відводити мізинець під час стрільби, але це виглядає красиво" },
	["cu_scar20_intervention"] = { name = "Сердечник", desc = "Cardiac. Запечатано у вінілову оболонку із сучасним помаранчевим і синім дизайном.\n\nбіп, біп, бі-і-і-і-і-і-іп…" },
	["sp_ump45_d-visions"] = { name = "Омана", desc = "Delusion. Нанесено зображення на морську тематику трафаретним друком.\n\nПоклик Каркоси" },
	["cu_xm1014_caritas"] = { name = "Лагідність", desc = "Tranquility. Вручну нанесено зображення жінки з довгим волоссям.\n\n«Я тут не заради того, що мені потрібно; Я тут заради того, що я бажаю» — Імоґен та Оні, частина 2" },
	["am_awp_glory"] = { name = "Лінкор", desc = "Man-o'-war. Нанесено основу з синього металіку, а потім вручну намальовано зображення візерунків і акцентів металевою золотою фарбою.\n\nПриверніть увагу деяких голів, перш ніж прострелити їх" },
	["cu_elites_urbanstorm"] = { name = "Міський перламутр", desc = "Urban Hazard. Нанесено зображення у вигляді сучасного камуфляжу із синіми акцентами.\n\nЗа світлом завжди слідує грім" },
	["aq_deagle_naga"] = { name = "Нага", desc = "Naga. Вигравіювано міфологічний мотив. \n\nЗвір в індійській міфології такий же небезпечний, як і на полі бою" },
	["cu_galil_abrasion"] = { name = "Тріскачка", desc = "Chatterbox. Вручну нанесено зображення, а потім обшарпано. Під час стрільби здається, що череп, зображений на протипиловій шторці, смикає щелепою.\n\nКажуть, що коли ви засмієтеся, то увесь світ засміється з вами… та коли сміється «Тріскачка», світ кричить від жаху" },
	["cu_glock_deathtoll"] = { name = "Катакомби", desc = "Catacombs. Аквадруком нанесено зображення черепів на срібну металеву основу.\n\nЗалякування — найщиріша форма поваги" },
	["cu_m4a4_ancestral"] = { name = "龍王 (Цар драконів)", desc = "龍王 (Dragon King). Нанесено зображення дракона в червоних і синіх кольорах.\n\n«Ця зброя підійде самому Королю мавп» — Бут, продавець зброї" },
	["cu_m249_sektor"] = { name = "Блокування системи", desc = "Sektor. Нанесено зображення у науково-фантастичному стилі.\n\nПрикриття ще ніколи не було таким чудовим" },
	["am_mac10_malachite"] = { name = "Малахіт", desc = "Malachite. Хвилястий візерунок нанесено гумовим гребенем на основу металевого кольору.\n\nЯскраві кольори зброї приховують її темне призначення" },
	["cu_mp9_deadly_poison"] = { name = "Смертельна отрута", desc = "Deadly Poison. Нанесено зображення динамічних і різких фігур, а також стилізований малюнок павука.\n\nПерший укус вас не вб’є, але за ним слідуватимуть ще 29" },
	["cu_p250_mandala"] = { name = "День мертвих", desc = "Muertos. Нанесено зображення кружних візерунків.\n\nВитримано у традиції та крові" },
	["cu_sawedoff_deva"] = { name = "Спокій", desc = "Serenity. Вручну нанесено зображення жінки з крилами.\n\nСпокій — це розкіш, що доступна небагатьом — Валерія Дженнер, революціонерка" },
	["aq_scar20_leak"] = { name = "Грот", desc = "Grotto. За допомогою губки та пензля нанесено зображення з металевим ефектом.\n\nЗасновник корпорації «Season» замовив цю зброю відомому службовцю Котаро Ідзакі на знак подяки за порятунок" },
	["aq_xm1014_sigla"] = { name = "Ртуть", desc = "Quicksilver. Аквадруком нанесено візерунок у вигляді мапи місцевості на срібну металеву основу.\n\nІноді вам потрібен ефект несподіванки, щоби вирішити ваші проблеми — Котаро Ідзакі, спеціаліст із проламування" },
	["an_tiger_orange"] = { name = "Тигряче ікло", desc = "Tiger Tooth. Анодовано в помаранчевий колір, а також вручну вигравіювано тигрові смуги.\n\nРідкісний, як тигр… Смертельний, як тигр…" },
	["am_marble_fade"] = { name = "Мармуровий перелив", desc = "Marble Fade. Нанесено зображення чорною і срібною фарбою під мармур, а потім вкрито градієнтом із трьох кольорів.\n\nНа лезі багато кольорів, та незабаром лишиться тільки колір крові" },
	["am_marbleized"] = { name = "Доплер", desc = "Doppler. Нанесено зображення чорною і срібною фарбою під мармур, а потім вкрито градієнтом.\n\nНадмірно уважне розглядання призводить до летального результату" },
	["cu_ak47_mastery"] = { name = "Elite Build", desc = "It has been custom painted using a combination of hydrographics and careful stenciling and detailed with heat-transfer decals.\n\nTread softly Naomi...people in your line of work aren't known for their longevity - The Shield and The Serpent Part 2" },
	["aq_mp7_ultramodern"] = { name = "Композитна броня", desc = "Armor Core. Розмальований чорною масляною фарбою з інкрустацією і прикрасами з нержавкої сталі.\n\n«Ідеально для оперативника, що цінує витонченість так само, як вогневу міць» — Бут, продавець зброї" },
	["aq_awp_twine"] = { name = "Хробачиний бог", desc = "Worm God. Малюнок черепу та коренів виконаний за допомогою стійких емалевих фарб.\n\nІснує коріння, за яке не варто триматись" },
	["am_bronze_sparkle"] = { name = "Бронзове Ар-деко", desc = "Bronze Deco. Розмальований у стилі ар-деко з використанням автомобільної фарби металік.\n\nВсі бажають срібла та золота" },
	["aq_p250_contour"] = { name = "Валентність", desc = "Valence. Розмальований блакитною та сірою фарбою, що підкреслює форму зброї .\n\nТвоє уміння не важливе, важливе твоє ставлення — Вчитель та бунтар, Частина 1" },
	["cu_fiveseven_banana"] = { name = "Мавпячий бізнес", desc = "Monkey Business. Це банан.\n\nІноді з їжею можна погратись" },
	["cu_galil_eco"] = { name = "Еко", desc = "Eco. Розмальований білою та зеленою фарбою і оздоблений наклейками з теплопередачею.\n\nЗа певну суму елітний підрозділ візьметься за будь-яку роботу" },
	["aq_famas_jinn"] = { name = "Джин", desc = "Djinn. Інкрустовано золотом і нікелем, вигравіюване зображення джина.\n\nОбережніше зі своїми бажаннями" },
	["cu_m4a1_hyper_beast"] = { name = "Hyper Beast", desc = "It has been custom painted with a beastly creature in psychadelic colours.\n\nYou really want to impress me Booth? Make this black light sensitive - Rona Sabri, Rising Star" },
	["cu_mag7_redhot"] = { name = "PaintKit_cu_mac10_redhot_tag", desc = "PaintKit_cu_mac10_redhot" },
	["am_negev_glory"] = { name = "PaintKit_am_awp_glory_Tag", desc = "PaintKit_am_awp_glory" },
	["cu_mac10_neonrider"] = { name = "Неоновий гонщик", desc = "Neon Rider. Нанесено зображення смертельного байкера.\n\nОдна частина — зброя, дві частини — Охоронець Пастки" },
	["cu_sawedoff_origami"] = { name = "Оріґамі", desc = "Origami. Вручну розмальований у стилі оріґамі.\n\nПотрібно багато часу, щоби створити щось прекрасне, але достатньо лиш миті, щоби зруйнувати його" },
	["cu_cz75_precision"] = { name = "Поул-позиція", desc = "Pole Position. Вручну розмальований червоно-сірим малюнком у гоночному стилі.\n\n«Ми маємо лише один шанс, давайте використаємо його правильно» — командир Фелікс Райлі" },
	["am_ump_racer"] = { name = "Ґран-прі", desc = "Grand Prix. Вручну нанесений ретро-малюнок гоночної тематики.\n\nНе забудь свій шолом" },
	["am_aqua_flecks"] = { name = "Сутінкова галактика", desc = "am_aqua_flecks" },
	["cu_chronos_g3sg1"] = { name = "Хронос", desc = "Chronos. Декоровано малюнками годинникового механізму в психоделічних тонах, нанесених аквадруком та оздоблено наліпками.\n\nЗ часом можливо все" },
	["hy_hades"] = { name = "Аїд", desc = "Hades. Аквадруком нанесено малюнок із зображенням античних грецьких шоломів.\n\nЯкщо вона бажала взяти в мене інтерв’ю, напевно було простіший спосіб здійснити задумане — Щит та Оповідач, частина 2" },
	["hy_icarus"] = { name = "Падіння Ікара", desc = "Icarus Fell. Аквадруком нанесено візерунок із пір’я і розплавленого воску навколо сонця.\n\nУ зраді Тернера моя провина… Я повинен усе виправити — Себастьян Енке, снайпер" },
	["cu_labyrinth"] = { name = "Лабіринт Мінотавра", desc = "Minotaur's Labyrinth. Аквадруком нанесено ізометричний лабіринт.\n\nВід мене неможливо втекти" },
	["sp_labyrinth1"] = { name = "Астеріон", desc = "Asterion. Аерозольним балончиком нанесено малюнок ізометричного лабіринту.\n\n«Королі всього лиш звичайні люди, а всі люди повинні мати рівні права» — Валерія Джаннер, революціонер" },
	["sp_labyrinth2"] = { name = "Провідна нитка", desc = "Pathfinder. Аерозольним балончиком нанесено малюнок ізометричного лабіринту.\n\n«Я надаю перевагу четвертому поколінню, але користувачам видніше» — Бут, продавець зброї" },
	["sp_labyrinth3"] = { name = "Дедал", desc = "Deadalus. Аерозольним балончиком нанесено малюнок ізометричного лабіринту.\n\n«Імоґене, якщо вони дізнаються, що ти накоїв, то нам гаплик!» — Сімейна справа, частина 1" },
	["cu_medusa_awp"] = { name = "Медуза", desc = "Medusa. Вручну нанесено зображення горгони.\n\nЯкщо ви мене бачите, ви вже мертві" },
	["gs_mother_of_pearl_elite"] = { name = "Дуелянт", desc = "Duelist. Нанесено зображення перламутровою фарбою з темною патиною.\n\nЖуйка до комплекту на додається" },
	["aa_pandora"] = { name = "Скринька Пандори", desc = "Pandora's Box. Пофарбовано за допомогою мультяшних духів.\n\n Світ у вогні, але ми все ще маємо надію" },
	["cu_poseidon"] = { name = "Посейдон", desc = "Poseidon. Нанесено зображення битви між Посейдоном та Рибами.\n\nТроє можуть зберегти таємницю, якщо двоє з них мертві" },
	["hy_zodiac1"] = { name = "Місяць у терезах", desc = "Moon in Libra. Аквадруком нанесено візерунок із мотивами зодіаку.\n\nСьогодні прекрасний день для нового кохання і розправи над заклятими ворогами" },
	["hy_zodiac2"] = { name = "Сонце в знаку Лева", desc = "Sun in Leo. Аквадруком нанесено візерунок із мотивами зодіаку.\n\nДля твоїх ворогів цей місяць стане вкрай скрутним" },
	["hy_zodiac3"] = { name = "Прогноз для судноплавства", desc = "Shipping Forecast. Аквадруком нанесено візерунок із мотивами зодіаку.\n\nХто може знати, що нас очікує в майбутньому" },
	["so_khaki_green"] = { name = "Десантний зелений", desc = "Para Green. Частини зброї розфарбовані суцільним кольором у стилі хакі.\n\n«Ким би він не був, це не змінить того, ким він став. Не переживай, Феліксе… Я спиню його!» — Паладін та учитель, частина 2" },
	["cu_anime_aug"] = { name = "Акіхабарська резолюція", desc = "Akihabara Accept. Зброя була прикрашена наклейкою в стилі обкладинки журналу з аніме.\n\nНя! ^_^" },
	["am_bamboo_jungle"] = { name = "Гідропоніка", desc = "Hydroponic. Зброя, за допомогою металевих фарб, була розмальована бамбуковими візерунками .\n\n«Зауважте, як зброярка Юкако використовує дерев’яні частини AK-47, щоби зробити акцент на зелених мотивах… Але не помиліться: гідропоніка прекрасна і смертельна як і сама природа» — Імоґен, початковий торговець зброєю" },
	["hy_bamboo_jungle_ink"] = { name = "Бамбуковий малюнок", desc = "Bamboo Print. Зброя була розфарбована аквадруком із бамбуковим візерунком.\n\nВставте сюди гру слів" },
	["hy_bamboo_jungle_black"] = { name = "Тінь бамбука", desc = "Bamboo Shadow. Аквадруком нанесено бамбукову текстуру.\n\nСина Котаро убили з цієї зброї… Одного дня він скористається нею та відомстить убивці за смерть сина" },
	["hy_bamboo_jungle"] = { name = "Бамбуковий ліс", desc = "Bamboo Forest. Аквадруком нанесено бамбукову текстуру.\n\n«Думаю, треба було домалювати панду» — Бут, продавець зброї" },
	["am_geometric_steps"] = { name = "Затоплена тераса", desc = "Aqua Terrace. Зброя, за допомогою металевих фарб, була розмальована геометричними візерунками.\n\nТиша і спокій…" },
	["hy_geometric_steps"] = { name = "Геометричні східці", desc = "Geo Steps. Аквадруком нанесено візерунок із геометричними мотивами." },
	["hy_geometric_steps_green"] = { name = "Антитераса", desc = "Counter Terrace. Аквадруком нанесено візерунок із геометричними мотивами.\n\nГей, Сабрі, бери ось цю… вона така ж голосна як і ти — Кармен Кочінеро, спеціаліст із порятунку заручників" },
	["hy_geometric_steps_yellow"] = { name = "Тераса", desc = "Terrace. Аквадруком нанесено візерунок із геометричними мотивами.\n\nТи занадто довірлива, Валерія… це тебе погубить — Франц Кригельд, стратег угрупування «Фенікс»" },
	["hy_kimono_diamonds"] = { name = "Неонове кімоно", desc = "Neon Kimono. Аквадруком нанесено візерунок із геометричними мотивами.\n\nЯ знаю, що прошу забагато… Тернет був тобі як син — Паладін і Вчитель, частина 1" },
	["hy_kimono_diamonds_orange"] = { name = "Помаранчеве кімоно", desc = "Orange Kimono. Аквадруком нанесено геометричні мотиви.\n\nВелика сила традицій" },
	["hy_kimono_diamonds_red"] = { name = "Багряне кімоно", desc = "Crimson Kimono. Аквадруком нанесено візерунок із геометричними фігурами\n\n«Тернере, можна зламати будь-кого, потрібно лишень знайти слабке місце» — Зрадник та Правдолюб, частина 2" },
	["sp_kimono_diamonds"] = { name = "М’ятне кімоно", desc = "Mint Kimono. Водостійкою фарбою нанесено візерунок геометричних фігур.\n\n«Буте, підійди, я покажу тобі, над чим працюю» — Йокако, зброярка" },
	["am_seastorm"] = { name = "Опівнічний шторм", desc = "Sunset Storm 壱. Аквадруком нанесено текстуру штормового моря на голубу металічну основу.\n\n«У мене немає ненависті до тебе, Себастьяне, лише жаль…» — Чейс Тернер, дезертир Коаліції" },
	["am_seastorm_blood"] = { name = "Шторм на заході сонця 壱", desc = "Sunset Storm 弐. Аквадруком нанесено текстуру штормового моря на голубу металічну основу.\n\nСпершу різниця непомітна, але придивившися до деталей… Починаєш розуміти, що різниця величезна" },
	["am_seastorm_shojo"] = { name = "Шторм на заході сонця 弐", desc = "am_seastorm_shojo" },
	["am_kimono_sunrise"] = { name = "Світанок", desc = "Daybreak. Металевими фарбами нанесено зображення сходу сонця.\n\nСонце світить однаково, як на лиходіїв так і праведників" },
	["am_so_keycolors"] = { name = "Ударний дриль", desc = "am_so_keycolors" },
	["so_aqua"] = { name = "Морська пташка", desc = "Seabird. Частини зброї пофарбовані аерозольною фарбою суцільними кольорами морської води та засмаги.\n\nМоє!" },
	["cu_ak47_courage_alt"] = { name = "Аквамаринова помста", desc = "Aquamarine Revenge. Фарбами було нанесено зображення зграї дельфінів.\n\nТут є ехо?" },
	["cu_cz75a_chastizer"] = { name = "Жовтий жакет", desc = "Yellow Jacket. Нанесено зображення у вигляді зігнутих форм.\n\nСновигай як метелик, кусай як люта ненависна комаха" },
	["am_famas_dots"] = { name = "Нейронна сітка", desc = "Neural Net. Аквадруком нанесено напівпрозору текстуру на металеву основу.\n\n«Я знаю. Однак це було правильне рішення…» — Сімейна справа, частина 2" },
	["cu_galilar_particles"] = { name = "Зліт ракети", desc = "Rocket Pop. Нанесено текстуру у вигляді ліній та піксельних частинок.\n\nЧудово підійде для спекотних літніх днів" },
	["aq_glock18_flames_blue"] = { name = "Пальник Бунзена", desc = "Bunsen Burner. Нанесено текстуру потертої синьої патини з візерунком язиків полум’я.\n\nСТУК-СТУК!" },
	["cu_m4a4_evil_daimyo"] = { name = "Злий магнат", desc = "Evil Daimyo. Нанесено текстуру червоними та чорними кольорами.\n\n«Кармене, я знайшов його…» — Демон та Валькірія, частина 1" },
	["cu_mp7_nemsis"] = { name = "Запеклий ворог", desc = "Nemesis. Нанесено зображення металевого монстра.\n\nВоно йде по тебе" },
	["am_mp9_nitrogen"] = { name = "Рубінове отруйне жало", desc = "Ruby Poison Dart. Нанесено текстуру червоними та фіолетовими фарбами з металевими відтінками на чорній основі.\n\nХто сказав, що найкращі друзі дівчат — діаманти?" },
	["cu_negev_annihilator"] = { name = "Крикун", desc = "Loudmouth. Нанесено зображення монстра, а потім обшарпано.\n\nА ну повернися, я до тебе говорю!" },
	["cu_nova_ranger"] = { name = "Рейнджер", desc = "Ranger. Аквадруком нанесено текстуру у вигляді мазків пензлика та оздоблено наліпками з теплопередачею.\n\nХтось знає який рахунок?" },
	["aq_p2000_boom"] = { name = "Пістолет", desc = "Handgun. Зброю було розмальовано вручну малюнком із рукою, яка цілиться.\n\nЗабавки закінчилися" },
	["cu_ump45_uproar"] = { name = "Бунт", desc = "Riot. Аквадруком нанесено текстуру та оздоблено наліпками з теплопередачею.\n\n«Якщо ти хотів, щоби діяли більш вправніше, напевне, треба було сказати про це…» — Хав’єр Алвісо, перший із «Фенікс»" },
	["cu_usp_progressiv"] = { name = "Кручений", desc = "Torque. Нанесено текстури у стилі модерного чорного, білого та жовтого кольорів.\n\n«Вони тобою маніпулюють, Котаре! І якщо ти цього не помічаєш, то у тебе величезні проблеми…» — Фелікс Райлі, командир" },
	["cu_ak47_winter_sport"] = { name = "Фронт в імлі", desc = "Frontside Misty. Пофарбовано яскравими арктичними кольорами.\n\n«Обожнюю працювати у сніжну погоду… Завжди отримую чудовий результат» — Хав’єр Алвісо, перший з «Фенікс»" },
	["cu_dualberretta_dragons"] = { name = "Подвійні дракони", desc = "Dualing Dragons. На дулі та затворі лазером вигравіювано візерунок драконів.\n\nМаріан завжди був крутішим за Сильвію" },
	["cu_famas_lenta"] = { name = "Уцілілий", desc = "Survivor Z. Пофарбовано поверх трафарету з клейкої стрічки, а потім обшарпано.\n\nЩо має червоний та білий кольори та стріляє автоматично?" },
	["gs_g3sg1_flux_purple"] = { name = "Потік", desc = "Flux. Пофарбовано у пурпуровий та срібний металічні кольори.\n\nПлан — послідовність дій, які ніколи не відбудуться!" },
	["gs_galil_nightwing"] = { name = "Кам’яне серце", desc = "Stone Cold. Частково нанесено синю металічну фарбу. На магазин та приклад нанесено логотип дизайнера.\n\nРано чи пізно шляхи учня та учителя розходяться!" },
	["gs_glock18_wrathys"] = { name = "Примари", desc = "Wraiths. На затвор нанесено зображення заблукалих душ.\n\nПостріли з нього пронизують темряву" },
	["gs_m249_nebula_crusader"] = { name = "Космічний хрестоносець", desc = "Nebula Crusader. Розфарбовано у вигляді класичного аркадного автомата.\n\nВи можете скористатися ним, але спочатку киньте монету" },
	["gs_m4a1s_snakebite_gold"] = { name = "Золотий звиток", desc = "Golden Coil. Аквадруком нанесено зображення атакуючої змії.\n\nГадюка може напасти звідки завгодно" },
	["cu_mac10_alekhya_duo"] = { name = "Хроматика", desc = "Rangeen. Нанесено текстуру в єгипетському стилі.\n\nЧи зможете ви витримати його зловісний погляд?" },
	["cu_mag7_myrcene"] = { name = "Ядро з кобальту", desc = "Cobalt Core. Нанесено текстуру у футуристичному стилі.\n\n«Кармен, оберігайте його» — Валькірія та коханець, частина 1" },
	["cu_mp7_classified"] = { name = "Спеціальна доставка", desc = "Special Delivery. Розмальований у -ВИДАЛЕНО-\n\nЯкщо я зроблю те, про що ви мене просите, ви мене відпустите? — «Фенікс» і оповідач, частина 2" },
	["hy_p250_crackshot"] = { name = "Постріл у літуна", desc = "Wingshot. Аквадруком нанесено яскраву текстуру.\n\n«Інколи, щоб отримати бажане, достатньо лише ніжного дотику» — Франц Криґель, стратег «Фенікс»" },
	["gs_scar20_peacemaker03"] = { name = "Зелені берети", desc = "Green Marine. Нанесено камуфляжну текстуру металічними фарбами.\n\n«Слухайте всі… Хоч Ізакі та Коцієро дезертирували, наша ціль залишається незмінною — потрібно врятувати Алекса Кінкейда!» — Фелікс Райлі, командир" },
	["cu_ssg08_technicality"] = { name = "Великий ствол", desc = "Big Iron. Аквадруком нанесено жовту та помаранчеву текстуру, а потім трішки затерли.\n\n«Перемога з приміткою — теж перемога», — Рона Себрі, військовий із великим потенціалом" },
	["cu_usp_kill_confirmed"] = { name = "Вбивство підтвердженно", desc = "Kill Confirmed. Нанесено зображення кулі, що летить із пробитого черепа.\n\nДві у груди, одна в голову" },
	["aq_xm1014_scumbria"] = { name = "Скумбрія", desc = "Scumbria. Нанесено текстуру металічними фарбами.\n\n«Повільніше, можливо привернути увагу Валерії можна іншим способом…» — Батьківська любов, частина 3" },
	["cu_ak47_point_disarray"] = { name = "Точка плутанини", desc = "Point Disarray. Водостійкою фарбою нанесено зображення геометричних фігур.\n\n«Подивишся зблизька — хаос… А здалеку — красиво!» — Франц Кріґельд, стратег «Фенікс»" },
	["am_aug_jumble"] = { name = "Рикошет", desc = "Ricochet. Нанесено зображення перетину двох ліній.\n\n«Котаро не потребує нашого захисту» — Валькірія та коханець, частина 2" },
	["cu_bizon_noxious"] = { name = "Паливний стрижень", desc = "Fuel Rod. Нанесено візерунок у вигляді радіоактивного боєзапасу.\n\nІнколи найкращий план — прориватися з боєм" },
	["aq_deagle_corinthian"] = { name = "Чесний гравець", desc = "Corinthian. Лазерне гравіювання та оснащений особливою шкіряною рукояткою.\n\nЗахист. Надія. Довіра. Завзяття." },
	["cu_fiveseven_retrobution"] = { name = "Відплата", desc = "Retrobution. Вручну пофарбовано аквадруком у ретро стилі.\n\nДещо ніколи не вийде з моди" },
	["cu_g3sg1_executioner"] = { name = "Кат", desc = "The Executioner. Нанесено зображення кісток\n\n«Я завжди беру трофей» — Хав’єр Алвісо, перший із «Фенікс»" },
	["gs_m4a4_royal_squire"] = { name = "Величний Паладін", desc = "Royal Paladin. Подряпано та пофарбовано у вигляді величного візерунка.\n\n«Цієї розмови не було, зрозумів?» — Паладін та батько, частина 1" },
	["cu_negev_impact"] = { name = "Потужний навантажувач", desc = "Power Loader. Аквадруком нанесено текстуру в стилі механізмів.\n\nДами бояться. Лакеї поважають." },
	["am_p2000_imperial_red"] = { name = "Імперський", desc = "Imperial. Лазерне гравіювання та розмальований металевим червоним та жовтим кольорами.\n\n«Мої почуття не мають Кео, я маю захищати імперію» — Бут, продавець зброї" },
	["cu_p90_shapewood"] = { name = "Різьба по дереву", desc = "Shapewood. На дерев’яній основі вирізьблено текстури, відшліфовано та заламіновано.\n\n Щипчики не входять у комплект" },
	["gs_sawedoff_necromancer"] = { name = "Йорік", desc = "Yorick. Аквадруком нанесено зображення черепа та вручну намальовані золоті акценти.\n\nРозенкранц та Гільденстерн ніколи не мали шансу…" },
	["hy_scar20_jungler"] = { name = "Бунт", desc = "Outbreak. Розмальований яскравими кольорами зеленого.\n\n«Валерія стає сміливішою… прийшов час бою з нею» — командир Фелікс Райлі" },
	["gs_sg553_tiger_moth"] = { name = "Міль-тигр", desc = "Tiger Moth. Нанесено зображення у вигляді крилець молі.\n\nВалерія втрачає пильність… Незабаром вона теж почує відлуння бою — Фелікс Райлі, командир" },
	["cu_tec9_avalanche"] = { name = "Лавина", desc = "Avalanche. Аквадруком нанесено гладку і футуристичну текстуру.\n\n«Ми маємо обережно діяти проти неї, один хибний крок і вона знищить нас обох» — Змія і Шукач правди" },
	["aq_xm1014_hot_rod"] = { name = "Пальник Теклу", desc = "Teclu Burner. Полум’я нанесене металевою фарбою.\n\nІнколи звичайного пальника не вистачає…" },
	["gs_ak47_supercharged"] = { name = "Паливний інжектор", desc = "Fuel Injector. Основу автомата розфарбовано яскраво-жовтими фарбами, а ріжок має вигляд механізму.\n\nПроживай своє життя на чверть ріжка щоразу…" },
	["cu_bizon_citizen"] = { name = "Світлова сфера", desc = "Photic Zone. Нанесено камуфляжну текстуру флюоресцентними фарбами.\n\nТе, що ви чуєте — це не гуркіт хвиль…" },
	["aq_deserteagle_kumichodragon"] = { name = "Дракон Камічо", desc = "Kumicho Dragon. Вигравіювано зображення драконів на затворі та руків’ї.\n\n«Мені дуже подобаються ваші творіння, Юкако, я б хотів із вами побалакати…» — Гакслі, Змагання" },
	["cu_fiveseven_augmented"] = { name = "Тріумвірат", desc = "Triumvirate. Аквадруком нанесено блакитний камуфляж зі світлим відтінком.\n\n«Я не заробляю на війні, я заробляю на людях…» — Бут, продавець зброї" },
	["gs_glock18_award"] = { name = "Королівський легіон", desc = "Royal Legion. Ретельно вигравірувано візерунок.\n\nНаомі розуміє, що рано чи пізно, вона віддасть життя за Валерію" },
	["gs_m4a4_pioneer"] = { name = "Світило війни", desc = "The Battlestar. Усі кути були ретельно зрізані з моменту придбання.\n\nОстерігайтеся тих, хто згадує про спостережну вежу" },
	["am_mac10_electricity"] = { name = "Лазуритний алігатор", desc = "Lapis Gator. Аквадруком нанесено яскраву текстуру з відтінком шкіри алігатора.\n\nЧовен на повітряній подушці не входить до комплекту" },
	["gs_mag7_praetorian"] = { name = "Преторіанець", desc = "Praetorian. Розфарбовано блискучими металевими фарбами.\n\nШпиль формується" },
	["sp_mp7_impire"] = { name = "Чортеня", desc = "Impire. Аквадруком нанесено візерунок із натовпом чортів.\n\nСтрата ще ніколи не була такою чарівною" },
	["cu_ssg08_necropos"] = { name = "Некромант", desc = "Necropos. Нанесено візерунок у вигляді нечестивих рун.\n\nКожному столяру потрібен молоток, кожному кравцю потрібне веретено…" },
	["gs_tec9_jambiya"] = { name = "Джамбія", desc = "Jambiya. Нанесено текстуру у стилі Середнього Сходу з елементами лазерного гравіювання.\n\nШахерезада розповіла тисячі історій… Але ця буде остання, яку ви почуєте" },
	["gs_usp_voltage"] = { name = "Свинцеве дуло", desc = "Lead Conduit. Глушник та затвор були виготовлені на замовлення.\n\n«Ви отримали те, що хотіли, тепер ви знаєте, що це означає…» — Бут, продавець зброї" },
	["cu_aug_swallows"] = { name = "Прудка зграя", desc = "Fleet Flock. Водостійкою фарбою нанесено зображення пташок, які летять крізь хмари.\n\nДеякі птахи символізують гармонію і мир… однак це інші" },
	["cu_bizon_Curse"] = { name = "Суд Анубіса", desc = "Judgement of Anubis. Нанесено зображення на єгипетську тематику.\n\nСпрацьовує навіть на легшому за шовк." },
	["gs_cz75a_redastor"] = { name = "Червоний радар", desc = "Red Astor. Нанесено зображення з червоними та білими акцентами. \n\nБут — це синонім сімейної драми, але в кого є час для драм, коли мова йде про бізнес? — Гакслі, «Змагання»" },
	["gs_dualberettas_ventilators"] = { name = "Лопасті", desc = "Ventilators. Ці пістолети були елегантно пофарбовані в хром.\n\nЗброя для митців від митців" },
	["sp_g3sg1_militiaorange"] = { name = "Помаранчева катастрофа", desc = "Orange Crash. Трафарет і помаранчева фарба дали цій рушниці її особливий вигляд.\n\nСтаньте сучасним Шир-Ханом" },
	["gs_galilar_incenerator"] = { name = "Сутичка", desc = "Firefight. Вручну нанесено зображення з червоними акцентами.\n\nБут спродав цю зброю Валерії зі знижкою, аби загладити ситуацію з «Феніксом»" },
	["cu_m249_spectre"] = { name = "Спектр", desc = "Spectre. Розфарбовано в лискучій сучасній манері.\n\nГромадянська війна «Фенікса» на порозі" },
	["cu_m4a1s_soultaker"] = { name = "Вогнище Чантіко", desc = "Chantico's Fire. Розфарбовано мінливими кольорами із зображенням усміхненого обличчя на руків’ї.\n\nЗахищайте свою власність" },
	["am_mp9_bioleak"] = { name = "Біовитік", desc = "Bioleak. Різні відтінки зеленого створюють ілюзію хімічних розливів.\n\nОСТЕРІГАЙТЕСЯ ПРЯМОГО ПОТРАПЛЯННЯ" },
	["hy_p2000_oceani"] = { name = "Океанічний", desc = "Oceanic. Нанесено чорну основу, а потім синє зображення.\n\nЧи чутно щось від Котаро або Кармен? — командир Фелікс Райлі" },
	["cu_p250_asiimov"] = { name = "Азімов", desc = "Asiimov. Нанесено зображення у науково-фантастичному стилі.\n\n«Якщо ви думаєте, що для того, щоб вбити людину потрібна гвинтівка, то ви нічого не знаєте…» — Міха Бітон, снайпер «Феніксів»" },
	["gs_sawedoff_fubar"] = { name = "Фубар", desc = "Fubar. Цей бувалий дробовик має позначки на стволі.\n\nПочинається з вибуху… а завершується у тиші" },
	["cu_sg553_atlas"] = { name = "Атлас", desc = "Atlas. Водостійкою фарбою нанесено сучасний камуфляж.\n\nКолись цей дизайн було зарезервовано для оперативної групи коаліції, але Гакслі вдався до корпоративного шпигунства, аби зробити його ексклюзивним для «Фенікса»" },
	["gs_ssg08_armacore"] = { name = "Примарний хрестоносець", desc = "Ghost Crusader. Нанесено зображення із синіми арктичними акцентами.\n\nЯ ніколи не зможу замінити те, що в нас відібрали… але я можу відібрати в них ще більше — Фенікс та Посвячений, частина 2" },
	["am_tec9_redblast"] = { name = "Зворотний вхід", desc = "Re-Entry. На ствол нанесено зображення полум’я.\n\nНенавиджу ніколи не помилятися — Кармен Кочінеро, спеціаліст із порятунку заручників" },
	["cu_ump45_primalsaber"] = { name = "Саблезубий", desc = "Primal Saber. Нанесено зображення черепа шаблезубого тигра.\n\nУсякий хижак рано чи пізно помирає" },
	["cu_xm1014_spectrum"] = { name = "Чорна краватка", desc = "Black Tie. Пофарбований у чорний та білий хром, цей дробовик найкраще підходить для врочистих подій.\n\nМаємо Ізакі та Кочінеро. Вам вони для чогось потрібні чи я можу їх убити просто зараз? — Найманець і змія, частина 1" },
	["am_marbleized_g"] = { name = "Гамма-ефект Доплера", desc = "Gamma Doppler. Нанесено зображення чорною і срібною фарбою під мармур, а потім вкрито градієнтом." },
	["gs_autotronic"] = { name = "Автотронік", desc = "Autotronic. Анодовано в червоний колір із використанням сталевої сітки для полегшення ваги." },
	["am_marked_up"] = { name = "Ручний розпис", desc = "Freehand. Чорно-пурпурове зафарбування прикрашене металевими візерунками." },
	["gs_aug_aristocrat"] = { name = "Аристократ", desc = "Aristocrat. Срібна філігрань нанесена на блакитну анодовану основу.\n\nЗа 1%" },
	["gs_awp_phobos"] = { name = "Фобос", desc = "Phobos. Виготовлена з металевих панелей та міцних електронних компонентів.\n\nЗбудуймо кращий світ" },
	["cu_five_seven_daimyo"] = { name = "Несамовитий даймьо", desc = "Violent Daimyo. Графічний дизайн нанесений фіолетовими та чорними барвами.\n\nЯк ви правитимете?" },
	["cu_glock_wasteland_rebel"] = { name = "Пустельний повстанець", desc = "Wasteland Rebel. Обшарпано, нанесено зображення за допомогою трафарету, а потім графіті.\n\nНе забудьте заплатити" },
	["gs_m4a1_mecha_industries"] = { name = "Меха-індустрія", desc = "Mecha Industries. Виготовлена із загартованих керамічних панелей.\n\nМістить найсучасніші технології" },
	["cu_m4a4_desolate_space"] = { name = "Безлюдний космос", desc = "Desolate Space. Була прикрашена малюнками космічної тематики.\n\n«Ми втратили контакт»" },
	["aq_mac_10_alien_camo"] = { name = "М’ясоїдний", desc = "Carnivore. Аквадруком нанесено червоно-пурпуровий малюнок на металеву основу.\n\nПриготуйте перед подачею" },
	["aq_nova_sci_fi"] = { name = "Екзо", desc = "Exo. Аквадруком нанесено текстуру зображення срібної панелі поверх пурпурової основи.\n\nМає значення лише те, що всередині" },
	["gs_p2000_imperial_dragon"] = { name = "Імперський дракон", desc = "Imperial Dragon. Нанесено зображення дракона за допомогою червоної металевої фарби.\n\nДивіться за Захід" },
	["gs_p250_metal_panels"] = { name = "Металеве вбрання", desc = "Iron Clad. Декоровано прикутими металевими панелями.\n\nНадійність та безпеку гарантовано" },
	["gs_p90_full_throttle"] = { name = "Чоппер", desc = "Chopper. Помаранчевою та синьою металевими фарбами нанесено малюнок із полум’яним мотивом.\n\nРемені в комплект не входять" },
	["gs_pp_bizon_harvester"] = { name = "Жнець", desc = "Harvester. Декоровано біомеханічним зображенням.\n\nБіомеханічний експеримент удався" },
	["cu_r8_cybersport"] = { name = "Перезавантаження", desc = "Reboot. Декоровано червоними акцентами поверх срібних і чорних панелей.\n\n«Ви пробували вимкнути та ввімкнути його знову?»" },
	["cu_sawed_off_lime"] = { name = "Лаймове світло", desc = "Limelight. Нанесено зображення м’яких ліній лаймового та білого кольору.\n\nВся увага на вас" },
	["gs_scar20_bloodsport"] = { name = "Кривавий спорт", desc = "Bloodsport. Аквадруком нанесено червоно-чорне зображення, вкрите білими наклейками.\n\nСпонсоровано прихильниками ССО…" },
	["cu_sg553_aerial"] = { name = "Політ", desc = "Aerial. Нанесено зображення срібним та жовтим кольором, а потім додано наклейку з білим крилом.\n\nОтримайте перевагу в повітрі" },
	["am_tec_9_sea_salt"] = { name = "Іній", desc = "Ice Cap. Аквадруком нанесено текстуру блакитної та білої абстракції.\n\nЗатопіть опозицію" },
	["bloodhound_black_silver"] = { name = "Обвуглені", desc = "Charred. Зроблені з класичної чорної м’якої шкіри.\n\n«Винагорода — це добре, та ви не вдарите нею до крові», — Кармен Кочінеро, спеціаліст із порятунку заручників" },
	["bloodhound_snakeskin_brass"] = { name = "Укус змії", desc = "Snakebite. Виготовлені шляхом поєднання шматків звичайної та зміїної шкіри.\n\n«Будьмо відвертими… за Сергієм ніхто не сумує», — Ганс Кригельд, стратег угрупування «Фенікс»" },
	["bloodhound_metallic"] = { name = "Засмаглі", desc = "Bronzed. Шкіра пофарбована в металевий та бронзовий кольори.\n\nОперація «Дойда» поклала кінець рум’яному снайперу Чезу Тернеру" },
	["bloodhound_guerrilla"] = { name = "Партизанські", desc = "Guerrilla. Виготовлені за допомогою поєднання коричневої та синтетичної шкіри з камуфляжним розфарбуванням.\n\n«Жодних сумнівів, Валерія готується до помсти…» — Себастьян Енке, снайпер" },
	["handwrap_leathery"] = { name = "Шкіра", desc = "Leather. Зроблені зі шкіри, марлі та нечестивих намірів.\n\nСтаньте горезвісними" },
	["handwrap_camo_grey"] = { name = "Пікс. камуфляж «Ялина»", desc = "Spruce DDPAT. Зовнішня оболонка — це тканина із сірим цифровим камуфляжем.\n\nОдні заявляють, що вони круті… інші просто показують це" },
	["handwrap_red_slaughter"] = { name = "Бійня", desc = "Slaughter. На цих багряних бинтах важко відрізнити тканину від кривавих плям.\n\nХто ж не любить працювати руками?" },
	["handwrap_fabric_orange_camo"] = { name = "Дикі землі", desc = "Badlands. Цей захист типу «зроби сам» зібрано з підручних матеріалів.\n\nФелікс заплющує очі на незаконні чемпіонати з боксу серед оперативної групи коаліції" },
	["slick_black"] = { name = "Місячний візерунок", desc = "Lunar Weave. Синя замша переплетена з чорною шкірою для надання цим рукавицям старовинного вигляду.\n\nНа ваших руках кров… якщо говорити фігурально" },
	["slick_military"] = { name = "Конвой", desc = "Convoy. Зроблені з коричневої м’якої шкіри та пофарбованої замші.\n\nІноді потрібно діяти розсудливо" },
	["slick_red"] = { name = "Багряний візерунок", desc = "Crimson Weave. Чорна замша переплетена з червоною шкірою для надання цим рукавицям старовинного вигляду.\n\nЗалишайте слід, а не відбитки" },
	["slick_snakeskin_yellow"] = { name = "Гримучник", desc = "Diamondblack. Зроблені зі шматків засмаглої шкіри, чорної шкіри та зміїної шкіри.\n\n«Звичайні види для невдах…» — Імоґен, продавець зброї" },
	["sporty_green"] = { name = "Живий лабіринт", desc = "Hedge Maze. Зелені з білим рукавиці, виготовлені Icarus Athletics.\n\nТільки боягузи бояться підлетіти близько до сонця" },
	["sporty_purple"] = { name = "Скриня Пандори", desc = "Pandora's Box. Чорні з фіолетовим рукавиці, на долоні ледь помітний візерунок.\n\nКожен підривник зі спітнілими долонями повинен мати пару таких" },
	["sporty_light_blue"] = { name = "Надпровідник", desc = "Superconductor. Сині з чорним рукавиці ідеально підходять для тих, что хоче бути феноменом.\n\nЇм не потрібні проблеми" },
	["sporty_military"] = { name = "Сухий", desc = "Arid. Кілька відтінків коричневого і тонкий камуфляжний візерунок дають цім рукавичкам видатний зовнішній вигляд.\n\n«Якщо ми хочемо стати героями, то нам потрібно виглядати, як герої…» — Рона Сабрі, перспективний боєць" },
	["specialist_ddpat_green_camo"] = { name = "Пікс. камуфляж «Ліс»", desc = "Forest DDPAT. Вручну зшита шкіра з нанесенням камуфляжу.\n\nДехто не любить бруднити свої руки" },
	["specialist_kimono_diamonds_red"] = { name = "Багряне кімоно", desc = "Crimson Kimono. Пара чорних рукавичок із нанесеним візерунком червоного алмазу.\n\nПочинайте помічати червоний" },
	["specialist_emerald_web"] = { name = "Смарагдове павутиння", desc = "Emerald Web. На тильній стороні рукавичок нанесено візерунок павутиння.\n\nДисципліна — запорука успіху" },
	["specialist_white_orange_grey"] = { name = "Фундамент", desc = "Foundation. До білих із червоним рукавиць додано помаранчеві відтінки.\n\nКажуть, Тортуга надихався серією зброї «Азімов»" },
	["motorcycle_basic_black"] = { name = "Затемнення", desc = "Eclipse. Це оригінальний класичний чорний дизайн, який зробив Тортугу загальною назвою.\n\nЦе не криза середнього віку" },
	["motorcycle_mono_boom"] = { name = "БАХ!", desc = "Boom! Ці рукавиці вкриті звуконаслідувальними написами з коміксів.\n\nБАМ! БУМ! ПТИЩ!" },
	["motorcycle_mint_triangle"] = { name = "М’ята", desc = "Spearmint. Ці рукавиці з білої шкіри та червоної стрічки не тільки зручні, а й стильні.\n\n«За таку ціну Гакслі міг би додати хоча б баночку льодяників…» — командир Фелікс Райлі" },
	["motorcycle_triangle_blue"] = { name = "Свіжа м’ята", desc = "Cool Mint. На м’яку синю шкіру нанесено арктичні кольорові геометричні візерунки.\n\nБій ще ніколи не був таким освіжаючим." },
	["slick_snakeskin_white"] = { name = "Королівська змія", desc = "King Snake. Виготовлено з білої шкіри і зміїної шкури." },
	["slick_plaid_purple"] = { name = "Імператорський плед", desc = "Imperial Plaid. Ця пара рукавиць із фланелі і шкіри має імперське забарвлення." },
	["slick_stitched_black_orange"] = { name = "Обгін", desc = "Overtake. Найголовніше в цих рукавицях із класичної чорної і вичиненої шкіри — це швидкість." },
	["slick_slick_stitched_green_grey"] = { name = "Гоночний зелений", desc = "Racing Green. Ця пара зелених рукавиць оздоблена сплетеним із сірої шкіри візерунком шеврона." },
	["sporty_poison_frog_blue_white"] = { name = "Амфібія", desc = "Amphibious. Ці синтетичні синьо-білі рукавиці швидко висихають і добре пропускають повітря." },
	["sporty_poison_frog_red_green"] = { name = "Бронзова патина", desc = "Bronze Morph. Ці синтетичні червоно-зелені рукавиці швидко висихають і добре пропускають повітря." },
	["sporty_black_webbing_yellow"] = { name = "Омега", desc = "Omega. Ці синтетичні чорні рукавиці мають виразне жовте оздоблення." },
	["sporty_blue_pink"] = { name = "Порок", desc = "Vice. Ці синтетичні рукавиці виготовлені з приголомшливого поєднання рожевої і синьої технічної тканини." },
	["motorcycle_choco_boom"] = { name = "БУМ!", desc = "POW!. Ця пара вкрита яскравими звуконаслідувальними словами з коміксів." },
	["motorcycle_basic_green_orange"] = { name = "Черепаха", desc = "Turtle. Ця зелено-сіра пара рукавиць має оздоблення яскравого помаранчевого кольору." },
	["motorcycle_yellow_camo"] = { name = "Транспорт", desc = "Transport. Ця пара має витончений цифровий камуфляж." },
	["motorcycle_trigrid_blue"] = { name = "Полігон", desc = "Polygon. Сконструйовані шляхом поєднання штучних матеріалів і шкіри у різних відтінках синього." },
	["handwrap_leathery_fabric_blue_skulls"] = { name = "Кобальтові черепи", desc = "Cobalt Skulls. На цих синіх тканинних бинтах надруковано узор із черепів." },
	["handwrap_leathery_fabric_geometric_blue"] = { name = "Відбиток", desc = "Overprint. На цих чорних тканинних бинтах надруковано гумоподібний геометричний узор." },
	["handwrap_leathery_ducttape"] = { name = "Скотч", desc = "Duct Tape. Ці саморобні бинти для рук на дивно міцні, але зовсім не пропускають повітря." },
	["handwrap_leathery_fabric_green_camo"] = { name = "Дерева", desc = "Arboreal. Ці тканинні бинти були зафарбовані узором лісового камуфляжу." },
	["bloodhound_hydra_black_green"] = { name = "Смарагд", desc = "Emerald. Рукавиці з лакованою зеленою емблемою, зроблені з класичної чорної м’якої шкіри." },
	["bloodhound_hydra_green_leather_mesh_brass"] = { name = "Мангри", desc = "Mangrove. Виготовлено шляхом поєднання шкіри, синтетичної сітки і латуні." },
	["bloodhound_hydra_snakeskin_brass"] = { name = "Гримуча змія", desc = "Rattler. Виготовлено шляхом поєднання шкіри, зміїної шкури і латуні." },
	["bloodhound_hydra_case_hardened"] = { name = "Зміцнені для футляра", desc = "Case Hardened. Рукавиці зі штучної шкіри з різноманітним металевим узором." },
	["specialist_webs_red"] = { name = "Криваве павутиння", desc = "Crimson Web. Ці червоно-сірі рукавиці мають нанесений гумою павутинний візерунок." },
	["specialist_forest_brown"] = { name = "Картеч", desc = "Buckshot. Практичні рукавиці, що чудово підходять для стрільби із засідки." },
	["specialist_fade"] = { name = "Градієнт", desc = "Fade. Ці кольорові рукавиці мають металеве оздоблення." },
	["specialist_winterhex"] = { name = "Могул", desc = "Mogul. У цих зимових рукавицях ви почуватиметеся на лижному пагорбі наче вдома." },
