	["cu_AK47_Anarchy"] = { name = "Неонова революція", desc = "Neon Revolution. Аквадруком нанесено еклектичну суміш графіті, а потім крапельно пофарбовано.\n\nЖоден загін самогубців не буде повним без цього" },
	["gs_AUG_Syd_Mead"] = { name = "Сід Мід", desc = "Syd Mead. Було спеціально створено відомим художником Сідом Мідом.\n\nУ минулому він дав нам бачення майбутнього; сьогодні він поклав майбутнє в наші руки…" },
	["gs_CZ75_Tread"] = { name = "Відбиток", desc = "Imprint. Ретельно витравлено вручну.\n\nСтильна зброя з Чеської республіки від storm" },
	["aq_Desert_Eagle_Constable"] = { name = "Директива", desc = "Directive. Розроблено, аби допомогти майбутнім правоохоронним силам зупиняти гадів.\n\nВаш хід…" },
	["gs_FAMAS_Rally"] = { name = "Каркас", desc = "Roll Cage. Нанесено відбитки у вигляді забрудненого шасі авто.\n\nБезпека понад усе" },
	["aq_Five_Seven_Scumbria"] = { name = "Скумбрія", desc = "Scumbria. Нанесено стримані лінії.\n\nНе можна лишати його помирати, Феліксе, ти маєш його врятувати — Донька і паладін, частина 1" },
	["gs_G3SG1_Ventilator"] = { name = "Вентилятор", desc = "Ventilator. Пофарбовано в елегантне чорне забарвлення з хромуванням і доповнено особливою гумовою рукояткою.\n\nНе бійся, Наомі, я змушу Фелікса заплатити — Міха Бітон, снайпер «Фенікса»" },
	["cu_Glock18_Weasel"] = { name = "Ласка", desc = "Weasel. Аквадруком нанесено зображення пустотливого самця ласки Боббі. Не зліть його.\n\nВін милий, та це не значить, що він не вкусить" },
	["cu_MAG7_Tribal"] = { name = "Петрогліф", desc = "Petroglyph. Забарвлення імітує наскальний живопис.\n\nКонфлікти існують із часу, коли перша печерна людина взяла у руки камінь — Бут, торговець зброєю" },
	["cu_MP9_Narcis"] = { name = "Повітряний шлюз", desc = "Airlock. Забарвлено в білий колір із яскраво-червоним магазином.\n\nІдеально для стрільби по тостерам" },
	["hy_Negev_Dazzle"] = { name = "Осліплення", desc = "Dazzle. Вкрито наліпками у вигляді синіх і білих смужок зебри.\n\nРадість для любителів воєнної історії" },
	["cu_P90_Grimm"] = { name = "Похмурість", desc = "Grim. Аквадруком нанесено зображення вбивчих привидів, а потім сухим пензлем додано зелені акценти.\n\nІмоґене… Ти продаєш мені зброю чи картинку? — Рона Сабрі, багатообіцяюча зірка" },
	["gs_SCAR20_Powercore"] = { name = "Енергогенератор", desc = "Powercore. Пофарбовано в зелений колір із жовтими акцентами. Додано штрих-код для зручності покупця.\n\nПравило номер один — завоювати серце клієнта" },
	["cu_SG556_Triarch"] = { name = "Тріарх", desc = "Triarch. Ця гвинтівка була виготовлена на замовлення королівської сім’ї та отримала пустельний камуфляж.\n\nНагадай їм про свою любов" },
	["gs_TEC9_Supercharged"] = { name = "Паливний інжектор", desc = "Fuel Injector. Основу розфарбовано яскраво-жовтими фарбами, а на ріжок аквадруком нанесено текстуру механізму. Шкіряна обшивка рукоятки ручної роботи завершує класичний сієтлівський стиль\n\nЕкіпаж механіків до комплекту не входить" },
	["cu_UMP45_Metritera"] = { name = "Інструктаж", desc = "Briefing. Аквадруком нанесено зображення топографічної мапи.\n\nЗустріч буде недовгою…" },
	["hy_XM1014_Fractal_Blue"] = { name = "Повітряний потік", desc = "Slipstream. Забарвлено в димчастий сірий та яскраво-блакитний кольори.\n\nТерпіння, Еліоте, вони ще зіграють свою роль… — Найманець та Змій, частина 2" },
	["am_czv2_mf"] = { name = "Полімер", desc = "Polimer. Наліпка на затворі нагадує формулу полімерів.\n\n«Ти вже не працюєш на Фенікс, ми знаємо, як самим впоратися…» — Донька і паладін, частина 2" },
	["aq_glock_dark-fall"] = { name = "Металева конструкція", desc = "Ironwork. Лазером вигравіювана верба та шипи.\n\n«Якщо за садом не доглядати, то він заросте бур’янами»" },
	["cu_ssg08_dragonfire_scope"] = { name = "Полум’я дракона", desc = "Dragonfire. Аквадруком нанесено водостійке зображення чудовиська-дракона, який дихає полум’ям.\n\n«Сиди на своїх скарбах і очікуй, хто за ними прийде»" },
	["gs_dual_berettas_golden_venice"] = { name = "Королівська пара", desc = "Royal Consorts. Рукоятка пофарбована зеленою перламутровою фарбою, бокові пластини із зображенням грифона та затвор із лазерним гравіюванням. Ця зброя змінює уявлення про елегантність.\n\n«Міняймо обличчя та випускаймо голубів»" },
	["gs_famas_mecha"] = { name = "Меха-індустрія", desc = "Mecha Industries. Контрастний чорно-білий дизайн робить цю зброю схожою на науково-фантастичну.\n\n«Синя фарба для волосся і робот-напарник у комплект не входять»" },
	["gs_final_pooldeadv2"] = { name = "Периста хмара", desc = "Cirrus. Пофарбовано в металево-синій колір та додано зображення черепа.\n\n«Досить літати у хмарах»" },
	["gs_g3sg1_viper_yellow"] = { name = "Жало", desc = "Stinger. Додано жовті та сірі акценти.\n\n«Мить болю. І більше нічого»" },
	["cu_galil_ar-camo"] = { name = "Чорний пісок", desc = "Black Sand. Пофарбовано в чорний камуфляж.\n\n«Міха Бетон збирається виступити проти ССО коаліції»" },
	["aa_hide-mp9"] = { name = "Осад", desc = "Sand Scale. Пофарбовано в синій та брунатний колір.\n\n«Нам слід підготуватися до відплати» — командир Фелікс Райлі" },
	["cu_m4a1_flashback"] = { name = "Флешбек", desc = "Flashback. Вручну нанесено зображення, яке показує двоїсту природу людини.\n\nЗа джокера в кожному з нас" },
	["gs_m4a4_sector"] = { name = "Кайфолом", desc = "Buzz Kill. Ця зброя від «Sektor Industry» була охайно пофарбована в жовтий та зелений колір.\n\nЗіпсує будь-яку вечірку" },
	["am_mag7_malform"] = { name = "Сонар", desc = "Sonar. Через трафарет нанесено бірюзовий візерунок.\n\n«Ви праві, програш — це новий досвід для мене» — Демон та Валькірія, частина 2" },
	["am_nova_sand"] = { name = "Жилатьє", desc = "Gila. Нанесено візерунок у формі шкіри ящірки.\n\n«Ти поводишся так, ніби найманці раніше не ловили тебе» — Демон і Валькірія, частина 1" },
	["gs_p2000-sport"] = { name = "Дерен", desc = "Turf. Рукоятка та затвор пофарбовані металевою фарбою.\n\n«З іншого боку трава завжди червоніша»" },
	["gs_p90_shallow_grave"] = { name = "Неглибока могила", desc = "Shallow Grave. Рукоятки пофарбовані так, щоби нагадувати рунні камені, а на прикладі аквадруком нанесено текстуру скелета.\n\n«За такої кількості трупів копати ніколи»" },
	["cu_usp_cyrex"] = { name = "Сайрекс", desc = "Cyrex. Червоні акценти надають зброї футуристичний вигляд.\n\n«Імоґен був попереднім Феніксом, а я є наступним» — Гакслі, «Змагання»" },
	["cu_wp_sawedoff"] = { name = "Принцеса пустоші", desc = "Wasteland Princess. Хтось позначає кількість убивств на стволі.\n\n«Чудовий дарунок для соціопата, у якого все є…»" },
	["gs_ak47_bloodsport"] = { name = "Кривавий спорт", desc = "Bloodsport. Аквадруком нанесено червоно-чорне зображення, вкрите білими наклейками.\n\nПотрібен другий водій" },
	["cu_awp_psychopath"] = { name = "Лихоманковий сон", desc = "Fever Dream. Нанесено рожевий, синій та пурпуровий малюнок на чорну основу.\n\nМарення — небезпечна річ" },
	["hy_bizon_torn_green"] = { name = "Атмосфера джунглів", desc = "Jungle Slipstream. Аквадруком нанесено зелено-жовте зображення.\n\nІдіть за лідером" },
	["cu_blueprint_scar"] = { name = "Креслення", desc = "Blueprint. Розмальовано вручну із застосуванням синьої теми, що нагадує креслення проектувальника.\n\nНайкращі плани малюються в масштабі" },
	["gs_cz_snakes_purple"] = { name = "Сянь-лю", desc = "Xiangliu. Користувацьке розфарбовування із використанням дев’яти фіолетових та золотих змій.\n\nБійтеся хвилі, що підіймається" },
	["gs_m4a1_decimator"] = { name = "Винищувач", desc = "Decimator. Нанесено нестандартний малюнок із яскравими блакитними та рожевими висвітленнями\n\nДосить ретрофутуристично" },
	["cu_desert_eagle_corroden"] = { name = "Спалах оксиду", desc = "Oxide Blaze. Нанесено нестандартний малюнок із заіржавілою кольоровою гамою та білим забарвленням деталей.\n\nНічого такого, що не виправить наждачний папір" },
	["cu_fiveseven_vein"] = { name = "Капіляр", desc = "Capillary. Розмальовано химерним переплетінням вен коралового та зеленувато-блакитного кольорів.\n\n20/100" },
	["sp_galil_wave"] = { name = "Багряне цунамі", desc = "Crimson Tsunami. На багряну основу нанесено сині хвилі.\n\nПрориваємося на захід" },
	["sp_m249_frog_original"] = { name = "Смарагдовий отруйний дротик", desc = "Emerald Poison Dart. Аквадруком нанесено малюнок, натхненний забарвленням жаби-дереволаза.\n\nРозмови нині не в пріоритеті" },
	["sp_mp7_tribal_yellow"] = { name = "Акобен", desc = "Akoben. На жовту основу нанесено блакитний візерунок.\n\nЦе ваш власний бойовий клич" },
	["am_p250_sputnik"] = { name = "Пульсація", desc = "Ripple. На чорну основу нанесені анодовані синьо-сірі візерунки.\n\nСтаньте галькою" },
	["aa_mac10_the_last_dive"] = { name = "Останнє занурення", desc = "Last Dive. Нанесено різні зелені та помаранчеві візерунки.\n\nУ хаосі криється краса" },
	["gs_ump_abyss"] = { name = "Риштування", desc = "Scaffold. Намальований вручну розпис забезпечує переконливий ефект глибини.\n\nЦеглину за цеглиною" },
	["cu_usps_noir"] = { name = "Нео-нуар", desc = "Neo-Noir. На сіре тло нанесене стилізоване синьо-пурпурове зображення дівчини.\n\n«Вона лежить перед гнітючим хмарочосом, оповита неоновим світлом»" },
	["aq_xm_leaf_fade"] = { name = "Сезони", desc = "Seasons. Візерунок зображує багатокольорове листя у різні пори року.\n\nНавесні, восени, влітку та іноді взимку" },
	["aq_sawedoff_zander2"] = { name = "Судак", desc = "Zander. Розмальовано на честь популярної риби, на яку полюють у рибальських турнірах.\n\nЗалиште одну собі" },
	["hy_p90_barebones_blue"] = { name = "Смертельна хватка", desc = "Death Grip. Аквадруком нанесено текстуру продубльованих рук, застосованих до синьої основи.\n\nНе ходіть тихо" },
	["cu_usps_blueprint"] = { name = "Креслення", desc = "Blueprint. Зброю було вручну розфарбовано синьою темою, яка нагадує архітекторське креслення.\n\nІноді найкращі плани йдуть шкереберть" },
	["gs_ak_colony01_red"] = { name = "Орбіта Mk01", desc = "Orbit Mk01. Вручну розмальовано червоним та чорним.\n\nВідбито у COLONY01" },
	["gs_dualberettas_cobra"] = { name = "Атака кобри", desc = "Cobra Strike. На кожному руків’ї вручну намальовано кобру. Кожух затвору демонструє їхню неонову отруту.\n\nВони завжди дають здачі" },
	["sp_famas_macabre"] = { name = "Похмурий", desc = "Macabre. Зброю розфарбовано у зеленій тематиці поверх жовтої ґрунтовки.\n\nЖахливе відкриття" },
	["cu_fiveseven_hyperbeast"] = { name = "Гіперчудовисько", desc = "Hyper Beast. Нанесено зображення брутальної істоти у психоделічних кольорах.\n\nУсі ми — монстри" },
	["cu_galil_candychaos"] = { name = "Цукролізація", desc = "Sugar Rush. Нанесено зображення із використанням кольорів на тему цукрової вати і різних шаблонів.\n\nГотуйтеся до аварії" },
	["cu_awp_hannya"] = { name = "Оні Тайджі", desc = "Oni Taiji. Вручну нанесено яскраве зображення самурая та Оні.\n\nЗустріньтеся зі своїми демонами" },
	["cu_m4a1s_metritera"] = { name = "Інструктаж", desc = "Briefing. Аквадруком нанесено зображення топографічної мапи.\n\nУ всіх є план…" },
	["cu_m4a4_hellfire"] = { name = "Пекельний вогонь", desc = "Hellfire. Вручну намальоване диявольське створіння у вогні та ланцюгах.\n\nІ запахло сіркою" },
	["am_mac10_aloha"] = { name = "Алоха", desc = "Aloha. Текстура червоного гібіскуса була нанесена з допомогою аерографа.\n\nДля неврахованих" },
	["am_mag7_caustic"] = { name = "Жорстка вода", desc = "Hard Water. Нанесено сірий анодований візерунок, а потім домальовано сині акценти.\n\n«Я це не питиму»" },
	["aq_ump45_flameflower"] = { name = "Залізні квіти", desc = "Metal Flowers. Нанесено візерунок у вигляді поховальних троянд сталевого кольору.\n\nНа пам’ять" },
	["cu_p2000_hunter"] = { name = "Лісник", desc = "Woodsman. Вручну нанесено нестандартний зелений камуфляж із помаранчевими деталями.\n\n«Помаранчевий — лише для вас»" },
	["cu_p250_axiom"] = { name = "Червоний камінь", desc = "Red Rock. Застосовано нестандартний камуфляж DDPAT із жовтими та помаранчевими відтінками.\n\nЗа горизонтом" },
	["cu_ssg08_deathshead"] = { name = "Голова смерті", desc = "Death's Head. Вручну нанесено зображення метелика, якого називають «Голова смерті».\n\nQuid pro quo" },
	["aq_tec9_chalk_pattern"] = { name = "Вирізка", desc = "Cut Out. Застосовано візерунок із метеликів та квітчастих деталей.\n\nВесна розквітає" },
	["aq_sawed-off_flower"] = { name = "Морріс", desc = "Morris. Для намалювання орнаменту з квітів використовувалася тьмяна кольорова гама.\n\nМайже модерново" },
	["cu_aug_orange_triangle"] = { name = "Триква", desc = "Triqua. На вигорілу помаранчеву основу нанесено металеві трикутники та карбонові волокна.\n\nУсі довкола рівні" },
	["gs_ak47_empress"] = { name = "Імператриця", desc = "The Empress. На створення цього малюнка надихнула карта Таро «Імператриця».\n\nБагатство має багато форм" },
	["cu_bizon_all_in"] = { name = "Азартний гравець", desc = "High Roller. На червону основу нанесені зображення гральних кісток, жетонів та комбінації «Роял-флеш».\n\nСтавлю все" },
	["gs_g3sg1_cetme_redux"] = { name = "Мисливець", desc = "Hunter. На чорну основу рушниці нанесено темні дерев’яні панелі.\n\nПахне червоним деревом" },
	["cu_p250_cybercroc"] = { name = "Ще побачимось", desc = "See Ya Later. Нанесено зображення механічного зеленого крокодила із жовтими деталями.\n\n«А тим часом крокодил…»" },
	["cu_mp9_goo"] = { name = "Смола", desc = "Goo. На сіру основу нанесено схожий на липкий слиз малюнок.\n\nРозмальовано з огидою" },
	["cu_glock_indigo"] = { name = "Неземний", desc = "Off World. Нанесено кілька шарів блакитної фарби.\n\nВерхній шар ще сохне" },
	["gs_m4a1_shatter"] = { name = "Кришталь", desc = "Leaded Glass. Малюнок зображує розбите синє та помаранчеве скло.\n\nСмертельна крихкість" },
	["am_mac10_oceani"] = { name = "Океанічний", desc = "Oceanic. Нанесено чорну основу, а потім синє зображення.\n\nБезформна порожнеча" },
	["gs_r8_llamacannon"] = { name = "Ламогармата", desc = "Liama Cannon. Руків’я кольору слонової кістки оздоблене орнаментом, а розфарбовування ствола виконано у металевому синьому стилі.\n\nПотягніть важіль" },
	["cu_tec9_cracked_opal"] = { name = "Тріснутий опал", desc = "Cracked Opal. Розфарбовано у вигляді тріснутої старовинної деревини зі вставками опала.\n\nМожна використовувати як перстень настрою" },
	["hy_scar20_jungle_slipstream"] = { name = "Вихор джунглів", desc = "Jungle Slipstream. Вручну пофарбовано зеленим та жовтим аквадруком.\n\nЗалиште свій слід" },
	["gs_sg553_phantom"] = { name = "Фантом", desc = "Phantom. Вручну пофарбовано у синьо-сірій кольоровій гамі із додаванням помаранчевих вкраплень.\n\nЗакупівля SkyTech" },
	["gs_cz75_tacticat"] = { name = "Тактикіт", desc = "Tacticat. Вручну пофарбовано у рожевий та пурпуровий, а також додані невеличкі котячі відмітні знаки.\n\n24 кулі, 9 життів" },
	["cu_ump45_x-ray_machine"] = { name = "Експозиція", desc = "Exposure. Нанесено зображення, що зображує рентгенівський знімок: грудна клітка, ребра і все таке.\n\nБудь ласка, дістаньте з кишень металеві предмети" },
	["aq_xm1014_ziggy_anarchy"] = { name = "Зіґґі", desc = "Ziggy. Нанесено візерунок із відтінків жовто-зеленого, а також пурпурового і срібного кольорів.\n\nПривнесімо трішки анархії" },
	["gs_aug_stymphalian_birds"] = { name = "Стімфалійський птах", desc = "Stymphalian. Нанесено зображення, що зображує стімфалійських птахів із грецької міфології.\n\nАртеміда шле привіт" },
	["gs_awp_death"] = { name = "Мортіс", desc = "Mortis. На створення цього малюнка надихнула карта Таро «Смерть».\n\nБудь-який кінець — це новий початок" },
	["cu_bizon_riot"] = { name = "Нічний бунт", desc = "Night Riot. Нанесено візерунок блакитного камуфляжу.\n\nВикличе сум’яття" },
	["gs_fiveseven_hot_rod_violet"] = { name = "Перевірка полум’ям", desc = "Flame Test. На пурпуровому стволі зброї нанесене срібне полум’я, а завершує картину металеве руків’я.\n\nВиявлено елемент: цезій" },
	["aa_glock_18_urban_moon_fever"] = { name = "Схід місяця", desc = "Moonrise. Над силуетом міста вимальовується чудовий краєвид повного місця та пурпурового нічного неба.\n\nШукайте Полярну зорю" },
	["cu_m4a4_neo_noir"] = { name = "Нео-нуар", desc = "Neo-Noir. На сіре тло нанесене стилізоване синьо-пурпурове зображення дівчини.\n\n«Зібравши все, що могла, і зникла в антиутопії розпаду»" },
	["gs_mp7_bloodsport"] = { name = "Кривавий спорт", desc = "Bloodsport. Аквадруком нанесено червоно-чорне зображення, вкрите білими наклейками.\n\nЗдається в оренду" },
	["cu_mp9_black_sand"] = { name = "Чорний пісок", desc = "Black Sand. Для розфарбовування використано піщано-чорний камуфляжний візерунок.\n\n«Завершіть їхню кампанію до її початку»" },
	["sp_negev_lionfish"] = { name = "Крилатка", desc = "Lionfish. Зброю було розфарбовано випадковою текстурою крилатки.\n\nПросто пливи далі" },
	["gs_nova_anchorite"] = { name = "Дика шістка", desc = "Wild Six. Використано дизайн пост-апокаліптичного світу.\n\nМоя рушниця — мої правила" },
	["cu_p2000_urban_hazard"] = { name = "Міська загроза", desc = "Urban Hazard. Нанесено зображення із відтінком сірого кольору в червоними штрихами.\n\nДалі необхідні каски" },
	["gs_revolver_tread"] = { name = "Хват", desc = "Grip. Метал був ретельно спресований, а ручка вкрита візерунчастою синьою гумою.\n\nКожен ворог — це цвях" },
	["sp_sg533_aloha"] = { name = "Алоха", desc = "Aloha. Зброю було розфарбовано текстурою синього гібіскусу.\n\nІдеальний партнер" },
	["aq_mag7_swag7"] = { name = "ІЛIТА-7", desc = "SWAG-7. Під час розфарбовування зброї використана монохромна срібно-чорна тема.\n\nАбо це є в тобі, або ні" },
	["cu_ump45_white_fang"] = { name = "Арктичний вовк", desc = "Arctic Wolf. Нанесено зображення, що відтворює білу шерсть арктичного вовка.\n\nКожен раунд — це 1 проти 5" },
	["cu_usp_cut"] = { name = "Кортекс", desc = "Cortex. Нанесено зображення із рожевими вставками на срібно-чорній основі.\n\nВище голову" },
	["cu_xm1014_oxide_blaze"] = { name = "Спалах оксиду", desc = "Oxide Blaze. Нанесено нестандартний малюнок із заіржавілою кольоровою гамою та білим забарвленням деталей.\n\nНе варто використовувати як основну зброю" },
	["cu_ak_neon_rider"] = { name = "Неоновий гонщик", desc = "Neon Rider. Смертоносний байкер зображений на тлі різних викривлених неонових візерунків.\n\nНа дві третини Trapper Keeper і на третину синтезатор" },
	["hy_aug_torn_orange"] = { name = "Бурштиновий потік", desc = "Amber Slipstream. Аквадруком нанесено димні сірі та пружні помаранчеві візерунки.\n\nБудь ласка, тримайте руки на кермі" },
	["cu_cz75_eco"] = { name = "Еко", desc = "Малюнок із термоналіпок, нанесених на білу та зелену основу.\n\nЗаради кращого майбутнього" },
	["sp_elites_winter_raider"] = { name = "Шмаття", desc = "Shred. Зброю було розфарбовано біло-блакитними смугами на чорній основі.\n\nІнколи грубість шматує вас" },
	["gs_deagle_aggressor"] = { name = "Червоний код", desc = "Code Red. Застосовано нестандартне розфарбування з червоною основою та білими деталями.\n\nПо місцям" },
	["gs_g3sg1_buccaneer"] = { name = "Відкрите море", desc = "High Seas. Нанесено зображення на старовинну навігаційну тематику із декоративними латунними вставками.\n\nНехай лічба померлих проведе вас" },
	["gs_glock_thunder_dust"] = { name = "Яструб", desc = "Warhawk. Розфарбування нагадує воєнні літаки минулої ери.\n\nХоч і не найкращий літун, але найдоступніший" },
	["cu_m4a1s_nightmare"] = { name = "Кошмар", desc = "Nightmare. Нестандартне розфарбування, на якому зображений моторошний нічний гість.\n\nНаступного разу заводьте будильник" },
	["cu_mp9_vein"] = { name = "Капіляр", desc = "Capillary. Розмальовано химерним переплетінням вен коралового та зеленувато-блакитного кольорів.\n\n857 ударів за хвилину" },
	["cu_nova_toy_soldier"] = { name = "Іграшковий солдатик", desc = "Toy Soldier. Вручну нанесено малюнок зі знайомим відтінком зеленого кольору, щоби викликати спогади з дитинства.\n\nПігулки та солдати постачаються оптом" },
	["gs_p90_tread"] = { name = "Тяга", desc = "Traction. Розмальовано синіми шестикутниками, що були спроектовані так, аби покрити унікальну форму P90.\n\nТримати зброю — то вже половина перемоги. Інша — поцілити у ворога" },
	["am_awp_pawpaw"] = { name = "ЛАПА", desc = "PAW. Нанесено текстуру із зображенням сімейства котячих, службових собак та створіння з червоним шаликом.\n\nНагороди за вбивства виплачуються подряпинами та тенісними м’ячиками" },
	["gs_powercore_mp7"] = { name = "Енергогенератор", desc = "Powercore. Нанесено нестандартний малюнок із зеленим фоном та жовтими вставками.\n\nПравило номер 2 – ніколи не конфліктуй при всіх" },
	["cu_sawedoff_devourer"] = { name = "Пожирач", desc = "Devourer. Нанесений вручну малюнок нагадує істоту, що їсть слова.\n\nРозмовляти з їжею у роті – прояв невихованості" },
	["cu_r8_survivalist"] = { name = "Виживальник", desc = "Survivalist. Намальовані вручну зелені смуги дозволяють власнику змішуватися з навколишнім середовищем і уникати виявлення в напружених ситуаціях. \n\n«Вісім пострілів і декілька ворогів; краще все порахувати»" },
	["cu_tec9_snake"] = { name = "Змія-9", desc = "Snek-9. Натхненний кораловими зміями нестандартний спосіб фарбування.\n\n«Поєднання червоного та жовтого означає, що ти труп»" },
	["cu_famas_owl_orange"] = { name = "Око Афіни", desc = "Eye of Athena. Намальоване вручну зображення механізованої сови, що сидить на гнізді з помаранчевого та коричневого пір’я.\n\n+1 до мудрості" },
	["cu_ak_island_floral"] = { name = "Дикий лотос", desc = "Wild lotus. Нанесено нестандартний малюнок помаранчевого лотоса на зеленому тлі.\n\nВсе або нічого" },
	["hy_bloom_red"] = { name = "Денна лілія", desc = "Day Lily. Аквадруком нанесено текстуру червоних лілій.\n\nКрасиві лілії найчастіше асоціюються зі смертю" },
	["sp_bloom_orange"] = { name = "Вечірня лілія", desc = "Sunset Lily. Аерозольним балончиком нанесено текстуру помаранчевих лілій.\n\nКрасиві лілії найчастіше асоціюються зі смертю" },
	["am_bloom_blue"] = { name = "Опівнічна лілія", desc = "Midnight Lily. Нанесено перламутрову текстуру лілій, яка змінює колір.\n\nКрасиві лілії найчастіше асоціюються зі смертю" },
	["sp_bud_green"] = { name = "Бірюзовий цвіт", desc = "Teal Blossom. Аерозольним балончиком нанесено візерунок трояндового бутона в бірюзових, зелених і помаранчевих відтінках.\n\nНе буває троянд без шипів" },
	["hy_bud_red"] = { name = "Багряний цвіт", desc = "Crimson Blossom. Аквадруком нанесено візерунок троянди винного кольору.\n\nНе буває троянд без шипів" },
	["sp_bud_blue"] = { name = "Темний квіт", desc = "Dark Blossom. Аерозольним балончиком нанесено візерунок блакитного бутона троянди.\n\nНе буває троянд без шипів" },
	["hy_leaf_green"] = { name = "Бананове листя", desc = "Banana Leaf. Аквадруком нанесено зелено-жовту текстуру продубльованого листя.\n\nКров на листі" },
	["hy_leaf_blue"] = { name = "Штучне листя", desc = "Synth Leaf. Аквадруком нанесено синьо-пурпурову текстуру продубльованого листя.\n\nКров на листі" },
	["sp_leaf_orange"] = { name = "Іржаве листя", desc = "Rust Leaf. Аерозольним балончиком нанесено помаранчеву текстуру продубльованого листя.\n\nКров на листі" },
	["cu_mp9_island_floral"] = { name = "Дика лілія", desc = "Wild lily. Нанесено нестандартний малюнок блакитної лілії на пурпуровому тлі.\n\nЗупинись і нюхни кордиту" },
	["hy_flowers_stmarc"] = { name = "Морський ситець", desc = "Sea Calico. На темно-бірюзову основу нанесено жовтий квітковий візерунок.\n\nМертвим квіти не потрібні" },
	["sp_palm_sunset"] = { name = "Захід сонця", desc = "Sundown. На синьо-рожевий камуфляж аерозольною фарбою нанесено гілки пурпурової пальми.\n\nЕ С Т Е Т И К А" },
	["sp_palm_green"] = { name = "Джунглеві хащі", desc = "Jungle Thicket. На камуфляж джунглів аерозольною фарбою нанесено гілки темно-зеленої пальми.\n\nЗ Aztec із любов’ю" },
	["sp_twigs_beach"] = { name = "Прибій", desc = "Surfwood. Аерозольною фарбою нанесено візерунок у блідо-зелених і кремових відтінках.\n\nМ’ятна насолода" },
	["hy_bamboo_stmarc"] = { name = "Бамбуковий сад", desc = "Bamboo Garden. Аквадруком нанесено візерунок паростків бамбука, намальованих пензлем.\n\nТут має бути жарт про паростки" },
	["gs_awp_enamel"] = { name = "Принц", desc = "The Prince. Нанесено нестандартний малюнок із червоною основою і золотою філігранню.\n\nМир вам" },
	["gs_mag7_glass"] = { name = "Чинкуеда", desc = "Cinquedea. Нанесено нестандартний малюнок зі скляних панелей із візерунком, червоного руків’я і цівки.\n\n«Скільки б ви не заплатили, та цього все одно буде замало»" },
	["hy_murano_orange"] = { name = "Помаранчеве Мурано", desc = "Orange Murano. Аквадруком нанесено візерунок із помаранчевого венеціанського бісеру.\n\nСмерть від тисячі бісеринок" },
	["am_murano_violet"] = { name = "Фіолетове Мурано", desc = "Violet Murano. Нанесено візерунок із помаранчевого і синього венеціанського бісеру.\n\nСмерть від тисячі бісеринок" },
	["hy_murano_blue"] = { name = "Синє Мурано", desc = "Navy Murano. Аквадруком нанесено візерунок із синього венеціанського бісеру.\n\nСмерть від тисячі бісеринок" },
	["am_ren_dark"] = { name = "Темна філігрань", desc = "Dark Filigree. Аквадруком нанесено темно-червоний візерунок на вугільно-темній основі.\n\nМожливо, цей вид мистецтва варто лишити в минулому" },
	["hy_ren_orange"] = { name = "Помаранчева філігрань", desc = "Orange Filigree. Аквадруком нанесено помаранчевий візерунок проса.\n\nМожливо, цей вид мистецтва варто лишити в минулому" },
	["am_ren_red"] = { name = "Червона філігрань", desc = "Red Filigree. Аквадруком нанесено червоний візерунок проса.\n\nМожливо, цей вид мистецтва варто лишити в минулому" },
	["am_veneto_red"] = { name = "Червоне бароко", desc = "Baroque Red. Нанесено витончений бордово-сірий візерунок в італійському стилі.\n\nОстанню вечерю подано" },
	["hy_veneto_purple"] = { name = "Пурпурове бароко", desc = "Baroque Purple. Нанесено витончений візерунок в італійському стилі.\n\nОстанню вечерю подано" },
	["hy_veneto_tan"] = { name = "Бароковий пісок", desc = "Baroque Sand. Нанесено витончений візерунок в італійському стилі.\n\nОстанню вечерю подано" },
	["am_veneto2"] = { name = "Помаранчеве бароко", desc = "Baroque Orange. Нанесено витончений червоно-золотий візерунок в італійському стилі.\n\nОстанню вечерю подано" },
	["am_stained_glass"] = { name = "Вітраж", desc = "Stained Glass. Нанесено зображення, яке нагадує синьо-червоний вітраж.\n\nРозбили — купили" },
	["hy_canals_tile"] = { name = "Кам’яна мозаїка", desc = "Stone Mosaico. Аквадруком нанесено візерунок із тріснутої коричневої плитки.\n\nДивись, куди йдеш" },
	["sp_spray_water"] = { name = "Водяні бризки", desc = "Canal Spray. Аерозольною фарбою нанесено невеликі діагональні сині штрихи.\n\nМи все одно складаємося з води…" },
	["cu_dual_elites_rally"] = { name = "Подвійне турбо", desc = "Twin Turbo. Застосоване нестандартне розфарбування на честь італійської гоночної машини.\n\nЗавжди недооцінена" },
	["gs_mac10_checker"] = { name = "Теляча шкіра", desc = "Calf Skin. Вручну нанесено візерунок коричневої шахівниці з білими акцентами.\n\nВключено в преміальний набір за 1050 $" },
	["gs_p250_checker"] = { name = "Першокласне вино", desc = "Vino Primo. Вручну нанесено візерунок пурпурової шахівниці.\n\nВаше найкраще червоне" },
	["cu_sg553_rally"] = { name = "Інтеграл", desc = "Integrale. Застосоване нестандартне розфарбування на честь італійської гоночної машини.\n\n«Аццендете і моторі!»" },
	["gs_ssg08_checker"] = { name = "Ручне гальмо", desc = "Hand Brake. Вручну нанесено візерунок біло-блакитної шахівниці.\n\nПризначено для повороту, а не паркування" },
	["aa_fade_mp7"] = { name = "Градієнт", desc = "Fade. Аерографічний малюнок нанесено прозорою фарбою на хромову основу.\n\n«Це не просто зброя, це жанрова картина» — Імоґен, продавець зброї" },
	["sp_tape_short_rally"] = { name = "Брудне випадіння", desc = "Dirt Drop. Аерозольним балончиком нанесено чорні смужки на темну основу.\n\nБруд не зашкодить… доки не зашкодить" },
	["hy_splatter3"] = { name = "Ковзання", desc = "Slide. Аквадруком нанесено текстуру помаранчевих і коричневих смуг.\n\nКому потрібна болотяна варта?" },
	["hy_red_hex"] = { name = "Конвертер", desc = "Converter. Аквадруком нанесено чорно-червону сітку.\n\nНе так уже й боляче…" },
	["sp_mesh_safetyblack"] = { name = "Прибиральник", desc = "Sweeper. Аерозольною фарбою нанесено коричневу, сіру і червону текстуру сітки.\n\nОчистіть шлях" },
	["hy_mesh_safetyorange"] = { name = "Захисна сітка", desc = "Safety Net. Помаранчева текстура сітки нанесена на білу основу.\n\nВаше спорядження; тепер із високою видимістю" },
	["sp_mesh_safetyred"] = { name = "Несправність", desc = "Check Engine. Аерозольною фарбою нанесено текстуру червоної сітки.\n\n«На це варто поглянути…»" },
	["aa_vertigo_red"] = { name = "Стоп-сигнал", desc = "Brake Light. На рукоятку нанесено червоно-білу спіральну текстуру.\n\nТримайтеся правого боку" },
	["aa_vertigo_blue"] = { name = "Дальнє світло", desc = "High Beam. На поверхню нанесено синю текстуру спіралі.\n\nВажко промахнутися" },
	["gs_awp_gungnir"] = { name = "Ґунґнір", desc = "Gungnir. Блакитними й світло-молочними кольорами нанесено нестандартний малюнок, що уособлює спис Одіна.\n\nСправжня зброя для Всебатька" },
	["am_jorm_green"] = { name = "Смарагдовий Йормунганд", desc = "Emerald Jörmungandr. Нанесено зелений візерунок, що уособлює Йормунганда, Великого Змія.\n\nПробудження богів" },
	["am_jorm_orange"] = { name = "Полум’яний Йормунганд", desc = "Flame Jörmungandr. Нанесено помаранчевий візерунок, що уособлює Йормунганда, Великого Змія.\n\nПробудження богів" },
	["am_jorm_blue"] = { name = "Астральний Йормунганд", desc = "Astral Jörmungandr. Нанесено срібно-блакитний візерунок, що уособлює Йормунганда, Великого Змія.\n\nПробудження богів" },
	["am_knots_silver"] = { name = "Льодяний Борре", desc = "Frost Borre. Нанесено візерунок зі срібних кілець, натхненний германо-скандинавською міфологією.\n\nНе всі хмари дарують дощ" },
	["am_knots_brown"] = { name = "Мідний Борре", desc = "Copper Borre. Нанесено візерунок із золотих кілець, натхненний германо-скандинавською міфологією.\n\nМи закінчуємо там, звідки починаємо — в землі" },
	["gs_negev_thor"] = { name = "Мйольнір", desc = "Mjölnir. Нанесено візерунок зі скандинавських рун на червоній основі.\n\n«У всьому винен Локі…»" },
	["am_crystallized_green"] = { name = "Смарагдовий кварц", desc = "Emerald Quartz. Розфарбовано зеленою металевою фарбою, для створення візерунка використано кристалічну маску.\n\nУсі хочуть справити враження" },
	["hy_gelpen_dark"] = { name = "Похоронне вогнище", desc = "Pyre. Аквадруком на іржаву основу нанесено візерунок гелевою ручкою.\n\nШануйте мертвих своїх" },
	["sp_asgard_wall"] = { name = "Барикада", desc = "Asgard. Аерозольним балончиком нанесено малюнок ізометричного лабіринту.\n\nЗадумана безнадія" },
	["am_crystallized_dark_green"] = { name = "Моховий кварц", desc = "Moss Quartz. Розфарбовано темно-зеленою металевою фарбою, для створення візерунка використано кристалічну маску.\n\nУсі хочуть справити враження" },
	["sp_knots_blue"] = { name = "Нічний Борре", desc = "Blue knots. Аерозольним балончиком нанесено малюнок, навіяний північним орнаментом переплетених синіх кілець.\n\n«Краще боротися й загинути, ніж жити у відчаї»" },
	["so_rune_stone"] = { name = "Червоний камінь", desc = "Red Stone. Руків’я розфарбовано в оливковий, а бік — у бордовий.\n\nШкода, що з часом написи стерлися…" },
	["hy_blueprint_white"] = { name = "Ескіз об’єкта", desc = "Facility Sketch. Аквадруком нанесено сіру текстуру, що нагадує атомну електростанцію.\n\n«Ваша точка входу — це вентиляційна система»" },
	["hy_blueprint_aqua"] = { name = "Об’єкт у негативі", desc = "Facility Negative. Аквадруком нанесено синьо-жовту текстуру, що нагадує атомну електростанцію.\n\nПротилежність стримувальної споруди" },
	["hy_blueprint_red"] = { name = "Чернетка об’єкта", desc = "Facility Draft. Аквадруком нанесено червону текстуру, що нагадує атомну електростанцію.\n\nСтворюючи енергію з атомів" },
	["hy_blueprint_bluered"] = { name = "Фабрична пітьма", desc = "Facility Dark. Аквадруком нанесено синю текстуру, що нагадує атомну електростанцію.\n\nВимкніть світло" },
	["am_circuitboard_silver"] = { name = "Мейнфрейм", desc = "Mainframe. Нанесено чорно-білу текстуру мікросхеми.\n\nУ центрі всього цього" },
	["am_circuitboard_aqua"] = { name = "Співпроцесор", desc = "Co-Processor. Нанесено синю текстуру мікросхеми.\n\nБеззвучне доповнення" },
	["am_circuitboard_green"] = { name = "Материнська плата", desc = "Motherboard. Нанесено зелено-білу текстуру мікросхеми.\n\nНе розривайте підключення" },
	["am_circuitboard_orange"] = { name = "Випадковий доступ", desc = "Random Access. Нанесено червону текстуру мікросхеми на темну основу.\n\nМандрівка є випадковістю, а от результат — ні" },
	["hy_ducts_yellow"] = { name = "Перемичка", desc = "Bulkhead. Аквадруком нанесено текстуру переплетених труб.\n\nТримайте міцно" },
	["hy_ducts_green"] = { name = "Охолоджувач", desc = "Coolant. Аквадруком нанесено на дуло текстуру переплетених труб.\n\nПереносний пристрій відведення спеки" },
	["hy_ducts_grey"] = { name = "Осердя", desc = "Mandrel. Аквадруком нанесено зображення переплетених труб.\n\nЗаокруглюйте кожним пострілом" },
	["hy_ducts_blue"] = { name = "Обмінник", desc = "Exchanger. Аквадруком нанесено текстуру переплетених труб.\n\n«Моє життя — за ваші»" },
	["hy_nuclear_hotorange"] = { name = "Витік реактора", desc = "Core Breach. Помаранчева текстура радіоактивних символів нанесена на чорну основу.\n\nПротоколи безпеки не спрацювали" },
	["hy_nuclear_skulls_redblue"] = { name = "Ахерон", desc = "Acheron. Аквадруком нанесено різнокольорову текстуру.\n\nЗброя горя" },
	["am_nuclear_skulls_green"] = { name = "Ядерний сад", desc = "Nuclear Garden. На дуло нанесено токсичну неоново-зелену текстуру.\n\nПродукт радіоактивного розпаду" },
	["hy_nuclear_skulls_aqua"] = { name = "Холодний сплав", desc = "Cold Fusion. Аквадруком нанесено токсичну блідо-синю текстуру.\n\nГіпотезу не було підтверджено" },
	["gs_tec9_envoy"] = { name = "Віддалене керування", desc = "Remote Control. Вручну нанесено зображення кольорового пульту і перемикачів на синю основу.\n\nНе торкайтеся цих кнопок" },
	["gs_m4a1s_operator"] = { name = "Панель керування", desc = "Remote Control. Вручну нанесено зображення кольорового пульту і перемикачів на синю основу.\n\n«Це захищена лінія?»" },
	["hy_labrat_mp5"] = { name = "Лабораторні щури", desc = "Lab Rats. Аквадруком нанесено текстуру, на якій зображено злість щурів.\n\n—ВИДАЛЕНО—" },
	["cu_ak47_asiimov"] = { name = "Азімов", desc = "Asiimov. Нанесено зображення у науково-фантастичному стилі.\n\nБудь-хто може передбачити майбутнє… але лише мрійник здійснює його" },
	["cu_ump_arrows"] = { name = "Імпульс", desc = "Momentum. Нанесено зображення із яскравими кольорами та стрілками, які напрямлені вперед.\n\nЗавжди рухайтеся вперед" },
	["cu_awp_neonoir"] = { name = "Нео-нуар", desc = "Neo-Noir. На сіре тло нанесене стилізоване синьо-пурпурове зображення дівчини.\n\n«Вони втішалися розпачем один одного»" },
	["gs_mp9_colony01"] = { name = "Скромна загроза", desc = "Modest Threat. Нанесено вручну малюнок у помаранчевому та чорному кольорах.\n\nБудьте останнім уцілілим" },
	["gs_deagle_mecha"] = { name = "Меха-індустрія", desc = "Mecha Industries. Чорно-білий дизайн передає чистоту та футуристичність.\n\nТа сама технологія відтепер доступна у меншому форматі" },
	["gs_g3sg1_savage"] = { name = "Cміттяр", desc = "Scavenger. Спочатку зелені частини зброї були замінені вуличними знаками та банкою з-під помідорів.\n\nЧому біжите?" },
	["hy_galil_signal_red"] = { name = "Сигнал", desc = "Signal. Аквадруком нанесено текстуру червоних шестикутників поверх темно-синьої основи.\n\nІнформація не буває вільною" },
	["cu_glock18_corroden"] = { name = "Оксидне полум’я", desc = "Oxide Blaze. Нанесено нестандартний малюнок із заіржавілою кольоровою основою та білими деталями.\n\n«Суб’єкт стверджує, що це винесло на берег»" },
	["gs_nova_hunter_brute"] = { name = "Обпалене дерево", desc = "Wood Fired. Дерев’яна основа була пофарбована в зелений колір, додані сині акценти, а потім її обпалили.\n\nНа полюванні" },
	["gs_mp5_festival_drip"] = { name = "Фосфор", desc = "Phosphor. Нанесено яскраво-рожеві, зелені та синьо-зелені плями на темну основу.\n\nТримайтеся подалі від натовпу" },
	["gs_m4a4_chopper_ghost"] = { name = "Магній", desc = "Magnesium. На темно-сіру основу вручну нанесено зображення білого полум’я і крилатого символу саперного набору.\n\nДля тих, хто живе швидко" },
	["gs_mac10_exo_pipes"] = { name = "Труба вниз", desc = "Pipe Down. Нанесено зображення так, неначе зброя заповнена червоними трубами.\n\nВиглядайте з-за завіси" },
	["aa_p250_gravediggers"] = { name = "Крук", desc = "Nevermore. Нанесено яскраву помаранчеву текстуру скелетів пташок.\n\nЗайміть своє місце в ієрархії" },
	["cu_sawedoff_black_sand"] = { name = "Чорний пісок", desc = "Black Sand. Для розфарбовування використано піщано-чорний камуфляжний візерунок.\n\nМіха не врахував високомотивовану опозицію" },
	["gs_sg553_over_heated"] = { name = "Небезпека близько", desc = "Danger Close. Вручну нанесено малюнок на вогняно-металеву тему.\n\nСтежте за вогнем" },
	["gs_tec9_fubar"] = { name = "Фубар", desc = "Fubar. Дуло цієї пофарбованої у військові барви зброї випалене частим використанням.\n\nВи назвете її поношеною, а ми назвемо її улюбленою" },
	["cu_usp_flashback"] = { name = "Флешбек", desc = "Flashback. Вручну нанесено зображення, яке показує двоїсту природу людини.\n\nС’єрра, готель, готель, готель" },
	["sp_famas_ghost_insects"] = { name = "Захисне забарвлення", desc = "Crypsis. Нанесено фарбою текстуру з комахами в осінніх кольорах.\n\nПоглянь уважно" },
	["cu_ak47_aztec"] = { name = "Незвіданий", desc = "Uncharted. Нанесено власну текстуру з різноманітними племінними візерунками.\n\nМи в одному каное" },
	["cu_five_seven_angry"] = { name = "Розлючений натовп", desc = "Angry Mob. Нанесено яскраво-кольорову власну текстуру, навіяну стріт-артом.\n\n«Внутрішні голоси, будь ласка»" },
	["cu_awp_viper"] = { name = "Деревна гадюка", desc = "Atheris. Нанесено яскраво-зелену та блакитну кущову гадюку на чорній основі.\n\nСмертельно красива" },
	["cu_tec9_bamboo"] = { name = "Обман", desc = "Bamboozie. Нанесено нестандартний бамбуковий малюнок.\n\nНе дайте себе обдурити" },
	["gs_mac10_fish_bait"] = { name = "Сиг", desc = "Whitefish. Рибна луска, кістки та крюки нанесені вручну на чорну основу.\n\nУпіймай та вмри" },
	["gs_deagle_exo"] = { name = "Рейкотрон", desc = "Light Rail. Нанесено власний малюнок, який створює оптичну ілюзію та змінює геометрію зброї.\n\nНеобхідний елемент живлення продається окремо" },
	["sp_galil_akoben"] = { name = "Акобен", desc = "Akoben. Нанесено текстуру іржі на білу основу.\n\nТвій особистий бойовий крич" },
	["cu_revolver_oppressor"] = { name = "Череполом", desc = "Skull Crusher. Нанесено зображення із жовтим полум’ям та червоними й білими акцентами.\n\nВідкрий ши-и-ирше" },
	["gs_m4a4_emperor"] = { name = "Імператор", desc = "The Emperor. На створення цього малюнка надихнула карта Таро «Імператор».\n\nДумай головою, навіть якщо це означає ігнорувати своє серце" },
	["cu_aug_momentum"] = { name = "Імпульс", desc = "Momentum. Нанесено зображення із яскравими кольорами та стрілками, які напрямлені вперед.\n\nЗавжди рухайтеся вперед" },
	["gs_mp5sd_astromatic"] = { name = "Гаус", desc = "Gauss. Нанесено зображення для враження, ніби зібрано зі шматочків штампованої сталі.\n\nЗ любов’ю називають «Зоряна Брама 2000»" },
	["cu_mp7_racketeer"] = { name = "Лихо", desc = "Mischief. На червону основу нанесено тривожну чорно-білу усмішку.\n\nНічого доброго з цього не вийде" },
	["aq_p250_verdigris"] = { name = "Мідянка", desc = "Verdigris. На мідній основі проявилися зелені краплі від хімії.\n\nМи дихаємо цим повітрям" },
	["cu_p90_offworld"] = { name = "Неземний", desc = "Off World. Нанесено кілька шарів блакитної фарби; навіть більше, ніж того разу.\n\nЦей верхній шар, мабуть, ніколи не висохне…" },
	["cu_xm1014_incinerator"] = { name = "Підпалігатор", desc = "Incinegator. Нанесено зображення алігатора, що плюється полум’ям.\n\nБум. Бам… Бах." },
	["aa_ump45_moonrise_sunset"] = { name = "Схід місяця", desc = "Moonrise. Над силуетом міста вимальовується чудовий краєвид повного місця та пурпурово-червоного нічного неба.\n\nШукайте Полярну зорю" },
	["cu_bizon_road_warrior"] = { name = "Ембарго", desc = "Embargo. Зібрано зі знайдених предметів, має бірюзову основу і багато реклами з Гавани 1950-х.\n\n¡Chao pescao!" },
	["gs_ak47_nibbler"] = { name = "Тарантайка", desc = "Rat Rod. Знайдено на звалищі, верхній блакитний шар був перефарбований, розмальований і вже майже стерся від часу.\n\nБігти нікуди" },
	["cu_aug_whitefang"] = { name = "Арктичний вовк", desc = "White fang. Нестандартний малюнок, що нагадує хутро арктичного вовка .\n\nКожен раунд — 1 проти 5" },
	["cu_awp_virus"] = { name = "Прорив ізоляції", desc = "Virus. Нанесено нестандартний малюнок, що показує мутованих пацюків, які намагаються втекти з радіоактивного неонового лісу.\n\nХтозна, які радіоактивні хвороби вони переносять…" },
	["gs_mp5_etch"] = { name = "Кислотний душ", desc = "Acid Wash. Перед збіркою верхній синій металевий шар облили кислотою. Оздоблено латунню.\n\nСкрізь чорний, синій і латунний" },
	["gs_tec9_decimator"] = { name = "Винищувач", desc = "Decimator. Нанесено нестандартний малюнок із яскравими блакитними та рожевими висвітленнями.\n\nДоступно за 4 прості платежі обсягом 124,95 $!" },
	["cu_nova_featherswing"] = { name = "Перо", desc = "Plume. Нанесено нестандартний малюк коричневого пір’я на дерев’яному прикладі й ствольній коробці.\n\nТрохи розпушимо пір’я" },
	["cu_g3sg1_blacksand"] = { name = "Чорний пісок", desc = "Black Sand. Для розфарбовування використано піщано-чорний камуфляжний візерунок.\n\nССО коаліції перемагає, тож Міха вирішив вдатися до… агресивнішого підходу" },
	["gs_r8_memento"] = { name = "Пам’ятка", desc = "Memento. Розкіш руків’я зі слонової кістки поєднується з чудовим металевим оздобленням ствольної коробки, ствола й циліндра.\n\nУперед!" },
	["cu_mp7_replica"] = { name = "Неонові жили", desc = "Neon Ply. Нанесено всі можливі неонові кольори.\n\n«Рой Джи Бів? Не чув про таке»." },
	["cu_p2000_obsidian"] = { name = "Обсидіан", desc = "Obsidian. Нестандартний візерунок надає враження, що зброю занурили в лаву, яка перетворюється на обсидіан після охолодження.\n\nЗавдяки цій зброї поняття «скляний пістолет» отримує кардинально нове значення" },
	["gs_dual_elites_rose"] = { name = "Баланс", desc = "Balance. На чорній основі та срібній філіграні кожного руків’я нанесено червону троянду.\n\nБаланс у всьому" },
	["sp_scar20_striker_dust"] = { name = "Розрив", desc = "Torn. Аерозольною фарбою нанесено текстуру випадкових смуг.\n\nНа шмаття…" },
	["cu_sg553_reactor"] = { name = "Колонія IV", desc = "Colony IV. На помаранчево-жовтій течії і протигазах вручну намальовано скелетну червону мураху.\n\nСтаньте частиною їхнього світу" },
	["gs_mac10_stalker"] = { name = "Мисливець", desc = "Torn. Нанесено нестандартний малюнок із синім неоновим представником сімейства котячих і жовто-чорними смугами.\n\nКиць, киць, киць…" },
	["cu_ssg08_tickler"] = { name = "Червоні очі", desc = "Bloodshot. Нанесено нестандартний малюнок почвари із зеленими мацаками, зубами й одним оком.\n\nЦе щоб тебе краще бачити" },
	["gs_m249_warbird_veteran"] = { name = "Льотчик-ветеран", desc = "Warbird. Вік та інтенсивне використання майже стерли блакитне й жовте покриття.\n\nЦій старій пташці вже час іти на пенсію" },
	["aq_m249_aztec"] = { name = "Ацтек", desc = "Aztec. Частини цього викопаного з ацтекських руїн M249 поржавіли та вкрилися мохом.\n\nНіколи не довіряйте мотузковому мосту" },
	["gs_dual_elites_classic"] = { name = "Еліта 1.6", desc = "Elite 1.6. Слово «ELITE» відбито збоку на цих класичних подвійних Береттах.\n\nПершоджерело" },
	["cu_famas_nuke_tension"] = { name = "Знято з експлуатації", desc = "Decommissioned. Цей нестандартний малюнок натхненний елементами мапи de_nuke з Counter-Strike 1.6.\n\nБудь ласка, поверніть знайдене до АЕС «Сідер Крік»" },
	["cu_tec9_flash"] = { name = "Засліплення", desc = "Flash Out. Нанесено нестандартний малюнок, що нагадує світлошумову гранату.\n\nRe: Уроки танців із Джорданом" },
	["cu_fiveseven_gsg9"] = { name = "Приятель", desc = "Buddy. Нанесено нестандартний малюнок камуфляжних візерунків та спецпризначенця за прицілом.\n\nЯ вас прикриваю" },
	["gs_p250_inferno"] = { name = "Інферно", desc = "Inferno. Нанесено нестандартний малюнок, що передає атмосферу мапи de_inferno.\n\n«Подивіться на час!»" },
	["gs_mac10_dust_crate"] = { name = "Класичний ящик", desc = "Classic Crate. Вручну нанесено малюнок, що нагадує зелені металеві ящики із оригінального de_dust2.\n\nАтакуйте B, не зупиняйтеся" },
	["gs_mag7_popdog"] = { name = "Песик", desc = "Popdog. Нанесено нестандартний малюнок, що нагадує вагон із боковим логотипом «Pop Dog».\n\nПес, що освіжає!" },
	["cu_mp9_hydra"] = { name = "Гідра", desc = "Hydra. Нестандартний малюнок, що відсилає до операції «Гідра», восьмій операції в CS:GO.\n\nЦе варте очікування" },
	["cu_p90_nostalgia"] = { name = "Ностальгія", desc = "Nostalgia. Нанесено нестандартний малюнок спецпризначенця з головного меню оригінального Counter-Strike.\n\nВстановлюється за хвилину, але потребує багато років для оволодіння" },
	["am_aug_death_by_doggy"] = { name = "Смерть від цуценят", desc = "Death by Puppy. Застосовано різнокольоровий візерунок із логотипом «Pop Dog».\n\nНе плутати з драбиною" },
	["cu_scar_assault"] = { name = "Напад", desc = "Assault. На синю основу вручну нанесено білий логотип науково-дослідницької установи.\n\nТакож доступна червона версія" },
	["gs_mp5_fbi"] = { name = "Агент", desc = "Agent. Нанесено нестандартне зображення в синьому стилі ФБР із жовтими акцентами.\n\n«Час заробити наші власні історії»" },
	["cu_ump_bomb"] = { name = "Пластикова вибухівка", desc = "Plastique. Нанесено нестандартний малюнок дротів, зеленої монтажної плати і панелі з цифрами.\n\n73556**" },
	["cu_awp_wildfire"] = { name = "Дике полум’я", desc = "Wildfire. На чорно-червону основу нанесено логотип операції «Дике полум’я».\n\nЯкщо гвіздок стирчить, то на нього звертає увагу молоток" },
	["cu_glock_hero"] = { name = "Самопожертва", desc = "Sacrifice. Нанесено нестандартний малюнок на честь випадку на «ESL One Cologne 2014», коли охоплений полум’ям olofmeister знешкодив бомбу на мапі Overpass.\n\n«Знешкодження триває… він не зупиняється!»" },
	["gs_famas_legacy_gold"] = { name = "Вшанування", desc = "Commemoration. Нанесено нестандратний малюнок із позолотою, що показує різні деталі святкування 20-річчя Counter-Strike.\n\nОсь ще 20" },
	["gs_ak47_gold_arabesque"] = { name = "Золота арабеска", desc = "Gold Arabesque. Вручну нанесено витончене зображення на приклад і цівку. Метал пофарбовано в золото з акцентами на філігранях.\n\n«Які ще козирі Бут сховав у рукаві?»" },
	["am_lizard_red"] = { name = "Помаранчевий аноліс", desc = "Orange Anolis. Нанесено текстуру червоної та золотої ящірки.\n\nРозмальовано до останньої луски." },
	["hy_brush_camo_tan"] = { name = "Пустельні штрихи", desc = "Desert Brush. Аквадруком нанесено пустельну текстуру.\n\nТест на ефективність камуфляжу, версія 1." },
	["hy_ddpat_urban_red"] = { name = "Пікс. камуфляж «Червоний»", desc = "Red DDPAT. Аквадруком нанесено червону текстуру піксельного камуфляжу.\n\nУ момент, коли ви будете достатньо близько, аби розгледіти пікселі, стане запізно" },
	["hy_dry_wood"] = { name = "Мертве дерево", desc = "Spalted Wood. Аквадруком нанесено текстуру, що нагадує висушене дерево.\n\n«Постукайте по дереву, адже вам знадобиться вся ваша удача» — командир Фелікс Райлі" },
	["hy_desert_multicam"] = { name = "Чорний і світлокоричневий", desc = "Black & Tan. Аквадруком нанесено чорне й коричневе мультикам-зображення.\n\nНе можна схибити, якщо не стріляти" },
	["hy_torn_camo_paints"] = { name = "Швидкий пісок", desc = "Quick Sand. Аквадруком нанесено коричневий і синій візерунок на приклад і ствольну коробку. Ствол пофарбовано в темо-синій колір.\n\nМожна назвати «Ласкаво просимо»" },
	["sp_moro_carving_lightblue"] = { name = "Нове коріння", desc = "New Roots. Аерозольним балончиком нанесено візерунок винограду й квітів у різних відтінках зеленого.\n\nМісце для росту" },
	["sp_moro_carving_yellow"] = { name = "Старе коріння", desc = "Old Roots. Аерозольним балончиком нанесено візерунок винограду й квітів у різних відтінках зеленого.\n\nУсталене, випробуване, витримане, а не «старе»" },
	["sp_moro_textile_purple_yellow"] = { name = "Зів’яла лоза", desc = "Withered Vine. Аерозольним балончиком нанесено візерунок квітів у пурпурових і золотих відтінках.\n\nХтось бачить прикрість, а я бачу красу" },
	["sp_palm_night"] = { name = "Опівнічна пальма", desc = "Midnight Palm. Аерозольним балончиком нанесено малюнок пальмового гілля на сірий і пурпуровий камуфляж.\n\n«Знак означає, що зачинено!»" },
	["sp_desert_skulls"] = { name = "Відбілений", desc = "Bleached. Аерозольним балончиком нанесено зображення черепів тварин.\n\nУсе, що лишилося" },
	["sp_zebracam_red"] = { name = "Здобич", desc = "Prey. Аерозольним балончиком нанесено смугастий візерунок.\n\nЇжте, спіть, полюйте, а потім спочатку" },
	["cu_ssg08_scorpion"] = { name = "Смертельний удар", desc = "Death Strike. На цей SSG 08 нанесено зображення піщаних потоків над імператорським скорпіоном.\n\nВоно жалить…" },
	["hy_desert_bloom"] = { name = "Пересохлий", desc = "Parched. Аерозольним балончиком нанесено зображення яскравих квітів на суху й потріскану основу.\n\nПрирода завжди перемагає" },
	["am_ddpat_purple"] = { name = "Пікс. камуфляж «Пурпуровий»", desc = "Purple DDPAT. Аквадруком нанесено пурпурову текстуру піксельного камуфляжу.\n\nУ момент, коли ви будете достатньо близько, аби розгледіти пікселі, стане запізно" },
	["gs_awp_hydra"] = { name = "Пустельна гідра", desc = "Desert Hydra. На цьому зображенні золоті змії повзуть до ствола. Приціл, ствол і задня частина прикладу пофарбована білим.\n\n«Якщо відрізати одну голову, то з’явиться дві нові. Ви до такого готові?» — Валерія Дженнер, революціонерка" },
	["gs_deagle_fennec"] = { name = "Фенек", desc = "Fennec Fox. Нанесено зображення лисиці фенек. Руків’я розфарбовано синьозеленим та помаранчевим.\n\nЦе щоби краще вас чути" },
	["am_mirage_flowers_metalic"] = { name = "Музична скринька", desc = "Music Box. Нанесено золотий дамастовий візерунок.\n\nСтильний політ" },
	["am_moro_textile_bright"] = { name = "Елегантні лози", desc = "Elegant Vines. Нанесено візерунок із червоними та зеленими квітами.\n\nХтось бачить прикрість, а я бачу красу" },
	["am_navy_shine"] = { name = "Морська сталь", desc = "Navy Sheen. Нанесено анодовану текстуру синього варіативного камуфляжу.\n\nЗвідки ця блакить?" },
	["gs_aug_sand_storm"] = { name = "Піщана буря", desc = "Sand Storm. Нанесено зображення піщаної бурі на тлі нічного неба.\n\nУсюди навколо нас краса, потрібно тільки знати, куди дивитися" },
	["sp_dry_wood"] = { name = "Корч", desc = "Drift Wood. Аерозольним балончиком нанесено малюнок, що нагадує висушене дерево.\n\n«Постукайте по дереву, адже вам знадобиться вся ваша удача» — командир Фелікс Райлі" },
	["sp_desert_skulls_dawn"] = { name = "Посуха", desc = "Drought. Аерозольним балончиком нанесено зображення черепів тварин.\n\nУсе, що лишилося" },
	["sp_mirage_flowers_tan"] = { name = "Сієновий дамаст", desc = "Sienna Damask. Аерозольним балончиком нанесено дамастовий квітковий візерунок на коричневу основу.\n\nТехнічно найкращий камуфляж — той, який ви не помічаєте…" },
	["sp_moro_carving_burnt"] = { name = "Зволоження", desc = "Humidor. Аерозольним балончиком нанесено візерунок із коричневими й пурпуровими лозами та квітами.\n\n«Що вони тут тримають?»" },
	["sp_moro_textile_green_vine"] = { name = "Зелені насадження", desc = "Verdant Growth. Аерозольним балончиком нанесено візерунок квітів у різних відтінках зеленого.\n\nХтось бачить прикрість, а я бачу красу" },
	["gs_mp5_neon_flektarn"] = { name = "Окиснена оаза", desc = "Oxide Oasis. Нанесено текстуру яскравого помаранчевого й синьо-зеленого камуфляжу. Зброю оздоблено золотими акцентами. \n\nЦя тиха зброя не мала би бути такою гучною" },
	["aa_desert_bloom_bright"] = { name = "Піщаний квіт", desc = "Desert Blossom. Аерозольним балончиком нанесено зображення яскравих квітів на суху й потріскану основу.\n\nПрирода завжди перемагає" },
	["hy_lizard_skin"] = { name = "Аноліс", desc = "Anolis. Аквадруком нанесено текстуру зеленої та чорної луски ящірки.\n\nРозмальовано до останньої луски." },
	["cu_ak-47_phantom_disruptor"] = { name = "Фантомний руйнівник", desc = "Phantom Disruptor. На матовий чорний корпус нанесено малюнок істоти з туманними обрисами.\n\n«Він може бути чим хоче й де хоче, але все одно хоче бути поруч із тобою…»" },
	["gs_aug_thunderstorm"] = { name = "Кіт Том", desc = "Tom Cat. На синьо-сіру основу нанесено золоті елементи, спрямовані назад стрілки й малюнок зловтішного кота.\n\nГрози — це муркотіння природи" },
	["cu_awp_vein"] = { name = "Капіляр", desc = "Capillary. Розмальовано химерним переплетінням вен коралового та зеленувато-блакитного кольорів.\n\nКлюч до серця агента коштує 4750 $" },
	["cu_cz75_cerakote"] = { name = "Обшарпаний", desc = "Distressed. Мідь на кераміці вже стирається від користування.\n\nДедалі краще з роками" },
	["cu_deagle_replica"] = { name = "Сині жили", desc = "Blue Ply. Нанесено всі можливі неонові кольори.\n\nУВАГА: це копія, яка не була протестована з боєприпасами" },
	["cu_m4a1s_csgo2048"] = { name = "Другий гравець", desc = "Player Two. Зброю розмальовано в яскраві кольори, на неї нанесли мультиплікаційні зображення спецпризначенця GIGN і собаки Pop Dog.\n\nНатисніть Start…" },
	["cu_mac10_nacre"] = { name = "Диско-технології", desc = "Disco Tech. Нанесено зображення перламутровою фарбою, щоби було схоже на вінілову наліпку. Залежно від освітлення зовнішній вигляд змінюється.\n\nДивися, куди направляєш цю штуку" },
	["gs_mag7_justice"] = { name = "Справедливість", desc = "Justice. На створення цього малюнка надихнула карта Таро «Справедливість».\n\nПокарання для всіх" },
	["cu_mp5_desert_strike"] = { name = "Пустельний удар", desc = "Desert Strike. Нанесено зображення у стилі хайтек чорною та коричнево-жовтою фарбами.\n\nПерший удар — останній удар" },
	["cu_negev_prototype"] = { name = "Прототип", desc = "Prototype. Нанесено тимчасовий малюнок червоною та білою фарбою допоки не затвердять остаточний дизайн.\n\nв.1.2.1, ПАТ. 3422107" },
	["aq_p2000_acid_clover"] = { name = "Кислотне гравірування", desc = "Acid Etched. Нанесено різнокольоровий візерунок із рідкісною чотирилистою конюшиною.\n\nВідчуваєте свою удачу?" },
	["gs_r8_leviathan"] = { name = "Кована кістка", desc = "Bone Forged. На руків’я цього револьвера нанесено зображення решток прадавньої морської істоти.\n\nЩо причаїлося внизу?" },
	["cu_sawedoff_apocalypto"] = { name = "Апокаліпто", desc = "Apocalypto. Нанесено зображення гнилої руки, яка тягнеться до планети у вигляді бомби на тлі фіолетового космосу.\n\n«Маєте хвилинку?»" },
	["gs_scar20_enforcer"] = { name = "Силовик", desc = "Enforcer. На різні частини зброї ретельно нанесено градієнт на тему заходу сонця.\n\nАвтоматичне рішення від збройної корпорації SYMETRY SMART" },
	["cu_sg553_darkwing"] = { name = "Темнокрил", desc = "Darkwing. Нанесено червоно-чорний малюнок із пір’ям та іншими чорними деталями.\n\nГострозорий і смертоносний — небезпечнішого наземного птаха годі й знайти" },
	["cu_ssg08_fever_dream"] = { name = "Лихоманковий сон", desc = "Fever Dream. Нанесено маніакально-рожевий, синій та пурпуровий малюнок на чорну основу.\n\nХ@@ХХ@Х@Х@ХА!!" },
	["cu_glock18_warmaiden"] = { name = "Королева куль", desc = "Bullet Queen. На жовто-чорну основу нанесено зображення шалених куль і воїтельки в рожевій куртці.\n\n«Я припиню стріляти, коли ти замовкнеш»" },
	["cu_negev_ultralight"] = { name = "Ультрасвітлий", desc = "Ultralight. Нанесено нестандартний малюнок із сірим цифровим камуфляжем та бірюзовими стрілками.\n\nЄ запальничка?" },
	["cu_ak47_anubis"] = { name = "Легіон Анубіса", desc = "Legion of Anubis. Нанесено нестандартний малюнок із єгипетськими богами Анубісом та Ра, які зображують баланс між життям і смертю.\n\nНехай ваша шалька терезів переможе" },
	["aq_p2000_lost_world"] = { name = "Покруч", desc = "Gnarled. Перекручений прибулець, що простягається від руків’я до затвора, охопив собою P2000.\n\nВід далеких закутків нескінченої порожнечі" },
	["cu_mag7_monster_call"] = { name = "Голос монстра", desc = "Monster Call. Нанесено нестандартний малюнок із голубою основою та підводними створіннями на передньому плані.\n\nЗавжди є більша та зеленіша риба" },
	["cu_printstream"] = { name = "Друкопис", desc = "Printstream. Нанесено зображення з різким чорно-білим дизайном та з перламутровими вставками.\n\nWHITE_1; BLACK_1; PEARLESCENT_1;" },
	["cu_glock_eyecontact"] = { name = "Мода", desc = "Vogue. Нанесено нестандартний малюнок у стилі поп-арт, а саме пару очей, які прикрашають затвор цього Глока.\n\n«Дивіться на мене, поки я у вас стріляю!»" },
	["gs_tec9_guerilla"] = { name = "Брат", desc = "Brother. Нанесено нестандартний малюнок із коричневим та золотим камуфляжем та агентом терористів за ціликом.\n\nЯ прикрию ваші тили" },
	["cu_mac10_isoonna"] = { name = "Спокуса", desc = "Allure. На MAC-10 нанесено нестандартний малюнок із жінкою в бірюзовому, вважають, що це ісо-онна, яка стоїть навпроти риби-кої та розбиває хвилі.\n\nКраса починає кровопролиття" },
	["gs_sg553_rusty"] = { name = "Старий іржавий", desc = "Ol' Rusty. Ця зброя первісно було виготовлена з полірованої сталі, але вологість змінила її вигляд. «Ol' Rusty» відбито над спусковою скобою.\n\nІржавий вірний друг" },
	["cu_ssg08_mainframe"] = { name = "Мейнфрейм 001", desc = "Mainframe 001. Нанесено високотехнологічний малюнок із голубими та фіолетовими кольорами.\n\nЯкі секрети приховує _001?" },
	["cu_p250_cassette"] = { name = "Касета", desc = "Cassette. Нанесено зображення у вигляді відеокасети з 90-х.\n\nПеревірте плівку" },
	["gs_p90_container"] = { name = "Вантаж", desc = "Freight. Нанесено зображення, що нагадує вагон із графіті.\n\nМистецтво не є вандалізмом" },
	["cu_xm1014_amulet_blue"] = { name = "Похований", desc = "Entombed. На нещодавно розкопаний XM-1014, який був під землею протягом тисячі років, нанесено малюнок із блакитною обгорткою та єгипетськими мотивами.\n\nСаме те для фараона" },
	["cu_m4a4_queenfairy"] = { name = "Зубна фея", desc = "Tooth Fairy. Нанесено зображення, що містить різці, моляри та моторошний варіант зубної феї.\n\nНіхто не казав, що зуби мають бути вашими…" },
	["gs_galil_phoenix"] = { name = "Зв’язок", desc = "Connexion. На біло-червону основу нанесено логотип фракції «Фенікс».\n\nВони з великими стволами" },
	["gs_bizon_hellraider"] = { name = "Рунічний", desc = "Runic. Нанесено зображення, що містить невідомі руни й магазин у вигляді кулемета Гатлінга.\n\nЗадайте їм жару" },
	["gs_mp5sd_wasteland_legacy"] = { name = "Кітбашинг", desc = "Kitbash. 20 років розмальовок МP5 стали важливою частиною цієї роботи, яка тримається на клейкій стрічці, кабельних стяжках та клеї.\n\nЯкщо з першого разу (або другого, або третього) у вас не вийде…" },
	["gs_awp_exoskeleton"] = { name = "Екзоскелет", desc = "Exoskeleton. На корпусі цієї AWP хитромудро витиснено текстури переплетених черепів.\n\nЦілковита розпука" },
	["gs_cz75_vendetta"] = { name = "Кровна помста", desc = "Vendetta. Багато років на ньому писали, вирізували, і його загартовували використанням.\n\nТільки після моєї смерті" },
	["hy_p90_dino_rampage"] = { name = "Какаове шаленство", desc = "Cocoa Rampage. Оливково-золотавий аквадрук із мультиплікаційним зображенням динозаврів.\n\nІ динозаври, і прибульці, і комети, ого!" },
	["gs_dual_elites_dezastre"] = { name = "Катастрофа", desc = "Dezastre. Чорний, білий і червоний колір вручну нанесений на корпус у стилі хай-тек.\n\nЗавжди дотримуйтеся рекомендацій виробника щодо безпечного користування" },
	["cu_five_seven_diary"] = { name = "Казка", desc = "Fairy Tale. Нанесено зображення принцеси, єдинорогів, красивих квітів із замком на тлі.\n\n«Сьогодні буде чудовий день, я це відчуваю»" },
	["gs_g3sg1_disrupt"] = { name = "Цифрова сітка", desc = "Digital Mesh. Нанесено нестандартний піксельний камуфляж червоного, білого й вугільного кольорів.\n\nСмертоносні хитрощі" },
	["gs_galil_vandal"] = { name = "Вандал", desc = "Vandal. Його зелену основу вкрили білими малюнками.\n\nКожен малюнок розповідає якусь історію, але деякі захопливіші за інші" },
	["cu_p250_infect"] = { name = "Забруднювач", desc = "Contaminant. Його каштанове покриття зітліло, він має трухлявий і ненадійний вигляд.\n\nЯкий вигляд має, так і працює" },
	["cu_m249_deep_relief"] = { name = "Глибокий рельєф", desc = "Deep Relief. Верхній шар корпусу покритий фарбою пісочного кольору, яка стає все більш темно-зеленою з кожним шаром. Це творіння можна відчути на дотик.\n\nСкільки куль потрібно, щоби дістатися до центру?" },
	["cu_m4a4_cyberpunk"] = { name = "Кібербезпека", desc = "Cyber Security. На чорне тло M4 нанесли навскіс сині, червоні й золотисті смуги.\n\nУже доступно на вашому місцевому кібернетичному ринку" },
	["gs_mp5_conditionzero"] = { name = "Нульова умова", desc = "Condition Zero. Синьо-жовті барви, які майстерно нанесені вручну, віддають шану славетній грі Counter-Strike: Condition Zero.\n\n«РПГ на три години! Ухиляйся!»" },
	["cu_nova_polymer"] = { name = "Прозорий полімер", desc = "Clear Polymer. Нанесено нестандартний малюнок з ілюзією прозорості, що дозволяє розгледіти нутрощі зброї.\n\nВаші наміри прозорі" },
	["cu_glock_noir"] = { name = "Нео-нуар", desc = "Neo-Noir. На сіре тло нанесено стилізоване синьо-пурпурове зображення дівчини.\n\n«Почалися зміни і в неї з’явилася надія, але потім вона поглянула вгору»" },
	["cu_ssg08_chromatic"] = { name = "Паралакс", desc = "Parallax. Нанесено нестандартний малюнок із викривленою кольоровою гаммою задля карколомного результату.\n\nДва постріли — один труп" },
	["gs_ump_gold_bismuth"] = { name = "Золотий вісмут", desc = "Gold Bismuth. Смуга золотистого вісмуту поміж білим та чорним кольорами.\n\n«Має коштовний вигляд…»" },
	["cu_usp_krokos"] = { name = "Потворна суміш", desc = "Monster Mashup. Нанесено зображення зеленого чудовиська та інших символів урбаністичного мистецтва. На глушник нанесено перламутрове покриття.\n\nX__X" },
	["cu_csgo_camo"] = { name = "Глобальний наступ", desc = "Global Offensive. Нанесено камуфляж синього, жовтого й сірого кольорів.\n\n«Спочатку вдаримо в Гарц, Німеччина, о 17:25 за Грінвічем + 1…»" },
	["am_authority_brown"] = { name = "Хартія", desc = "Charter. Нанесено текстуру кольору засмаги з різноманітними гербами, написами й текстом із Великої хартії вольностей.\n\nСправедливість однакова для всіх — навіть для вас" },
	["hy_authority_purple"] = { name = "Велика хартія вольностей", desc = "Magna Carta. Нанесено пурпурове зображення гербів, надписів та тексту з Великої хартії вольностей.\n\nСуд здійснять над кожним, навіть над вами" },
	["am_intelligence_grey"] = { name = "Спостереження", desc = "Target acquired. Нанесено нестандартне розфарбовування з накладеним на аерофотографію інтерфейсом.\n\nЦіль визначено" },
	["am_intelligence_orange"] = { name = "Загрозу виявлено", desc = "Target acquired. Нанесено нестандартне розфарбовування з накладеним на аерофотографію інтерфейсом.\n\nЦіль визначено" },
	["am_intelligence_magenta"] = { name = "Ціль визначено", desc = "Target acquired. Нанесено нестандартне розфарбовування з накладеним на аерофотографію інтерфейсом.\n\nЦіль визначено" },
	["am_numbers_bronze"] = { name = "Бронзовий", desc = "The Bronze. Нанесено різнокольорову текстуру з випадкових цифр.\n\nЦифри не брешуть, а я брешу" },
	["am_numbers_red_blue"] = { name = "Регулятор", desc = "Dispatch. Нанесено різнокольорову текстуру з випадкових цифр.\n\nЦифри не брешуть, а я брешу" },
	["hy_numbers_green"] = { name = "Зміна сторін", desc = "Switch Board. Нанесено різнокольорову текстуру з випадкових цифр.\n\nЦифри не брешуть, а я брешу" },
	["am_numbers_magenta"] = { name = "Глобальна змова", desc = "Prime Conspiracy. Нанесено різнокольорову текстуру з випадкових цифр.\n\nЦифри не брешуть, а я брешу" },
	["aa_fade_red_blue"] = { name = "Ягоди та вишні", desc = "На затвор нанесено червоно-синю текстуру.\n\nВіу-віу!" },
	["cu_ump_crime_scene"] = { name = "Місце злочину", desc = "Crime Scene. На червоно-синю основу нанесено нестандартний малюнок жовтої стрічки із застереженням.\n\nЗвільніть територію, нам потрібно працювати…" },
	["am_m4a1s_bluesmoke"] = { name = "Синій фосфор", desc = "Blue Phosphor. На синю хромовану основу нанесено декілька шарів синьої металічної фарби.\n\nВідчуваєте смуток?" },
	["cu_ak_xray"] = { name = "Рентген", desc = "X-Ray. Нанесено рентген-зображення нутрощів AK-47.\n\nМожливо, вам не сподобається бачити все…" },
	["am_heist_plans_green"] = { name = "Пограбування", desc = "Heist. На поверхню нанесено бежевий малюнок мапи з ефектом анодування. Здається, що після першого нанесення хтось зробив додаткові позначення.\n\nСпланувати — це одне, а от виконати — інша справа." },
	["am_heist_plans_purple"] = { name = "Нічне пограбування", desc = "Night Heist. На поверхню нанесено фіолетовий малюнок мапи з ефектом анодування. Здається, що після першого нанесення хтось зробив додаткові позначення.\n\nСпланувати — це одне, а от виконати — інша справа" },
	["am_heist_plans_yellow"] = { name = "Пограбування сховища", desc = "Vault Heist. На поверхню нанесено сірий малюнок мапи з ефектом анодування. Здається, що після першого нанесення хтось зробив додаткові позначення.\n\nСпланувати — це одне, а от виконати — інша справа" },
	["hy_houndstooth_brown"] = { name = "Гусячі лапки", desc = "Houndstooth. Аквадруком нанесено коричневу твідову текстуру.\n\nЦе ви вибираєте зброю, а не вона вас" },
	["gs_mac10_snake"] = { name = "Пекучі змії", desc = "Hot Snakes. Нанесено текстуру бронзової зміїної шкіри та малюнок змії, що скрутилася клубком.\n\nКулі сичать та пролітають повз вас" },
	["hy_phoenix_tags_lilac"] = { name = "Крейда «Фенікса»", desc = "Phoenix Chalk. Нанесено багатокольорову текстуру з різними графіті угрупування «Фенікс».\n\n«Безглуздо думати, що ти переможеш!»" },
	["am_phoenix_tags_blue"] = { name = "Маркер «Фенікса»", desc = "Phoenix Marker. Нанесено багатокольорову текстуру з різними графіті угрупування «Фенікс».\n\n«Їм не зупинити нас тепер!»" },
	["hy_phoenix_tags_red"] = { name = "Трафарет «Фенікса»", desc = "Phoenix Stencil. Нанесено багатокольорову текстуру з різними графіті угрупування «Фенікс».\n\n«Нас не можна вбити!»" },
	["sp_phoenix_tags_purple"] = { name = "Ультрафіолет «Фенікса»", desc = "Phoenix Blacklight. Нанесено різнокольорові графіті угрупування «Фенікс».\n\n«Ми танцюватимемо на ваших могилах!»" },
	["hy_tigers_tan"] = { name = "Глиняна засідка", desc = "Clay Ambush. Аквадруком нанесено жовтувато-коричневу текстуру тигрів.\n\nЗазвичай ці хижаки — одинаки, але для вас вони зроблять виняток" },
	["am_tigers_brown"] = { name = "Яма з тиграми", desc = "Tiger Pit. Аквадруком нанесено жовтувато-коричневу текстуру тигрів на чорній основі.\n\nУсюди, лише не тут" },
	["am_tigers_blue"] = { name = "Шовковий тигр", desc = "Silk Tiger. Аквадруком нанесено тигрову текстуру на синій основі.\n\nНаціональне надбання — не AWP, а тигри" },
	["cu_m4a1_snake"] = { name = "Вітаємо в джунглях", desc = "Welcome to the Jungle. Нанесено зображення золотої кобри та інших стародавніх символів на затемненому тлі джунглів.\n\nЗавдає удару без попередження" },
	["cu_ak_jaguar"] = { name = "Ягуар", desc = "Panthera onca. Дерево на прикладі та цівці прикрашене красивими різьбленнями, які часто відвертають увагу від нанесеного збоку нестандартного малюнка ягуара.\n\nХто сказав, що козаки тільки шаблями махати вміють?" },
	["cu_jaguar_p90"] = { name = "Біжи й ховайся", desc = "Run and Hide. Нанесено нестандартний малюнок із текстурою ягуара й оброблено плямистою шкірою.\n\nБудь-хто може собі грішми відчинити двері до світу моди, однак мало хто має істинний стиль і смак" },
	["aa_ancient_brown"] = { name = "Древня земля", desc = "Ancient Earth. Нанесено коричнево-золоту текстуру з хитромудрими смужками та древніми істотами.\n\nЦей привілей належить вам" },
	["am_ancient_warm"] = { name = "Древнє знання", desc = "Ancient Lore. Нанесено зображення із текстурою древніх плит блідо-зеленого, бежевого та червоного кольорів.\n\n«Надія залишається навіть із тими, хто пішов хибним шляхом»" },
	["aa_ruins_green"] = { name = "Зарослі руїни", desc = "Lush Ruins. На тлі цих розкішних кущів видніються обриси стародавніх богів.\n\n«Усім бути пильними — за нами стежать…»" },
	["hy_ancient_tiles_peach"] = { name = "Вибух із минулого", desc = "Blast From the Past. Нанесено текстуру з древніх плит блідо-зеленого й бежевого кольорів.\n\n«Надія залишається навіть із тими, хто пішов хибним шляхом»" },
	["am_gold_brick"] = { name = "Золота цеглина", desc = "Gold Brick. Це золоте покриття переливається на світлі, підкреслюючи кожну прекрасну недосконалість на зброї.\n\nУсе це блищить…" },
	["sp_ancient_bright"] = { name = "Древнє бачення", desc = "Ancient Visions. На корпус було нанесено червоно-зелену текстуру з хитромудрими смужками та древніми істотами.\n\nЦей привілей належить вам" },
	["hy_ruins_red"] = { name = "Сутінкові руїни", desc = "Dusk Ruins. На фоні цих розкішних кущів видніються обриси стародавніх богів.\n\n«Усім бути пильними — за нами стежать…»" },
	["am_jade"] = { name = "Різьблений нефрит", desc = "Carved Jade. На основу було нанесено хромове покриття, а потім пофарбовано ледь не прозорим шаром нефритово-зеленої фарби, щоби створився ефект анодованого металу.\n\nНайкраща річ з-поміж найкращих" },
	["am_ancient_wine"] = { name = "Древній ритуал", desc = "Ancient Ritual. Нанесено бордово-сріблясту текстуру з хитромудрими смужками та древніми істотами.\n\nЦей привілей належить вам" },
	["hy_drywood_green"] = { name = "Висока трава", desc = "Tall Grass. Аквадруком нанесено текстуру зеленої трави.\n\nУ високій траві ніколи нічого хорошого не відбувається" },
	["am_black_panther"] = { name = "Камуфляж пантери", desc = "Panther Camo. Вкрито плямами чорної пантери.\n\nПідшукайте собі місце в тіні" },
	["gs_ak47_professional"] = { name = "Сланець", desc = "Slate. Нанесено нестандартне розфарбовування, яке можна описати лише словами «чорний на чорному на сланцевому чорному».\n\n«Покличте лікаря, бо він їм знадобиться»" },
	["cu_cz75_whirlwind"] = { name = "Змієїд", desc = "Circaetus. Нанесено нестандартне розфарбовування у вигляді хижого птаха зі змією.\n\nЯк в іншій грі, але без крутецької заголовної пісні" },
	["cu_mp9_food_chain"] = { name = "Ланцюг харчування", desc = "Food Chain. На яскраво забарвлений MP9 нанесли зображення харчового ланцюга монстрів.\n\nЇж або будь з’їденим" },
	["cu_galil_chroma_pink"] = { name = "Хроматична аберація", desc = "Chromatic Abberation. Нанесено нестандартний малюнок із викривленою кольоровою гаммою задля карколомного результату.\n\n(Не) приємно споглядати…" },
	["gs_glock_polymer"] = { name = "Прозорий полімер", desc = "CLear Polymer. Затвор розфарбовано жовтуватим, а курок — синім. Корпус зроблений із прозорого полімеру.\n\nЩо бачиш, те й отримаєш" },
	["gs_usps_hangedman"] = { name = "Повішений", desc = "The Traitor. На створення цього малюнка надихнула карта Таро «Повішений».\n\nКолишні невдачі — це основа для майбутніх успіхів" },
	["cu_m4a4_love"] = { name = "Живі кольори", desc = "In Living Color. Нанесено зображення із використанням неонових кольорів і вкрита вручну намальованими візерунками.\n\nБ’єшся за одного — б’єшся за всіх" },
	["gs_m249_combine"] = { name = "ШЗПІР", desc = "O.S.I.P.R. Нанесено нестандартне темно-сіре розфарбування із доданим логотипом комбінантів.\n\nШтатна зброя патрульних (імпульсна рушниця)" },
	["cu_negev_devtexture"] = { name = "dev_texture", desc = "dev_texture. На помаранчеве тло нанесено сірі розробницькі текстури.\n\nЧас для hammer.exe" },
	["gs_p250_cybershell"] = { name = "Цифровий панцир", desc = "Cyber Shell. Затвор пофарбовано в сріблястий та нанесено шестикутники на ручку.\n\nВи уособлюєте наше прагнення до кібербезпеки" },
	["cu_mac10_portable"] = { name = "Кнопкодав", desc = "Button Masher. Під прозорий полімерний корпус нанесено зображення ґеймпада й кнопок.\n\nТепер вираз «війни консолей» має зовсім нове значення." },
	["aq_xm1014_punk"] = { name = "Цьомки", desc = "XOXO. Нанесено яскравий візерунок у стилі субкультури «панк».\n\nТак-так…" },
	["gs_r8_rustking"] = { name = "Звалище брухту", desc = "Junk Yard. На синю основу нанесено сріблясті написи й малюнки. Мушку та циліндр пофарбовано в червоний.\n\nГідний короля" },
	["gs_sg553_deathmetal"] = { name = "Важкий метал", desc = "Heavy Metal. На чорну основу нанесено помаранчеві й жовті смуги. Задню частину прикладу прикрашено трьома зірками.\n\n«Тут якось можна ввімкнути турбо. Щоправда, я не пам’ятаю як…»" },
	["am_ump45_electrowave"] = { name = "Осцилятор", desc = "Oscillator. Нанесено нестандартний візерунок у помаранчевих, чорних і сірих кольорах.\n\nКожен має свою межу витривалості. Знаєш, де твоя?" },
	["cu_deag_trigger_discipline"] = { name = "Дисципліна стрільби", desc = "Trigger Discipline. На дуло нанесено ізометричний візерунок, курок і приціл пофарбовано в яскраво-синій.\n\nТерпіння — це благо" },
	["sp_nova_wind_dispersal"] = { name = "Кульбаба", desc = "Windblown. Нанесено нестандартний візерунок кульбаб.\n\nЗабирайся з мого ґанку" },
	["am_nuclear_pattern4_famas"] = { name = "Розплавлення", desc = "Meltdown. Візерунок із символів ядерної небезпеки нанесено металевою фарбою.\n\nЯкийсь час тримайтеся подалі…" },
	["aa_spacerace_orange"] = { name = "Космічні перегони", desc = "Space Race. Нанесено візерунок на космічну тему із зірок і супутників.\n\nВідсилання і приймання" },
	["sp_spacerace_blue"] = { name = "Супутник", desc = "Sputnik. Нанесено візерунок на космічну тему із зірок і супутників.\n\nВідсилання і приймання" },
	["sp_technowar_red"] = { name = "ПОП-Шипучка", desc = "Fizzy POP. Нанесено візерунок на ретротехнічну тематику коричневими та червоними відтінками. \n\nВійни виграються інформацією" },
	["hy_technowar_rwb"] = { name = "ПОП-AWP", desc = "POP AWP. Нанесено візерунок на ретротехнічну тематику білими, червоними та синіми відтінками.\n\nВійни виграються інформацією" },
	["hy_trainarchitect_green"] = { name = "Весняний фасон", desc = "Spring Twilly. Аквадруком нанесено текстуру різнорідних чорних та зелених образів.\n\nОстанній шанс, потяг залишає станцію" },
	["hy_trainarchitect"] = { name = "Осінній фасон", desc = "Autumn Twilly. Аквадруком нанесено текстуру різнорідних червоних та коричневих образів.\n\nОстанній шанс, потяг залишає станцію" },
	["gs_m4a4_coalition"] = { name = "Коаліція", desc = "The Coalition. Нанесено зображення золотих і чорних переплетених трикутників із синім підсвіченням.\n\nЦей альянс тимчасовий, але ви змушуєте мене переглянути…" },
	["cu_mac10_propaganda"] = { name = "Пропаганда", desc = "Propaganda. Нанесено зображення пари очей із кількома словами російською, зокрема «контроль» і «страх».\n\nВи ж їм не вірите?" },
	["gs_train_cz75"] = { name = "Синдикат", desc = "Syndicate. Нанесено зображення золотих і чорних переплетених трикутників із синім підсвіченням.\n\nМи йтимемо разом" },
	["hy_ak47lam_green"] = { name = "Зелений композит", desc = "Green Laminate. На приклад, руків’я та цівку нанесено зелене ламінування.\n\nЦе прості речі" },
	["sp_galil_caution"] = { name = "ОБЕРЕЖНО!", desc = "CAUTION!. Аерозольним балончиком нанесено зображення контрастних чорних і жовтих шевронів.\n\nПочувайтеся комфортно в некомфортності" },
	["hy_geometric_steps_pearl"] = { name = "Призматичні сходи", desc = "Prism Terrace. Нанесено зображення перламутрової оптичної ілюзії.\n\nДе це почалося й де це закінчиться? І чи це закінчиться?" },
	["gs_m4a1_vertigo"] = { name = "Неминуча небезпека", desc = "Imminent Danger. Нанесено зображення різнокольорових знаків про небезпеку.\n\n«Не кажіть, що я вас не попереджав…» — Бут, продавець зброї" },
	["hy_vertigogeo"] = { name = "Схема", desc = "Schematic. Нанесено різнокольоровий візерунок ізометричної сітки.\n\nВи пам’ятаєте біль, а я пам’ятаю перемогу" },
	["hy_vertigospray_blue"] = { name = "Стратегія", desc = "Strats. Аквадруком нанесено текстуру плану 51-го поверху, інженерних схем вентиляції та електричних мереж.\n\nУсе згідно з планом…" },
	["hy_vertigoillusion"] = { name = "Фреймворк", desc = "Framework. Аквадруком нанесено сіру й коричневу текстуру з оптичною ілюзією.\n\nМаєте гарний вигляд?" },
	["hy_vertigoillusion_yellow"] = { name = "Переплетіння", desc = "Interlock. Аквадруком нанесено жовто-синю текстуру з оптичною ілюзією.\n\nГарно виглядаєте?" },
	["hy_vertigospray"] = { name = "Інфраструктура", desc = "Infrastructure. Аквадруком нанесено текстуру плану 51-го поверху, інженерних схем вентиляції та електричних мереж.\n\nУсе згідно з планом…" },
	["aa_vertigogeo_neon"] = { name = "Цифровий архітектор", desc = "Digital Architect. Нанесено різнокольоровий візерунок ізометричної сітки.\n\nВи пам’ятаєте біль, а я пам’ятаю перемогу" },
	["cu_sg553_caution"] = { name = "Ціна за небезпеку", desc = "Hazard Pay. Нанесено зображення помаранчевих дорожніх конусів на жовту основу.\n\nВідкладайте увагу в бік один раз на кулю" },
	["aa_wiring"] = { name = "Електрощит", desc = "Breaker Box. Нанесено анодовану текстуру мережевих схем.\n\nБудь ласка, проконсультуйтеся з електриком перед виконанням власного ремонту" },
	["aa_wiring_yellow"] = { name = "Несправне підключення", desc = "Faulty Wiring. Нанесено текстуру мережевих схем.\n\nБудь ласка, проконсультуйтеся з електриком перед самостійним виконанням ремонту" },
	["sp_engine_dirty"] = { name = "Заміна мастила", desc = "Oil Change. Аерозольним балончиком нанесено зображення компонентів двигуна.\n\n«Має кепський вигляд…»" },
	["aa_engine_performance"] = { name = "Механізм", desc = "Mechanism. Нанесено анодований візерунок компонентів двигуна.\n\n«Має кепський вигляд…»" },
	["cu_vertigo_fiveseven"] = { name = "Загроза падіння", desc = "Fall Hazard. Нанесено різнокольорове зображення із застережувальними написами про можливе падіння.\n\nДотримуйтеся дистанції" },
	["sp_tire_tread_red"] = { name = "Червона шина", desc = "Red Tire. Аерозольною фарбою нанесено текстуру чорно-червоних слідів від шин.\n\nГоріла гума" },
	["sp_tire_tread_blue"] = { name = "Синя шина", desc = "Blue Tire. Аерозольною фарбою нанесено текстуру чорно-синіх слідів від шин.\n\nГоріла гума" },
	["gs_ak47_abstract"] = { name = "Елітний музей", desc = "Leet Museo. Нанесено кубістичне зображення елітної команди терористів, що прокрадаються довшим шляхом до точки А на мапі Dust II.\n\n«Усе, що ви можете уявити, — реальне»" },
	["gs_aug_plague"] = { name = "Чума", desc = "Plague. На створення цього малюнка надихнуло темне фентезі та фільми жахів.\n\n«Мов метелик на вогонь»" },
	["gs_mag7_bismuth"] = { name = "Спектральний вісмут 83", desc = "BI83 Spectrum. Нанесено зображення, що нагадує райдужні кристали вісмута.\n\nЧому найрідкісніші речі — найпрекрасніші?" },
	["cu_deagle_kitch"] = { name = "Океанське узбережжя", desc = "Ocean Drive. Нанесено зображення за допомогою перенасичених королів та різних форм.\n\nВажко не зауважити — і кольори, і використання зброї" },
	["gs_dual_berettas_tread"] = { name = "Слід", desc = "Tread. На обох Береттах було вручну витравлено візерунок хвиль. Затвори пофарбовано золотим.\n\nДосконалі деталі коштують найбільше часу, додають найбільше цінності, але помічаються найменше" },
	["cu_famas_spectron"] = { name = "Зет-Ікс Спектрон", desc = "ZX Spectron. На створення цього малюнка надихнули старі ігрові ПК. Його чорна основа оздоблена різнокольоровими акцентами.\n\nКлайв би пишався" },
	["gs_five_seven_efusion"] = { name = "Протокол посилення", desc = "Boost Protocol. Нанесено зображення за допомогою висококонтрастних кольорів. Знак робота за прицілом збігається з малюнком на затворі.\n\nКод: МЕХА 0272" },
	["aa_mp9_fuji_pink"] = { name = "Гора Фудзі", desc = "Mount Fuji. Нанесено акриловий малюнок гори Фудзі на світанку. Використовується як візерунок, тому кожна зброя може виглядати по-різному.\n\nСходить червоне сонце" },
	["cu_g3sg1_tacticalmap"] = { name = "Нагляд", desc = "Keeping Tabs. На створення цього малюнка надихнув дисплей тактичної мапи «Небезпечної зони».\n\n«Маєте всю необхідну інформацію» — Франц Кріґельд, стратег угрупування «Фенікс»" },
	["cu_mp7_khaki"] = { name = "Партизани", desc = "Guerrilla. Зображення містить зелену основу, чорні деталі й мавпу, що дивиться вперед.\n\nЛев ніколи не був царем джунглів" },
	["gs_m4_flowers"] = { name = "Павуча лілея", desc = "Spider Lily. Нанесено зображення з синьою основою та червоними павучими лілеями над магазином і спусковим гачком.\n\nЯскраві кольори — це застереження, а не запрошення" },
	["cu_mac10_toybox"] = { name = "Коробка з іграшками", desc = "Toybox. Зображення містить яскраві малюнки курчат, іншопланетян, кусачок тощо.\n\nНаведіть тут лад" },
	["gs_bizon_flasher"] = { name = "Люмен", desc = "Lumen. Нанесено чорне зображення з червоними акцентами. Магазин розфарбовано так, щоби нагадувати ліхтарик.\n\nОсвітіть шлях" },
	["cu_glock_snackattack"] = { name = "Атака смаколиків", desc = "Snack Attack. Нанесено зображення, що нагадує залиті пластиком об’їдки.\n\nСмаколики завдають удар у відповідь" },
	["cu_ssg_overtake"] = { name = "Турборежим", desc = "Turbo Peek. Збоку цієї снайперської гвинтівки неоновими кольорами намальовані перегони спортивних автомобілів.\n\nДрифт — не привілей" },
	["cu_usp_black_lotus"] = { name = "Чорний лотос", desc = "Black Lotus. Нанесено зображення чорної квітки лотоса.\n\nДодає три вбивства під час пістолетного раунду, а потім скидається" },
	["gs_xm1014_watchdog"] = { name = "Сторож", desc = "Watchdog. На смарагдову основу нанесено зображення двох вартових левів, що дивляться в різні боки. Третій лев намальований за прицілом.\n\nЗахищайте і процвітайте" },
	["cu_ak47_nightwish"] = { name = "Побажання на ніч", desc = "Nightwish. Неоновими кольорами нанесено зображення оленя й ловців снів й оздоблено перламутровим ефектом.\n\n«Я зберігав це для особливої нагоди…» — Бут, продавець зброї" },
	["cu_bizon_spacecat"] = { name = "Космічний кіт", desc = "Space Cat. На чорну й пурпурову основу нанесено космічний малюнок кота.\n\nЛапи творіння" },
	["cu_elites_beware"] = { name = "Плід уяви", desc = "Melondrama. На ці Беретти нанесено зображення викривленого та страшного кавуна на темно-синьому тлі.\n\nСолодкі сни…" },
	["gs_famas_rapid_eyes"] = { name = "Швидкі рухи очей", desc = "Rapid Eye Movement. Нанесено зображення переплетених рожевих квадратів у вигляді очей.\n\nСтильні очі мрійника" },
	["cu_fiveseven_alpha_omega"] = { name = "Каракулі", desc = "Scrawl. На жовту й синьо-зелену основу нанесено зображення грубих штрихів від руки.\n\nВперше й востаннє" },
	["cu_g3sg1_glade"] = { name = "Поляна снів", desc = "Dream Glade. Нанесено зображення зеленого лісу на тлі синього неба.\n\n«Он, там ліс закінчується!»" },
	["gs_m4a1s_insomnia"] = { name = "Нічний жах", desc = "Night Terror. Нанесено зображення фрагментарних думок зі свідомості, що страждає на безсоння.\n\nУ серці ніжної ночі я шукаю світло" },
	["cu_mac10_pixie"] = { name = "Ув’язнення", desc = "Ensnared. Нанесено зображення фей, ув’язнених усередині цієї зброї, які прагнуть вирватися на волю.\n\nВони ще не знайшли того, кого не можна одурити" },
	["cu_mag7_predictor"] = { name = "Передбачення", desc = "Foresight. На малюнку зображено восьминога, якого попросили передбачити результат матчу і який тягнеться до таблички «перемога».\n\nУ мене добре передчуття…" },
	["cu_mp7_fear"] = { name = "Пекельна з’ява", desc = "Abyssal Apparition. На малюнку зображено покинуті душі, які падають у діру кошмарів.\n\nВам не втекти від своєї долі" },
	["gs_mp9_starlight"] = { name = "Захисник зоряного світла", desc = "Starlight Protector. На малюнку зображено єдинорога з райдужною гривою на білій основі. Оздоблено золотими акцентами.\n\nНехай божество вас захистить, нехай божество вас обере" },
	["am_xm_zombie_offensive"] = { name = "Наступ зомбі", desc = "Zombie Offensive. Нанесено візерунок зомбованих агентів Counter-Strike.\n\n«Невмируща гра»" },
	["cu_usp_to_hell"] = { name = "Квиток до пекла", desc = "Ticket to Hell. На чорній основі намальовані білі технічні схеми.\n\nСмерть дизайнера" },
	["gs_mp5_kid_necronomicon"] = { name = "Некро-молодший", desc = "Necro Jr.. Нанесено зображення чарівних істот і курчати на глушник.\n\nЧас гри сплив!" },
	["cu_p2k_flying_dream"] = { name = "Піднесені душі", desc = "Lifted Spirits. Нанесено зображення лісу та повсталих трупів.\n\nБагато загублених душ не бажають, щоби їх знайшли" },
	["sp_scar_chickenfight"] = { name = "Курчегейст", desc = "Poultrygeist. Аерозольним балончиком нанесено візерунок із курчат на полі бою.\n\nКурча вривається у бій…" },
	["cu_sawedoff_ouija"] = { name = "Спіритична дошка", desc = "Spirit Board. Нанесено зображення, що нагадує затерту спіритичну дошку.\n\n«Знаю, що це ви нею рухаєте…»" },
	["cu_ak47_cogthings"] = { name = "Крижане вугілля", desc = "Ice Coaled. Нанесено яскравий зелено-синій градієнт.\n\nХолодний на дотик" },
	["cu_awp_chroma_pink"] = { name = "Хроматична аберація", desc = "Chromatic Abberation. Нанесено зображення з викривленою кольоровою гаммою задля карколомного результату.\n\n(Не) приємно споглядати…" },
	["gs_revolver_purple_elite"] = { name = "Шалена вісімка", desc = "Crazy 8. Нанесено зображення з фіолетовим кольором, а на руків’я нанесено цифру 8.\n\nНатхненний подіями 8 грудня 2015 року" },
	["gs_famas_corp_meow"] = { name = "Няв 36", desc = "Meow 36. На зелену основу нанесено число 36.\n\nТехнічно це чотири кішки, але хто їх буде рахувати?" },
	["cu_galil_destroyer"] = { name = "Руйнівник", desc = "Destroyer. Нанесено слово «DESTROYER» (з англ. «РУЙНІВНИК»), але прочитати його можна лише під певним кутом.\n\nЗнищ їх усіх" },
	["cu_m249_downvote"] = { name = "Діловий центр", desc = "Downtown. Нанесено сині та рожеві стрілки, спрямовані в усі сторони.\n\nРозпилення іншого типу" },
	["cu_m4a4_elite_tactical"] = { name = "Полімерний ріжок", desc = "Poly Mag. Нанесено зображення з прозорим полімерним магазином, а приклад, захист для рук і руків’я пофарбовано в бежевий колір.\n\nСтандартна робоча процедура" },
	["cu_mac10_monkeyflage"] = { name = "Мавпячий камуфляж", desc = "Monkeyflage. Нанесено зображення червоних, бежевих та зелених мавп.\n\nНахабні мавпочки!" },
	["cu_sg553_cyber_dragon"] = { name = "Техніка дракона", desc = "Dragon Tech. Нанесено зображення біомеханічного зеленого дракона\n\nЦе не мій дракон…" },
	["cu_negev_clear_sky"] = { name = "Відчепись", desc = "Drop Me. Нанесено зображення знаку «Стоп» біля штурмового руків’я на переважно коричневій основі.\n\nЯкщо знак «Стоп» вас не переконає, то це зробить потік куль" },
	["gs_p250_visions"] = { name = "Видіння", desc = "Visions. Нанесено зображення яскравими кольорами.\n\nЧекає, як ворон крові" },
	["gs_p90_tangled"] = { name = "Вентиляційний порив", desc = "Vent Rush. Нанесено зображення клейкої стрічки, трубок і вогню, щоби нагадувати понівечений P90.\n\n«Ви знаєте, як швидко ви рухались?»" },
	["cu_sawedoff_kisslove"] = { name = "Любки♥Цьомки", desc = "Kiss♥Love. За допомогою рожево-фіолетових кольорів нанесено зображення дівчини із сяйнистими очима.\n\n«Ніяких ні! Приймається лише згода!»" },
	["cu_dual_elites_evil_flora"] = { name = "Хижі рослини", desc = "Flora Carnivora. На кожен пістолет нанесено зображення трьох яскраво забарвлених голодних м’ясоїдних рослин.\n\nДопоможіть їм вирости великими та сильними" },
	["gs_ump_roadblock"] = { name = "Блокпост", desc = "Roadblock. Нанесено три горизонтальні помаранчеві лінії, спрямовані вперед стрілками.\n\nШукайте альтернативні маршрути" },
	["gs_glock_elite_camo"] = { name = "Готовий до зими", desc = "Winterized. Нанесено військовий камуфляж зимового кольору.\n\nЗамри!" },
	["cu_mag7_insomnia"] = { name = "Безсоння", desc = "Insomnia. За допомогою оранжево-чорної палітри кольорів нанесено зображення риби, що пливе до рота людини.\n\nКовток, ковток, ковток" },
	["cu_ak_head_shot_holo"] = { name = "Постріл у голову", desc = "Head Shot. На зброю було нанесено графіті «HEAD SHOT GUN» і покрито перламутровим покриттям.\n\n«У кожного є цілі, але чи готові ви докладати зусиль для їх досягнення?» — Бут, продавець зброї" },
	["gs_awp_limbo_snake"] = { name = "Двоїстість", desc = "Duality. Нанесено зображення змії з обох боків. Одна змія червоного кольору, а інша — золота.\n\nКожна історія має дві сторони" },
	["cu_m4a1s_feeding_frenzy"] = { name = "Емфорозавр-С", desc = "Emphorosaur-S. Нанесено зображення динозавра, який прогризає боковину зброї.\n\nБезумство годування" },
	["cu_p2000_decline"] = { name = "Безумний", desc = "Wicked Sick. Нанесено малюнки неоновими кольорами, щоби привернути (і утримати) вашу увагу.\n\nХвилинку вашої уваги." },
	["cu_ump_clutch_kick"] = { name = "Дике дитя", desc = "Wild Child. На основу цього розмальованого UMP-45 нанесено ескізи куль, корону та інші малюнки смугою неонового кольору.\n\nКонцепція проста, складна лише реалізація" },
	["cu_mp9_superlight"] = { name = "Невагомий", desc = "Featherweight. Нанесено зображення за допомогою чорного і білого кольорів для пористого вигляду, що зменшує вагу тримача зброї.\n\nХороший стрілець не звинувачує свої інструменти" },
	["gs_scar_fragments_black"] = { name = "Фрагменти", desc = "Fragments. Нанесено зображення нестандартним металевим кольором, що створює ілюзію зміни геометрії зброї.\n\nФрагмент вашої уяви" },
	["cu_glock_moon_rabbit"] = { name = "Місячний кролик", desc = "Umbral Rabbit. Нанесено малюнок казкової сцени з містичним кроликом, який стрибає перед повним місяцем.\n\nЗробіть стрибок віри" },
	["cu_m4a4_temukau"] = { name = "Темукау", desc = "Temukau. Нанесено нестандартне фарбування в стилі японської анімації, що зображує оперативницю ССО, яка бореться зі штурмом терористів.\n\n«Стійте на своєму!»" },
	["cu_mac10_sakkaku"] = { name = "Саккаку", desc = "Sakkaku. На цьому намальованому MAC-10 на спеціальному освітленні очі сяють червоним.\n\nЗробіть своє сприйняття реальністю" },
	["cu_p250_rebuilt"] = { name = "Відбудова", desc = "Re.built. На цьому P250 намальовано трикутники, що з’єднуються між собою, та інші чіткі, ледь помітні малюнки.\n\nДев'ятий раз — чарівність" },
	["cu_mp5sd_quick_liquidation"] = { name = "Ліквідація", desc = "Liquidation. Завдяки темній основі та ретельно підібраним червоним і синім відблискам цей намальований MP5-SD виглядає так, ніби він світиться під освітленням.\n\nТихе віддзеркалення" },
	["gs_r8_banana"] = { name = "Бананова гармата", desc = "Banana Cannon. Нанесено зображення, що нагадує дуже стиглий банан.\n\nЛовіть їх, поки ковзають" },
	["gs_p90_neoqueen"] = { name = "Неокоролева", desc = "Neoqueen. Зображення розписано на замовлення з використанням суміші військового стилю та наукової фантастики.\n\nСтворено на замовлення для всіх ваших штурмових потреб" },
	["cu_sg553_cyberforce"] = { name = "Кіберсила", desc = "Cyberforce. На червоно-чорну основу нанесено логотип CYBERFORCE INC.\n\n«CYBERFORCE INC: Ми забезпечуємо ваше майбутнє за вас!»" },
	["cu_tec9_freedom"] = { name = "Повстанець", desc = "Rebel. Цей розмальований TEC-9 покритий слабо пов’язаними малюнками з використанням приглушеної колірної палітри.\n\nОдин постріл, одне вбивство, один раунд за раз" },
	["gs_revolver_acanthus_bronze"] = { name = "Inlay", desc = "It has been custom painted to resemble acanthus engravings and a marble grip.\n\nOpulence in the palm of your hand" },
	["gs_ak47_strone"] = { name = "Steel Delta", desc = "The lines of this custom paint job resemble grooves on a topographical map.\n\nBleed between the lines" },
	["gs_awp_strone"] = { name = "Black Nile", desc = "The lines of this custom paint job resemble grooves on a topographical map.\n\nBleed between the lines" },
	["cu_glock_ramzes"] = { name = "Ramese's Reach", desc = "The Nile river has been custom painted on the slide and the grip depicts Ramesses I, the founding pharaoh of ancient Egypt's 19th Dynasty.\n\n\"My people reach for their Gods, and I am here to connect them\"" },
	["cu_famas_holo_ocean"] = { name = "Waters of Nephthys", desc = "The blues on this custom painted FAMAS shift as lighting conditions change.\n\nWater always wins" },
	["cu_m249_scarab"] = { name = "Submerged", desc = "It has been custom painted using teal-colored desert camouflage and environments as inspiration.\n\n\"WE'VE BEEN SET UP! THEY'RE EVERYWHERE!\"" },
	["gs_m4a1s_milspec"] = { name = "Mud-Spec", desc = "A custom black and brown camouflage has been applied.\n\nBackup has arrived" },
	["gs_m4a4_ra"] = { name = "Eye of Horus", desc = "It has been custom painted with a white base, gold accents, and the Egyptian falcon god Horus.\n\nRise above the rest" },
	["cu_mac10_scarab"] = { name = "Echoing Sands", desc = "It has been custom painted using desert camouflage and environments as inspiration. A scarab has been painted under the wire form butt.\n\n\"WE'VE BEEN SET UP! THEY'RE EVERYWHERE!\"" },
	["gs_mag7_goold"] = { name = "Copper Coated", desc = "It has been custom painted to look like it was held under a stream of liquid copper.\n\nThis was an accident, not a mistake..." },
	["gs_mp7_strone"] = { name = "Sunbaked", desc = "The lines of this custom paint job resemble grooves on a topographical map.\n\nBleed between the lines" },
	["gs_nova_sobek"] = { name = "Sobek's Bite", desc = "This custom paint job features gold and black Egyptian inspired designs.\n\nFrom the water comes chaos" },
	["gs_p250_apep"] = { name = "Apep's Curse", desc = "It has been custom painted using Egyptian patterns and materials as inspiration.\n\nIn opposition of Ra" },
	["hy_dune_trap"] = { name = "Snake Pit", desc = "A custom pattern of snakes and amphibians has been applied.\n\nActing on ill intentions" },
	["cu_p90_scarab_of_wisdom"] = { name = "ScaraB Rush", desc = "It has been custom painted with scarabs and Egyptian inspired designs.\n\nResurrection requires death, and death requires rushing" },
	["gs_ssg08_anubis"] = { name = "Azure Glyph", desc = "It has been custom painted with blue water, Egyptian hieroglyphs, and finished with wood running the length of the weapon.\n\nFlood warning" },
	["cu_tec9_mummy"] = { name = "Mummy's Rot", desc = "It has been custom painted to resemble a mummy. A cobra with green eyes has been painted beneath the rear sight.\n\nKeep this under wraps" },
	["gs_usps_tactical_camo"] = { name = "Desert Tactical", desc = "It has been custom painted with an olive-colored tactical camouflage.\n\nBase models don't have to be boring" },
	["cu_xm1014_pharaoh"] = { name = "Hieroglyph", desc = "It has been custom painted with Egyptian hieroglyphs and ornaments.\n\nThe moral of the story" },
	["sp_nukestripe"] = { name = "sp_nukestripe", desc = "Fallout Warning, Irradiated Alert, Nuclear Threat, Radiation Hazard. Аерозольним балончиком нанесено малюнок у вигляді знаку радіологічної небезпеки." },
	["sp_spray_waves_bravo"] = { name = "sp_spray_waves_bravo", desc = "Wave Spray. На цей сувенір з Seaside аерозольним балончиком нанесено зображення коротких товстих ліній контрасних кольорів.\n\nІдеально підходить для бою на ходу" },
	["hy_ocean_bravo"] = { name = "hy_ocean_bravo", desc = "Bright Water. На цей сувенір із Seaside аквадруком нанесено зображення синього камуфляжу.\n\n«Наш світ сповнений підступністю… та часом камуфляж зроблений саме для того, аби його побачили» — Бут, продавець зброї" },
	["sp_spitfire_famas_bravo"] = { name = "sp_spitfire_famas_bravo", desc = "Spitfire. На цей сувенір з Cache нанесено зображення пащі акули у стилі зображень на носах літаків.\n\nСмерть блазня" },
	["hy_tile_bravo"] = { name = "hy_tile_bravo", desc = "Mosaico. На цей сувенір з Ali аквадруком нанесено зображення мозаїки.\n\nТерпіння — чеснота" },
	["an_navy_bravo"] = { name = "an_navy_bravo", desc = "Anodized Navy. Цей сувенір з Ali вкрито хромом, на який нанесено напівпрозору темно-синю фарбу з ефектом анодування.\n\n«Я вже втратив одну людину через брехню Валерії… І більше не хочу щоб це повторилось» — командир Фелікс Райлі" },
	["sp_hazard_bravo"] = { name = "sp_hazard_bravo", desc = "Hazard. На цей сувенір з Siege аерозольною фарбою нанесено зображення смугастих знаків небезпеки.\n\nНЕ ПЕРЕХОДИТИ" },
	["sp_tape_dots_bravo"] = { name = "sp_tape_dots_bravo", desc = "Dry Season. На цей сувенір з Gwalior аерозольною фарбою нанесено зображення за допомогою трафарету зі рваних магнітофонних стрічок та перфорованого металу.\n\n«Не турбуйтесь… скоро справи підуть в гору» — Бут, продавець зброї" },
	["hy_ddpat_bravo"] = { name = "hy_ddpat_bravo", desc = "Forest DDPAT. На цей сувенір із Ruins аквадруком нанесено зображення піксельного камуфляжу.\n\nНавіть якщо ви його не бачити, ви обов’язково його почуєте" },
	["so_jungle_bravo"] = { name = "so_jungle_bravo", desc = "Jungle. На частини цього сувеніру з Ruins аерозольною фарбою нанесено зображення у кольоровій гамі джунглів.\n\nДля тих, любить руйнувати" },
	["so_tornado_bravo"] = { name = "so_tornado_bravo", desc = "Tornado. На частини цього сувеніру з Ali аерозольною фарбою нанесено зображення у кольоровій гамі торнадо.\n\nСлова — це вітер, але вітер може вбити" },
	["so_sand_bravo"] = { name = "so_sand_bravo", desc = "Sand Dune. На частини цього сувеніру з Gwalior аерозольною фарбою нанесено зображення у кольоровій гамі піщаних дюн.\n\nГонзо-журналіст Алекс Кінкайд, зник після подорожі до Магрибу, де він висвітлював операцію «Авангард»" },
	["so_olive_bravo"] = { name = "so_olive_bravo", desc = "Groundwater. На частини цього сувеніру з Cache аерозольною фарбою нанесено зображення в оливковій кольоровій гамі.\n\nНіколи не приймайте перемогу за належне" },
	["an_gunmetal_bravo"] = { name = "an_gunmetal_bravo", desc = "Anodized Gunmetal. На цей сувенір з Siege нанесено хромову основу, а потім малюнок металевого відтінку з ефектом анодування.\n\n«Ти не знаєш Фелікса Райлі так, як знаю його я… Цього мужика не зламати!» — Зрадник та Правдолюб, частина 1" },
	["am_ossify_blue_p2000_bravo"] = { name = "am_ossify_blue_p2000_bravo", desc = "Ocean Foam. На цей сувенір із Seaside аквадруком нанесено напівпрозорий синій абстрактний малюнок на металеву основу.\n\n«Хвилі розбиваються, наша рішучість ні» — Котаро Ідзакі, спеціаліст із проламування" },
	["am_crumple_bravo"] = { name = "am_crumple_bravo", desc = "Graphite. На цей сувенір з Agency нанесено хромову основу, а потім зображення полігональної сітки різними відтінками металевих кольорів.\n\nУрок завершено, ручки відклали" },
	["aq_steel"] = { name = "aq_steel", desc = "Rust Coat. Усе ще чудово працює, незважаючи на іржу зовні.\n\nДекотрі особи не приховують, якими брудними є їхні діяння" },
	["hy_hex"] = { name = "hy_hex", desc = "Hive, Hexane. Аквадруком нанесено візерунок у вигляді шестикутників." },
	["hy_camo_large"] = { name = "hy_camo_large", desc = "Blind Spot. Аквадруком нанесено дуже збільшену камуфляжну текстуру." },
	["am_ddpatdense"] = { name = "am_ddpatdense", desc = "Cobalt / Steel Disruption. Нанесено металеву фольгу у вигляді піксельного камуфляжу." },
	["twigs"] = { name = "twigs", desc = "Ash Wood. Аерозольним балончиком нанесено малюнок за допомогою трафарету з гілок." },
	["varicamo"] = { name = "varicamo", desc = "CaliCamo, Full Stop, Night Ops, Urban Rubble, VariCamo. Аквадруком нанесено візерунок у вигляді змішаного камуфляжу.\n\nВідновлення приходить тільки після руйнування" },
	["aq_damascus_knife"] = { name = "aq_damascus_knife", desc = "Damascus Steel. Викуваний із болванки, виготовленої із двох різних типів вуглецевої сталі.\n\nНарешті з’явилася зброя, яка відповідає вашій рішучості" },
	["am_crystallized"] = { name = "am_crystallized", desc = "Cobalt / Red / Silver Quartz. Металевою фарбою нанесено малюнок за допомогою трафарету у вигляді кристалів.\n\nВсі хочуть бути впливовими" },
	["snakeskin"] = { name = "snakeskin", desc = "Pit Viper. Аквадруком нанесено візерунок у вигляді шкіри змії.\n\n«Я знаю, ти мені не віриш… Але коли ти попадешся, Валерія отримає твою голову» — Щит та змій, частина 1" },
	["cu_usp-s_elegant"] = { name = "cu_usp-s_elegant", desc = "Guardian. Пофарбовано синім кобальтом, а за допомогою трафарету на магазин нанесено логотип ССО.\n\n«Можу слідувати протоколу і витягти сімох, чи я можу зробити це у свій спосіб і врятувати всіх десятьох… вибір за вами», — Кармен Кочінеро, спеціаліст із порятунку заручників" },
	["hy_plaid2"] = { name = "hy_plaid2", desc = "Green Plaid. Аквадруком нанесено сіро-брунатну текстуру із синьо-зеленими клітковими візерунками.\n\nЦе не волинка, але вона грає пісню перед кожним похороном" },
	["cu_fiveseven_urban_hazard"] = { name = "cu_fiveseven_urban_hazard", desc = "Urban Hazard. Нанесено зображення у вигляді сучасного камуфляжу з червоними акцентами.\n\nЩороку у Бута збираються найкращі зброярі-дизайнери та показують свої найкращі творіння" },
	["an_emerald"] = { name = "an_emerald", desc = "Emerald. Нанесено текстуру напівпрозорою смарагдовою фарбою на хромовану основу .\n\n«Що ж сталося з величним Себастьяном Енке? Чого він став як криклива бабця? Що ж з тобою сталося…» — Учитель та бунтівник, частина 2" },
	["so_keycolors"] = { name = "so_keycolors", desc = "Impact Drill. Частини зброї пофарбовані аерозольною фарбою в суцільні голубий та жовтий кольори.\n\nВеличний зрівнювач" },
	["cu_awp_mastery"] = { name = "cu_awp_mastery", desc = "Elite Build. Нанесено зображення із використанням аквадруку та трафарету, а також оздоблений наліпками з теплопередачею.\n\n«Чейз Тернер був видатним чоловіком… Вам важко досягти такої слави» — Фенікс та Посвячений, частина 1" },
	["aq_dualberettas_cartel"] = { name = "aq_dualberettas_cartel", desc = "Cartel. Вигравіювано зображення черепів, квітів та зміїв.\n\n«Ось чому Гакслі робить усі тяжку роботу, треба лишень її підштовхнути…» — Змій та Праведник, частина 2" },
	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "Valence. Розфарбований блакитною та сірою фарбою, що підкреслює форму зброї.\n\nРазом ми завжди сильніші" },
	["cu_nova_hyperbeast"] = { name = "cu_nova_hyperbeast", desc = "Hyper Beast. Зображення чудовиська у психоделічних кольорах нанесене вручну.\n\n«Вони звуть тебе Демоном, так? Хех, таке страшне ім’я для такого смішного чоловіка…» — Еліот Кінґсмен, найманець" },
	["hy_ocean_knife"] = { name = "hy_ocean_knife", desc = "Bright Water. Аквадруком нанесено синій камуфляж." },
	["cu_m4a1s_printstream"] = { name = "cu_m4a1s_printstream", desc = "Printstream. Cтриманий чорно-білий дизайн із перламутровими відблисками, розфарбований вручну.\n\nБІЛИЙ_1; ЧОРНИЙ_1; ПЕРЛАМУТРОВИЙ_1; ТИХИЙ_1;" },
	["cu_usp_printstream"] = { name = "cu_usp_printstream", desc = "Printstream. Нанесено зображення зі стриманим чорно-білим дизайном та з перламутровими вставками.\n\nБІЛИЙ_1; ЧОРНИЙ_1; ПЕРЛАМУТРОВИЙ_1; ГЛУШНИК_1; ПІСТОЛЕТ_1;" },
}
